        }
        #control-panel label {
            margin: 0 5px;
        }
    </style>
</head>
<body>
    <div id="demo-container">
        <div id="control-panel">
            <label>Step:</label>
            <input type="radio" id="step1" name="step-selector" value="1" checked>
            <label for="step1">1</label>
            <input type="radio" id="step2" name="step-selector" value="2">
            <label for="step2">2</label>
            <input type="radio" id="step3" name="step-selector" value="3">
            <label for="step3">3</label>
        </div>
        <div id="canvas-container"></div>
    </div>
    <script>
        let currentStep = 1;
        let pointP;
        let dragging = false;
        let triangleVertices;
        let mainAltitude;
        const CANVAS_WIDTH = 600;
        const CANVAS_HEIGHT = 520;

        function setup() {
            const canvas = createCanvas(CANVAS_WIDTH, CANVAS_HEIGHT);
            canvas.parent('canvas-container');

            // Initialize main triangle vertices
            const centerX = width/2;
            const centerY = height/2;
            const radius = 200;
            triangleVertices = [
                createVector(centerX - radius * cos(PI/6), centerY + radius * sin(PI/6)),  // A
                createVector(centerX + radius * cos(PI/6), centerY + radius * sin(PI/6)),  // B
                createVector(centerX, centerY - radius)  // C
            ];

            // Initialize point P
            pointP = createVector(width * 0.4, height * 0.6);

            // Calculate main altitude
            mainAltitude = {
                start: triangleVertices[2],
                end: createVector(centerX, centerY + radius * sin(PI/6))
            };

            // Add radio button event listeners
            const radios = document.getElementsByName('step-selector');
            radios.forEach(radio => {
                radio.addEventListener('change', function() {
                    currentStep = parseInt(this.value);
                });
            });
        }

        function draw() {
            background(255);

            // Draw main triangle
            stroke(0);
            noFill();
            beginShape();
            triangleVertices.forEach(v => vertex(v.x, v.y));
            endShape(CLOSE);

            // Label vertices
            textSize(16);
            noStroke();
            fill(0);
            text('A', triangleVertices[0].x - 20, triangleVertices[0].y + 20);
            text('B', triangleVertices[1].x + 20, triangleVertices[1].y + 20);
            text('C', triangleVertices[2].x, triangleVertices[2].y - 20);

            // Draw main altitude
            drawDashedLine(mainAltitude.start, mainAltitude.end, color(200, 200, 255));

            // Draw step-specific elements
            if (currentStep === 1) {
                drawStep1();
            } else if (currentStep === 2) {
                drawStep2();
            } else if (currentStep === 3) {
                drawStep3();
            }

            // Draw point P (except in step 3)
            if (currentStep !== 3) {
                drawPointP();
            }
        }

        function drawPointP() {
            stroke(0);
            fill(200);
            circle(pointP.x, pointP.y, 10);
            line(pointP.x - 5, pointP.y, pointP.x + 5, pointP.y);
            line(pointP.x, pointP.y - 5, pointP.x, pointP.y + 5);
            noStroke();
            fill(255, 0, 0);
            text('P', pointP.x + 10, pointP.y);
        }

        function drawStep1() {
            const perpendiculars = calculatePerpendiculars();

            stroke(0);
            perpendiculars.forEach(perp => {
                line(pointP.x, pointP.y, perp.foot.x, perp.foot.y);
                fill(0, 0, 255);
                noStroke();
                circle(perp.foot.x, perp.foot.y, 6);
            });
        }

        function drawStep2() {
            const perpendiculars = calculatePerpendiculars();

            // Draw colored triangles
            noStroke();

            // Red triangle
            fill(255, 0, 0, 127);
            drawEquilateralTriangle(pointP, perpendiculars[0].distance, 0);

            // Green triangle
            fill(0, 255, 0, 127);
            drawEquilateralTriangle(pointP, perpendiculars[1].distance, 2*PI/3);

            // Blue triangle
            fill(0, 0, 255, 127);
            drawEquilateralTriangle(pointP, perpendiculars[2].distance, 4*PI/3);
        }

        function drawStep3() {
            const perpendiculars = calculatePerpendiculars();
            const totalHeight = dist(triangleVertices[2].y, mainAltitude.end.y);
            let currentY = mainAltitude.end.y;

            // Draw translated triangles
            noStroke();

            // Red triangle
            fill(255, 0, 0, 127);
            const redHeight = perpendiculars[0].distance;
            drawStackedTriangle(currentY, redHeight);
            currentY -= redHeight;

            // Blue triangle
            fill(0, 0, 255, 127);
            const blueHeight = perpendiculars[2].distance;
            drawStackedTriangle(currentY, blueHeight);
            currentY -= blueHeight;

            // Green triangle
            fill(0, 255, 0, 127);
            const greenHeight = perpendiculars[1].distance;
            drawStackedTriangle(currentY, greenHeight);
        }

        function drawStackedTriangle(baseY, height) {
            const width = height * 2 / sqrt(3);
            const x = mainAltitude.end.x;
            triangle(x - width/2, baseY, x + width/2, baseY, x, baseY - height);
        }

        function calculatePerpendiculars() {
            // Calculate perpendicular distances from P to each side
            const perpendiculars = [];

            for (let i = 0; i < 3; i++) {
                const a = triangleVertices[i];
                const b = triangleVertices[(i + 1) % 3];
                const foot = getPerpendicularFoot(pointP, a, b);
                const distance = dist(pointP.x, pointP.y, foot.x, foot.y);
                perpendiculars.push({foot, distance});
            }

            return perpendiculars;
        }

        function getPerpendicularFoot(p, a, b) {
            const dx = b.x - a.x;
            const dy = b.y - a.y;
            const k = ((p.x - a.x) * dx + (p.y - a.y) * dy) / (dx * dx + dy * dy);
            return createVector(a.x + k * dx, a.y + k * dy);
        }

        function drawEquilateralTriangle(center, height, rotation) {
            const width = height * 2 / sqrt(3);
            push();
            translate(center.x, center.y);
            rotate(rotation);
            triangle(-width/2, 0, width/2, 0, 0, -height);
            pop();
        }

        function drawDashedLine(start, end, color) {
            const dashLength = 5;
            const gap = 5;
            stroke(color);
            const d = dist(start.x, start.y, end.x, end.y);
            const steps = d / (dashLength + gap);
            const dx = (end.x - start.x) / steps;
            const dy = (end.y - start.y) / steps;

            for (let i = 0; i < steps; i++) {
                const x1 = start.x + dx * i;
                const y1 = start.y + dy * i;
                const x2 = x1 + dx * dashLength/(dashLength + gap);
                const y2 = y1 + dy * dashLength/(dashLength + gap);
                line(x1, y1, x2, y2);
            }
        }

        function mousePressed() {
            if (dist(mouseX, mouseY, pointP.x, pointP.y) < 10) {
                dragging = true;
            }
        }

        function mouseReleased() {
            dragging = false;
        }

        function mouseDragged() {
            if (dragging && currentStep !== 3) {
                pointP.x = mouseX;
                pointP.y = mouseY;
                constrainPointToTriangle();
            }
        }

        function constrainPointToTriangle() {
            const area = triangleArea(triangleVertices[0], triangleVertices[1], triangleVertices[2]);
            const a1 = triangleArea(pointP, triangleVertices[1], triangleVertices[2]);
            const a2 = triangleArea(triangleVertices[0], pointP, triangleVertices[2]);
            const a3 = triangleArea(triangleVertices[0], triangleVertices[1], pointP);

            if (a1 + a2 + a3 > area + 0.1) {
                const nearest = getNearestPoint();
                pointP.x = nearest.x;
                pointP.y = nearest.y;
            }
        }

        function triangleArea(p1, p2, p3) {
            return abs((p2.x - p1.x) * (p3.y - p1.y) - (p3.x - p1.x) * (p2.y - p1.y)) / 2;
        }

        function getNearestPoint() {
            let minDist = Infinity;
            let nearest = null;

            for (let i = 0; i < 3; i++) {
                const a = triangleVertices[i];
                const b = triangleVertices[(i + 1) % 3];
                const foot = getPerpendicularFoot(pointP, a, b);
                const d = dist(pointP.x, pointP.y, foot.x, foot.y);

                if (d < minDist) {
                    minDist = d;
                    nearest = foot;
                }
            }

            return nearest;
        }
    </script>
</body>
</html>
