                yaxis: { range: [0, 600] }
            });

            // Ozone concentration plot
            const selectedConcentration = interpolateConcentration(selectedCFC, year);
            const slope = selectedConcentration * 0.1; // Simplified rate constant calculation
            const timePoints = Array.from({length: time}, (_, i) => i);
            const ozoneTrace = {
                x: timePoints,
                y: timePoints.map(t => 20 + slope * t),
                line: { color: 'blue' }
            };

