        function interpolateConcentration(cfcName, year) {
            const data = cfcData[cfcName].data;
            for (let i = 0; i < data.length - 1; i++) {
                if (data[i][0] <= year && data[i + 1][0] >= year) {
                    const x0 = data[i][0];
                    const x1 = data[i + 1][0];
                    const y0 = data[i][1];
                    const y1 = data[i + 1][1];
                    return y0 + (y1 - y0) * (year - x0) / (x1 - x0);
                }
            }
            return data[data.length - 1][1];
        }

        // Event listeners
        document.getElementById('select-cfc').addEventListener('change', initializePlots);
        document.getElementById('checkbox-show-all').addEventListener('change', initializePlots);
