            const canvas = createCanvas(600, 450);
            canvas.parent('demo-canvas');
            angleMode(DEGREES);
        }

        function draw() {
            background('#F0F0F0');

            if (!isZoomed) {
                drawFullView();
            } else {
                drawZoomedView();
            }

            drawSunRays();
        }

        function drawFullView() {
            const earthRadius = 150;
            const centerX = width/3;
            const centerY = height/2;

            // Draw Earth
            fill('#468296');
            stroke(0);
            circle(centerX, centerY, earthRadius * 2);

            // Draw Syene line
            drawDashedLine(centerX, centerY, centerX + earthRadius, centerY);
            textAlign(LEFT);
            text("Syene", centerX + earthRadius + 5, centerY);

            // Draw Alexandria line and angle
            const alexX = centerX + earthRadius * cos(-angle);
            const alexY = centerY - earthRadius * sin(-angle);

            drawDashedLine(centerX, centerY, alexX, alexY);
            drawDashedLine(alexX, alexY, centerX + earthRadius, alexY);

            // Draw angle arc
            noFill();
            arc(alexX, alexY, 40, 40, -90, -90-angle);

            // Draw angle label
            text("Alexandria", alexX + 5, alexY - 10);
            text(angle.toFixed(1) + "°", alexX - 40, alexY - 20);

            // Draw observation point
            fill(0);
            triangle(alexX, alexY, alexX-5, alexY-5, alexX+5, alexY-5);
        }

        function drawZoomedView() {
            const sectorRadius = 300;
            const centerX = 100;
            const centerY = height - 50;

            // Draw Earth sector
            fill('#468296');
            stroke(0);
            arc(centerX, centerY, sectorRadius * 2, sectorRadius * 2, -90-angle, -90);

            // Draw radii
            drawDashedLine(centerX, centerY, centerX + sectorRadius, centerY);
            const alexX = centerX + sectorRadius * cos(-angle);
            const alexY = centerY - sectorRadius * sin(-angle);
            drawDashedLine(centerX, centerY, alexX, alexY);

            // Draw labels
            textAlign(LEFT);
            text("Syene", centerX + sectorRadius + 5, centerY);
            text("Alexandria", alexX + 5, alexY - 10);

            // Draw angle
            noFill();
            arc(centerX, centerY, 80, 80, -90, -90-angle);
            text(angle.toFixed(1) + "°", centerX + 20, centerY - 40);

            // Draw post and shadow
            const postSize = 30;
            fill('#DEB887');
            triangle(alexX, alexY, 
                    alexX, alexY - postSize,
                    alexX + postSize, alexY);
        }

        function drawSunRays() {
            stroke('#DAA520');
            for(let y = 50; y < height; y += 40) {
                line(width-150, y, width-50, y);
                // Arrow head
                line(width-50, y, width-60, y-5);
                line(width-50, y, width-60, y+5);
            }
        }

        function drawDashedLine(x1, y1, x2, y2) {
            const dashLength = 5;
            const gapLength = 5;
            const dx = x2 - x1;
            const dy = y2 - y1;
            const dist = sqrt(dx * dx + dy * dy);
            const steps = dist / (dashLength + gapLength);
            const stepX = dx / steps;
            const stepY = dy / steps;

            for(let i = 0; i < steps; i++) {
                line(x1 + i * stepX, y1 + i * stepY,
                     x1 + i * stepX + stepX/2, y1 + i * stepY + stepY/2);
            }
        }

        // Event listeners
        document.getElementById('slider-post-angle').addEventListener('input', function(e) {
            angle = parseFloat(e.target.value);
            document.getElementById('display-post-angle').textContent = angle.toFixed(1) + '°';
        });

        document.getElementById('checkbox-zoom').addEventListener('change', function(e) {
            isZoomed = e.target.checked;
        });
    </script>
</body>
</html>
