<body>
    <div id="app-container">
        <div id="control-panel">
            <button id="btn-reset">+</button>
            <div>
                <label for="slider-moon-pos">position of moon</label>
                <input type="range" id="slider-moon-pos" min="0" max="360" step="1" value="144">
            </div>
            <div>
                <label for="slider-moon-pull">pull from moon</label>
                <input type="range" id="slider-moon-pull" min="0" max="100" step="1" value="40">
            </div>
            <div>
                <label for="slider-sun-pos">position of sun</label>
                <input type="range" id="slider-sun-pos" min="0" max="360" step="1" value="144">
            </div>
            <div>
                <label for="slider-sun-pull">pull from sun</label>
                <input type="range" id="slider-sun-pull" min="0" max="100" step="1" value="20">
            </div>
        </div>
        <div id="viz-container"></div>
    </div>

    <script>
        const EARTH_RADIUS = 180;
        const BASE_WATER_RADIUS = 200;
        const SCALING_FACTOR = 0.5;

        function setup() {
            const canvas = createCanvas(500, 500);
            canvas.parent('viz-container');
            noLoop();

            // Add event listeners
            document.querySelectorAll('input[type="range"]').forEach(slider => {
                slider.addEventListener('input', () => redraw());
            });

            document.getElementById('btn-reset').addEventListener('click', () => {
                document.getElementById('slider-moon-pos').value = 144;
                document.getElementById('slider-moon-pull').value = 40;
                document.getElementById('slider-sun-pos').value = 144;
                document.getElementById('slider-sun-pull').value = 20;
                redraw();
            });
        }

        function draw() {
            background(255);
            translate(width/2, height/2);

            const moonAngle = radians(parseFloat(document.getElementById('slider-moon-pos').value));
            const moonPull = parseFloat(document.getElementById('slider-moon-pull').value);
            const sunAngle = radians(parseFloat(document.getElementById('slider-sun-pos').value));
            const sunPull = parseFloat(document.getElementById('slider-sun-pull').value);

            // Draw water layer
            fill('#aed6f1');
            stroke(0);
            strokeWeight(1);
            beginShape();
            for (let angle = 0; angle <= TWO_PI; angle += PI/180) {
                const moonEffect = moonPull * pow(cos(angle - moonAngle), 2);
                const sunEffect = sunPull * pow(cos(angle - sunAngle), 2);
                const r = BASE_WATER_RADIUS + SCALING_FACTOR * (moonEffect + sunEffect);
                const x = r * cos(angle);
                const y = r * sin(angle);
                vertex(x, y);
            }
            endShape(CLOSE);

            // Draw Earth
            fill('#966F33');
            noStroke();
            circle(0, 0, EARTH_RADIUS * 2);
        }
    </script>
</body>
</html>
