        .control-group label {
            display: block;
            margin-bottom: 5px;
        }
        input[type="range"] {
            width: 100%;
        }
        #visualization-panel {
            flex: 1;
            display: flex;
            flex-direction: column;
            border-left: 1px solid #ccc;
        }
        #telescopic-view-wrapper,
        #orbit-view-wrapper {
            flex: 1;
            position: relative;
        }
        canvas {
            width: 100%;
            height: 100%;
        }
        p {
            text-align: center;
            margin: 5px 0;
            position: absolute;
            bottom: 0;
            width: 100%;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="controls-panel">
            <div class="control-group">
                <label for="slider-earth-position">Earth position</label>
                <input type="range" id="slider-earth-position" min="0" max="360" value="180" step="1">
            </div>
            <div class="control-group">
                <label for="slider-comet-position">Comet position</label>
                <input type="range" id="slider-comet-position" min="0" max="360" value="180" step="1">
            </div>
            <div class="control-group">
                <label for="slider-coma">Coma</label>
                <input type="range" id="slider-coma" min="0" max="100" value="20" step="1">
            </div>
            <div class="control-group">
                <label for="slider-tail">Tail</label>
                <input type="range" id="slider-tail" min="0" max="100" value="50" step="1">
            </div>
            <div class="control-group">
                <label for="slider-tail-spread">Tail spread</label>
                <input type="range" id="slider-tail-spread" min="0" max="100" value="20" step="1">
            </div>
        </div>
        <div id="visualization-panel">
            <div id="telescopic-view-wrapper">
                <canvas id="telescopic-canvas"></canvas>
                <p>telescopic view</p>
            </div>
            <div id="orbit-view-wrapper">
                <canvas id="orbit-canvas"></canvas>
                <p>orbit diagram</p>
            </div>
        </div>
    </div>

    <script>
        // Three.js setup
        let orbitScene, orbitCamera, orbitRenderer;
        let telescopicScene, telescopicCamera, telescopicRenderer;
        let cometGroup, earthMesh;

        function init() {
            // Orbit view setup
            orbitScene = new THREE.Scene();
            orbitScene.background = new THREE.Color(0xffffff);
            orbitCamera = new THREE.PerspectiveCamera(45, window.innerWidth / (window.innerHeight/2), 0.1, 1000);
            orbitCamera.position.set(0, 20, 20);
            orbitCamera.lookAt(0, 0, 0);

            orbitRenderer = new THREE.WebGLRenderer({
                canvas: document.getElementById('orbit-canvas'),
                antialias: true
            });
            orbitRenderer.setSize(window.innerWidth - 300, window.innerHeight/2);

            // Telescopic view setup
            telescopicScene = new THREE.Scene();
            telescopicScene.background = new THREE.Color(0x000000);
            telescopicCamera = new THREE.PerspectiveCamera(15, window.innerWidth / (window.innerHeight/2), 0.1, 1000);

            telescopicRenderer = new THREE.WebGLRenderer({
                canvas: document.getElementById('telescopic-canvas'),
                antialias: true
            });
            telescopicRenderer.setSize(window.innerWidth - 300, window.innerHeight/2);

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
            orbitScene.add(ambientLight);
            telescopicScene.add(ambientLight.clone());

            const sunLight = new THREE.PointLight(0xffffff, 1);
            orbitScene.add(sunLight);

            // Create celestial bodies and orbits
            createCelestialBodies();
            createOrbits();

            // Add event listeners
            window.addEventListener('resize', onWindowResize);
            addSliderListeners();

            // Start animation loop
            animate();
        }

        function createCelestialBodies() {
            // Sun
            const sunGeometry = new THREE.SphereGeometry(0.5, 32, 32);
            const sunMaterial = new THREE.MeshBasicMaterial({color: 0xffff00, emissive: 0xffff00});
            const sun = new THREE.Mesh(sunGeometry, sunMaterial);
            orbitScene.add(sun);

            // Earth
            const earthGeometry = new THREE.SphereGeometry(0.3, 32, 32);
            const earthMaterial = new THREE.MeshBasicMaterial({color: 0x0000ff});
            earthMesh = new THREE.Mesh(earthGeometry, earthMaterial);
            orbitScene.add(earthMesh);

            // Comet
            cometGroup = new THREE.Group();

            // Nucleus
            const nucleusGeometry = new THREE.SphereGeometry(0.2, 32, 32);
            const nucleusMaterial = new THREE.MeshBasicMaterial({color: 0xffffff});
            const nucleus = new THREE.Mesh(nucleusGeometry, nucleusMaterial);
            cometGroup.add(nucleus);

            // Coma
            const comaGeometry = new THREE.SphereGeometry(1, 32, 32);
            const comaMaterial = new THREE.MeshBasicMaterial({
                color: 0x88ff88,
                transparent: true,
                opacity: 0.5
            });
            const coma = new THREE.Mesh(comaGeometry, comaMaterial);
            cometGroup.add(coma);

            // Tail
            const tailGeometry = new THREE.ConeGeometry(1, 4, 32);
            const tailMaterial = new THREE.MeshBasicMaterial({
                color: 0x88ff88,
                transparent: true,
                opacity: 0.4
            });
            const tail = new THREE.Mesh(tailGeometry, tailMaterial);
            tail.position.z = -2;
            tail.rotation.x = Math.PI / 2;
            cometGroup.add(tail);

            orbitScene.add(cometGroup);
            telescopicScene.add(cometGroup.clone());
        }

        function createOrbits() {
            const orbits = [
                {a: 4, e: 0.0167, color: 0x0000ff}, // Earth
                {a: 6.1, e: 0.0934, color: 0xff0000}, // Mars
                {a: 20.8, e: 0.0489, color: 0xff8800}, // Jupiter
                {a: 14.4, e: 0.432, color: 0xffff00} // Comet Holmes
            ];

            orbits.forEach(orbit => {
                const points = [];
                for(let i = 0; i <= 360; i++) {
                    const angle = i * Math.PI / 180;
                    const r = orbit.a * (1 - orbit.e * orbit.e) / (1 + orbit.e * Math.cos(angle));
                    points.push(new THREE.Vector3(r * Math.cos(angle), 0, r * Math.sin(angle)));
                }

                const geometry = new THREE.BufferGeometry().setFromPoints(points);
                const material = new THREE.LineBasicMaterial({color: orbit.color});
                const line = new THREE.Line(geometry, material);
                orbitScene.add(line);
            });
        }

        function updateScene() {
            const earthPos = document.getElementById('slider-earth-position').value * Math.PI / 180;
            const cometPos = document.getElementById('slider-comet-position').value * Math.PI / 180;
            const comaScale = document.getElementById('slider-coma').value / 20;
            const tailLength = document.getElementById('slider-tail').value / 25;
            const tailSpread = document.getElementById('slider-tail-spread').value / 20;

            // Update positions
            earthMesh.position.set(4 * Math.cos(earthPos), 0, 4 * Math.sin(earthPos));

            const cometOrbitR = 14.4 * (1 - 0.432 * 0.432) / (1 + 0.432 * Math.cos(cometPos));
            cometGroup.position.set(cometOrbitR * Math.cos(cometPos), 0, cometOrbitR * Math.sin(cometPos));

            // Update comet appearance
            cometGroup.children[1].scale.set(comaScale, comaScale, comaScale);
            cometGroup.children[2].scale.set(tailSpread, tailLength, tailSpread);

            // Update telescopic camera
            telescopicCamera.position.copy(earthMesh.position);
            telescopicCamera.lookAt(cometGroup.position);
        }

        function addSliderListeners() {
            const sliders = document.querySelectorAll('input[type="range"]');
            sliders.forEach(slider => {
                slider.addEventListener('input', updateScene);
            });
        }

        function onWindowResize() {
            orbitCamera.aspect = (window.innerWidth - 300) / (window.innerHeight/2);
            orbitCamera.updateProjectionMatrix();
            orbitRenderer.setSize(window.innerWidth - 300, window.innerHeight/2);

            telescopicCamera.aspect = (window.innerWidth - 300) / (window.innerHeight/2);
            telescopicCamera.updateProjectionMatrix();
            telescopicRenderer.setSize(window.innerWidth - 300, window.innerHeight/2);
        }

        function animate() {
            requestAnimationFrame(animate);
            orbitRenderer.render(orbitScene, orbitCamera);
            telescopicRenderer.render(telescopicScene, telescopicCamera);
        }

        // Initialize everything when the page loads
        window.addEventListener('load', init);
    </script>
</body>
</html>
