    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <style>
        body {
            margin: 0;
            padding: 20px;
            font-family: Arial, sans-serif;
        }
        #main {
            display: flex;
            flex-direction: column;
            align-items: center;
            gap: 20px;
        }
        #control-panel {
            display: flex;
            gap: 30px;
            padding: 20px;
            background: #f0f0f0;
            border-radius: 8px;
        }
        .slider-group {
            display: flex;
            align-items: center;
            gap: 10px;
        }
        .slider-group label {
            font-weight: bold;
            min-width: 20px;
        }
        #canvas-container {
            background: #f0f0f0;
            padding: 20px;
            border-radius: 8px;
        }
    </style>
</head>
<body>
    <div id="main">
        <div id="control-panel">
            <div class="slider-group">
                <label for="slider-m">m</label>
                <input type="range" id="slider-m" min="0" max="10" step="0.001" value="2.845">
                <span id="label-m">2.845</span>
            </div>
            <div class="slider-group">
                <label for="slider-x">x</label>
                <input type="range" id="slider-x" min="0" max="10" step="0.1" value="4.1">
                <span id="label-x">4.1</span>
            </div>
            <div class="slider-group">
                <label for="slider-y">y</label>
                <input type="range" id="slider-y" min="0.1" max="5" step="0.001" value="1.498">
                <span id="label-y">1.498</span>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let sliderM, sliderX, sliderY;
        let labelM, labelX, labelY;
        const L = 10; // Distance between posts
        const g = 9.81; // Acceleration due to gravity

        function setup() {
            const canvas = createCanvas(800, 500);
            canvas.parent('canvas-container');

            sliderM = select('#slider-m');
            sliderX = select('#slider-x');
            sliderY = select('#slider-y');

            labelM = select('#label-m');
            labelX = select('#label-x');
            labelY = select('#label-y');

            sliderM.input(() => labelM.html(sliderM.value()));
            sliderX.input(() => labelX.html(sliderX.value()));
            sliderY.input(() => labelY.html(sliderY.value()));
        }

        function drawDashedLine(x1, y1, x2, y2, dashLength) {
            const dx = x2 - x1;
            const dy = y2 - y1;
            const dist = Math.sqrt(dx * dx + dy * dy);
            const steps = dist / dashLength;
            const dashX = dx / steps;
            const dashY = dy / steps;

            for (let i = 0; i < steps; i += 2) {
                const startX = x1 + (dashX * i);
                const startY = y1 + (dashY * i);
                const endX = startX + dashX;
                const endY = startY + dashY;
                line(startX, startY, endX, endY);
            }
        }

        function draw() {
            background(255);

            // Get current values
            const m = parseFloat(sliderM.value());
            const x = parseFloat(sliderX.value());
            const y = parseFloat(sliderY.value());

            // Calculate angles and tensions
            const theta1 = atan(y / x);
            const theta2 = atan(y / (L - x));
            const Fg = m * g;
            const T1 = (Fg * cos(theta2)) / sin(theta1 + theta2);
            const T2 = (Fg * cos(theta1)) / sin(theta1 + theta2);

            // Draw setup
            const leftPost = 100;
            const rightPost = 700;
            const topY = 150;
            const scale = 50; // pixels per unit

            // Map logical coordinates to pixel coordinates
            const massX = map(x, 0, L, leftPost, rightPost);
            const massY = topY + y * scale;

            // Draw posts
            strokeWeight(10);
            stroke(0);
            line(leftPost, topY - 50, leftPost, topY + 100);
            line(rightPost, topY - 50, rightPost, topY + 100);

            // Draw dashed reference line
            strokeWeight(1);
            stroke(100);
            drawDashedLine(leftPost, topY, rightPost, topY, 10);

            // Draw ropes
            stroke(0);
            line(leftPost, topY, massX, massY);
            line(rightPost, topY, massX, massY);

            // Draw mass
            fill(0);
            ellipse(massX, massY, 20, 20);

            // Draw angles
            noFill();
            stroke(0);
            arc(leftPost, topY, 40, 40, 0, theta1);
            arc(rightPost, topY, 40, 40, PI - theta2, PI);

            // Draw labels
            textAlign(CENTER, CENTER);
            textSize(16);
            fill(0);
            text(`T₁ = ${T1.toFixed(4)} N | T₂ = ${T2.toFixed(4)} N`, width/2, 30);

            // Angle labels
            text("θ₁", leftPost + 30, topY - 10);
            text("θ₂", rightPost - 30, topY - 10);

            // Tension labels
            const midpoint1X = (leftPost + massX) / 2;
            const midpoint1Y = (topY + massY) / 2;
            const midpoint2X = (rightPost + massX) / 2;
            const midpoint2Y = (topY + massY) / 2;

            text("T₁", midpoint1X - 20, midpoint1Y - 20);
            text("T₂", midpoint2X + 20, midpoint2Y - 20);
        }
    </script>
</body>
</html>
