            </div>
            <div class="control-row">
                <label for="slider-mass">Mass</label>
                <input type="range" id="slider-mass" min="0.1" max="5" step="0.01" value="1">
                <span id="value-mass">1.00</span>
            </div>
            <div class="control-row">
                <label for="slider-damping">Dampening</label>
                <input type="range" id="slider-damping" min="0" max="5" step="0.1" value="0.1">
                <span id="value-damping">0.1</span>
            </div>

            <h2>Controller Gains</h2>
            <div class="control-row">
                <label for="slider-proportional-gain">Proportional Gain</label>
                <input type="range" id="slider-proportional-gain" min="0" max="5" step="0.1" value="0.1">
                <span id="value-proportional-gain">0.1</span>
            </div>
            <div class="control-row">
                <label for="slider-derivative-gain">Derivative Gain</label>
                <input type="range" id="slider-derivative-gain" min="0" max="5" step="0.1" value="0.1">
                <span id="value-derivative-gain">0.1</span>
            </div>

            <h2>Release System</h2>
            <div class="control-row">
                <button id="btn-play">▶</button>
                <button id="btn-pause">||</button>
                <button id="btn-reset">|◀</button>
            </div>
        </div>

        <div id="visuals-container">
            <div id="scene-container"></div>
            <div id="plot-displacement"></div>
            <div id="plot-velocity"></div>
        </div>
    </div>

    <script>
        // State variables
        let time = 0;
        let animationFrameId = null;
        let isRunning = false;

        // System parameters
        let params = {
            x0: 0.1,
            v0: 1.0,
            k: 1.0,
            m: 1.0,
            c: 0.1,
            kp: 0.1,
            kd: 0.1
        };

        // Three.js setup
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth/2/window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer();
        const container = document.getElementById('scene-container');
        renderer.setSize(container.clientWidth, container.clientHeight);
        container.appendChild(renderer.domElement);

        // Basic 3D scene setup
        camera.position.set(5, 5, 5);
        camera.lookAt(0, 0, 0);

        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);
        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
        scene.add(directionalLight);

        // Create basic shapes
        const base = new THREE.Mesh(
            new THREE.BoxGeometry(10, 0.5, 4),
            new THREE.MeshStandardMaterial({color: 0x00ff00})
        );
        scene.add(base);

        const mass = new THREE.Mesh(
            new THREE.BoxGeometry(1, 1, 1),
            new THREE.MeshStandardMaterial({color: 0xffff00})
        );
        scene.add(mass);
        mass.position.y = 1;

        // Animation loop
        function animate() {
            if (isRunning) {
                time += 0.016;  // Approximately 60 FPS
                if (time > 10) {
                    isRunning = false;
                } else {
                    updateSystem();
                }
            }
            renderer.render(scene, camera);
            animationFrameId = requestAnimationFrame(animate);
        }

        // Initialize plots
        const displacementPlot = document.getElementById('plot-displacement');
        const velocityPlot = document.getElementById('plot-velocity');

        Plotly.newPlot(displacementPlot, [{
            x: [0],
            y: [params.x0],
            mode: 'lines',
            line: {color: 'blue'}
        }], {
            title: 'Displacement vs Time',
            xaxis: {range: [0, 10], title: 'Time (s)'},
            yaxis: {range: [-3, 3], title: 'Displacement (m)'},
            margin: {l: 50, r: 10, t: 30, b: 30}
        });

        Plotly.newPlot(velocityPlot, [{
            x: [0],
            y: [params.v0],
            mode: 'lines',
            line: {color: 'green'}
        }], {
            title: 'Velocity vs Time',
            xaxis: {range: [0, 10], title: 'Time (s)'},
            yaxis: {range: [-5, 5], title: 'Velocity (m/s)'},
            margin: {l: 50, r: 10, t: 30, b: 30}
        });

        // Event listeners
        document.getElementById('btn-play').onclick = () => {
            isRunning = true;
        };

        document.getElementById('btn-pause').onclick = () => {
            isRunning = false;
        };

        document.getElementById('btn-reset').onclick = () => {
            isRunning = false;
            time = 0;
            updateSystem();
        };

        // Initialize animation
        animate();

        // System update function
        function updateSystem() {
            // Update 3D scene
            mass.position.x = calculateDisplacement(time);

            // Update plots
            Plotly.animate(displacementPlot, {
                data: [{x: [time], y: [calculateDisplacement(time)]}],
                traces: [0],
                layout: {}
            }, {
                transition: {duration: 0},
                frame: {duration: 0, redraw: false}
            });

            Plotly.animate(velocityPlot, {
                data: [{x: [time], y: [calculateVelocity(time)]}],
                traces: [0],
                layout: {}
            }, {
                transition: {duration: 0},
                frame: {duration: 0, redraw: false}
            });
        }

        // Helper functions for calculations
        function calculateDisplacement(t) {
            // Simplified harmonic motion
            return params.x0 * Math.cos(Math.sqrt(params.k/params.m) * t) * Math.exp(-params.c * t / (2 * params.m));
        }

        function calculateVelocity(t) {
            // Derivative of displacement
            const omega = Math.sqrt(params.k/params.m);
            return params.x0 * (-omega * Math.sin(omega * t) - (params.c/(2*params.m)) * Math.cos(omega * t)) * Math.exp(-params.c * t / (2 * params.m));
        }
    </script>
</body>
</html>
