<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="slider-group">
                <h4>tension T</h4>
                <input type="range" id="slider-tension" min="1" max="100" step="1" value="50">
            </div>

            <div class="slider-group">
                <h4>displacement</h4>
                <div class="slider-container">
                    <label for="slider-x1">x₁</label>
                    <input type="range" id="slider-x1" min="-0.5" max="0.5" step="0.01" value="0.1">
                </div>
                <div class="slider-container">
                    <label for="slider-x2">x₂</label>
                    <input type="range" id="slider-x2" min="-0.5" max="0.5" step="0.01" value="0.1">
                </div>
                <div class="slider-container">
                    <label for="slider-x3">x₃</label>
                    <input type="range" id="slider-x3" min="-0.5" max="0.5" step="0.01" value="0">
                </div>
            </div>

            <div class="slider-group">
                <h4>mass</h4>
                <div class="slider-container">
                    <label for="slider-m1">m₁</label>
                    <input type="range" id="slider-m1" min="0.1" max="2" step="0.05" value="1.0">
                </div>
                <div class="slider-container">
                    <label for="slider-m2">m₂</label>
                    <input type="range" id="slider-m2" min="0.1" max="2" step="0.05" value="1.0">
                </div>
                <div class="slider-container">
                    <label for="slider-m3">m₃</label>
                    <input type="range" id="slider-m3" min="0.1" max="2" step="0.05" value="1.0">
                </div>
            </div>

            <h4>release system</h4>
            <div id="animation-controls">
                <button id="btn-play-pause">▶</button>
                <button id="btn-reset">⏮</button>
            </div>
        </div>

        <div id="visualization-area">
            <div id="main-animation-container"></div>
            <div id="displacement-plot-container"></div>
            <div id="velocity-plot-container"></div>
        </div>
    </div>

    <script>
        let isAnimating = false;
        let t = 0;
        let dt = 0.02;
        let canvas;
        let system = {
            omega: [], // angular frequencies
            modes: [], // eigenvectors
            c: [],     // participation factors
            y0: []     // initial displacements
        };

        function setup() {
            canvas = createCanvas(
                document.getElementById('main-animation-container').offsetWidth,
                document.getElementById('main-animation-container').offsetHeight
            );
            canvas.parent('main-animation-container');
            noLoop();
            resetSimulation();
        }

        function draw() {
            background(255);
            drawGrid();

            if (isAnimating) {
                t += dt;
                updatePlotMarkers();
            }

            let positions = calculatePositions(t);
            drawString(positions);
            drawMasses(positions);
        }

        function calculatePositions(t) {
            let positions = [0, 0, 0];
            for (let i = 0; i < 3; i++) {
                positions[i] = 0;
                for (let j = 0; j < 3; j++) {
                    positions[i] += system.c[j] * system.modes[i][j] * Math.cos(system.omega[j] * t);
                }
            }
            return positions;
        }

        function resetSimulation() {
            t = 0;
            isAnimating = false;
            document.getElementById('btn-play-pause').textContent = '▶';

            // Get current parameters
            const T = parseFloat(document.getElementById('slider-tension').value);
            const m1 = parseFloat(document.getElementById('slider-m1').value);
            const m2 = parseFloat(document.getElementById('slider-m2').value);
            const m3 = parseFloat(document.getElementById('slider-m3').value);
            system.y0 = [
                parseFloat(document.getElementById('slider-x1').value),
                parseFloat(document.getElementById('slider-x2').value),
                parseFloat(document.getElementById('slider-x3').value)
            ];

            // Define matrices
            const M = numeric.diag([m1, m2, m3]);
            const K = numeric.mul(T, [
                [2, -1, 0],
                [-1, 2, -1],
                [0, -1, 2]
            ]);

            // Solve eigenvalue problem
            const Minv = numeric.inv(M);
            const eigResult = numeric.eig(numeric.dot(Minv, K));

            system.omega = eigResult.lambda.x.map(x => Math.sqrt(Math.abs(x)));
            system.modes = numeric.transpose(eigResult.E.x);

            // Calculate participation factors
            system.c = numeric.solve(system.modes, system.y0);

            // Initialize plots
            initializePlots();
            redraw();
        }

        function initializePlots() {
            const t_vals = numeric.linspace(0, 5, 250);
            const y_data = [];
            const v_data = [];

            for (let i = 0; i < 3; i++) {
                y_data.push([]);
                v_data.push([]);
                for (let t of t_vals) {
                    let y = 0;
                    let v = 0;
                    for (let j = 0; j < 3; j++) {
                        y += system.c[j] * system.modes[i][j] * Math.cos(system.omega[j] * t);
                        v += -system.c[j] * system.modes[i][j] * system.omega[j] * Math.sin(system.omega[j] * t);
                    }
                    y_data[i].push(y);
                    v_data[i].push(v);
                }
            }

            const colors = ['blue', 'orange', 'green'];
            const displacement_traces = y_data.map((y, i) => ({
                x: t_vals,
                y: y,
                mode: 'lines',
                line: {color: colors[i]},
                name: `Mass ${i+1}`
            }));

            const velocity_traces = v_data.map((v, i) => ({
                x: t_vals,
                y: v,
                mode: 'lines',
                line: {color: colors[i]},
                name: `Mass ${i+1}`
            }));

            const layout = {
                xaxis: {range: [0, 5]},
                shapes: [{
                    type: 'line',
                    x0: 0,
                    y0: -1,
                    x1: 0,
                    y1: 1,
                    line: {
                        color: 'red',
                        width: 2
                    }
                }]
            };

            Plotly.newPlot('displacement-plot-container', displacement_traces, 
                {...layout, title: 'Displacement'});
            Plotly.newPlot('velocity-plot-container', velocity_traces, 
                {...layout, title: 'Velocity'});
        }

        function updatePlotMarkers() {
            const update = {
                'shapes[0].x0': t,
                'shapes[0].x1': t
            };
            Plotly.relayout('displacement-plot-container', update);
            Plotly.relayout('velocity-plot-container', update);
        }

        // Event listeners
        document.getElementById('btn-play-pause').addEventListener('click', () => {
            isAnimating = !isAnimating;
            document.getElementById('btn-play-pause').textContent = isAnimating ? '❚❚' : '▶';
            if (isAnimating) {
                loop();
            } else {
                noLoop();
            }
        });

        document.getElementById('btn-reset').addEventListener('click', resetSimulation);

        // Add listeners to all sliders
        const sliders = document.querySelectorAll('input[type="range"]');
        sliders.forEach(slider => {
            slider.addEventListener('input', resetSimulation);
        });

        // Initialize the simulation when the page loads
        window.addEventListener('load', resetSimulation);
    </script>
</body>
</html>
