<!DOCTYPE html>
<html>
<head>
    <title>Jarvis March Visualization</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <style>
        #app-container {
            display: flex;
            flex-direction: column;
            align-items: center;
            gap: 20px;
            padding: 20px;
            font-family: Arial, sans-serif;
        }
        button {
            padding: 8px 16px;
            margin: 0 5px;
            background: #f0f0f0;
            border: 1px solid #ccc;
            border-radius: 4px;
            cursor: pointer;
        }
        button:disabled {
            background: #ddd;
            cursor: not-allowed;
        }
        button.active {
            background: #d0d0d0;
            border-color: #999;
        }
        #step-controls {
            display: none;
        }
        .step-group {
            display: inline-block;
            margin: 0 10px;
        }
        #explanation-panel {
            width: 700px;
            min-height: 60px;
            padding: 10px;
            background: #f8f8f8;
            border: 1px solid #ddd;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="header-controls">
            <button id="btn-move" class="active">move the points</button>
            <button id="btn-start-algo">start the algorithm</button>
        </div>

        <div id="step-controls">
            <div class="step-group">
                <button disabled>step 1</button>
                <button id="btn-step1-explain">explain</button>
                <button id="btn-step1-do">do it</button>
            </div>
            <div class="step-group">
                <button disabled>step 2</button>
                <button id="btn-step2-explain" disabled>explain</button>
                <button id="btn-step2-do" disabled>do it</button>
            </div>
            <div class="step-group" id="step3-controls-container">
                <button disabled>step 3</button>
                <button id="btn-step3-explain" disabled>explain</button>
                <div id="step3-actions" style="display: inline-block;"></div>
            </div>
        </div>

        <div id="canvas-container"></div>

        <div id="explanation-panel">
            <p id="explanation-text"></p>
        </div>
    </div>

    <script>
        let mode = 'move';
        let points = [];
        let draggedPoint = null;
        let algorithmState = {
            step: 0,
            hullPointsIndices: [],
            fullHull: [],
            leftmostPointIndex: null,
            currentPointIndex: null,
            candidateLines: [],
            explanationSteps: []
        };

        function setup() {
            const canvas = createCanvas(700, 500);
            canvas.parent('canvas-container');

            // Initialize points
            points = [
                createVector(150, 350),
                createVector(250, 150),
                createVector(350, 250),
                createVector(550, 180),
                createVector(480, 320),
                createVector(420, 400)
            ];

            setupEventListeners();
        }

        function draw() {
            background(255);

            // Draw canvas border
            stroke(220);
            noFill();
            rect(0, 0, width-1, height-1);

            // Draw candidate lines
            if (algorithmState.candidateLines.length > 0) {
                stroke(60, 150, 140);
                strokeWeight(2);
                for (let line of algorithmState.candidateLines) {
                    line(points[line[0]].x, points[line[0]].y,
                         points[line[1]].x, points[line[1]].y);
                }
            }

            // Draw hull edges
            if (algorithmState.hullPointsIndices.length > 1) {
                stroke(45, 60, 110);
                strokeWeight(3);
                for (let i = 1; i < algorithmState.hullPointsIndices.length; i++) {
                    const p1 = points[algorithmState.hullPointsIndices[i-1]];
                    const p2 = points[algorithmState.hullPointsIndices[i]];
                    line(p1.x, p1.y, p2.x, p2.y);
                }
            }

            // Draw points
            for (let i = 0; i < points.length; i++) {
                const p = points[i];
                if (algorithmState.hullPointsIndices.includes(i)) {
                    fill(45, 60, 110);
                } else {
                    fill(200, 220, 100);
                }
                stroke(0);
                strokeWeight(1);

                // Circle
                ellipse(p.x, p.y, 12, 12);

                // Crosshair
                line(p.x-6, p.y, p.x+6, p.y);
                line(p.x, p.y-6, p.x, p.y+6);
            }
        }

        function mousePressed() {
            if (mode === 'move') {
                for (let i = 0; i < points.length; i++) {
                    if (dist(mouseX, mouseY, points[i].x, points[i].y) < 8) {
                        draggedPoint = i;
                        break;
                    }
                }
            }
        }

        function mouseDragged() {
            if (mode === 'move' && draggedPoint !== null) {
                points[draggedPoint].x = constrain(mouseX, 0, width);
                points[draggedPoint].y = constrain(mouseY, 0, height);
            }
        }

        function mouseReleased() {
            draggedPoint = null;
        }

        function setupEventListeners() {
            document.getElementById('btn-move').addEventListener('click', function() {
                mode = 'move';
                resetAlgorithmState();
                document.getElementById('step-controls').style.display = 'none';
                document.getElementById('btn-move').classList.add('active');
                document.getElementById('btn-start-algo').classList.remove('active');
                document.getElementById('explanation-text').innerHTML = '';
            });

            document.getElementById('btn-start-algo').addEventListener('click', function() {
                mode = 'algorithm';
                resetAlgorithmState();
                calculateFullHull();
                document.getElementById('step-controls').style.display = 'block';
                document.getElementById('btn-move').classList.remove('active');
                document.getElementById('btn-start-algo').classList.add('active');
                setupStep3Buttons();
            });

            setupStepButtons();
        }

        function resetAlgorithmState() {
            algorithmState = {
                step: 0,
                hullPointsIndices: [],
                fullHull: [],
                leftmostPointIndex: null,
                currentPointIndex: null,
                candidateLines: [],
                explanationSteps: []
            };
        }

        function calculateFullHull() {
            // Find leftmost point
            let leftmost = 0;
            for (let i = 1; i < points.length; i++) {
                if (points[i].x < points[leftmost].x) leftmost = i;
            }

            algorithmState.fullHull = [leftmost];
            let current = leftmost;

            do {
                let next = (current + 1) % points.length;
                for (let i = 0; i < points.length; i++) {
                    if (i === current) continue;
                    const orientation = getOrientation(points[current], points[next], points[i]);
                    if (orientation < 0 || (orientation === 0 && dist(points[current].x, points[current].y,
                        points[i].x, points[i].y) > dist(points[current].x, points[current].y,
                        points[next].x, points[next].y))) {
                        next = i;
                    }
                }
                current = next;
                algorithmState.fullHull.push(current);
            } while (current !== leftmost);

            algorithmState.fullHull.pop(); // Remove duplicate endpoint
        }

        function getOrientation(p1, p2, p3) {
            return (p2.x - p1.x) * (p3.y - p1.y) - (p2.y - p1.y) * (p3.x - p1.x);
        }

        function setupStepButtons() {
            // Step 1
            document.getElementById('btn-step1-explain').addEventListener('click', function() {
                algorithmState.explanationSteps.push("Step 1. Find the leftmost point.");
                updateExplanationText();
            });

            document.getElementById('btn-step1-do').addEventListener('click', function() {
                let leftmost = 0;
                for (let i = 1; i < points.length; i++) {
                    if (points[i].x < points[leftmost].x) leftmost = i;
                }
                algorithmState.leftmostPointIndex = leftmost;
                algorithmState.currentPointIndex = leftmost;
                algorithmState.hullPointsIndices.push(leftmost);

                // Enable Step 2 buttons
                document.getElementById('btn-step2-explain').disabled = false;
                document.getElementById('btn-step2-do').disabled = false;

                // Disable Step 1 buttons
                document.getElementById('btn-step1-explain').disabled = true;
                document.getElementById('btn-step1-do').disabled = true;
            });

            // Similar setup for Step 2 and Step 3 buttons...
        }

        function setupStep3Buttons() {
            const container = document.getElementById('step3-actions');
            container.innerHTML = '';

            const doButton = document.createElement('button');
            doButton.id = 'btn-step3-do';
            doButton.textContent = 'do it';
            doButton.disabled = true;
            container.appendChild(doButton);

            for (let i = 0; i < algorithmState.fullHull.length - 2; i++) {
                const repeatButton = document.createElement('button');
                repeatButton.id = `btn-step3-repeat-${i}`;
                repeatButton.textContent = 'repeat';
                repeatButton.disabled = true;
                container.appendChild(repeatButton);
            }
        }

        function updateExplanationText() {
            document.getElementById('explanation-text').innerHTML = 
                algorithmState.explanationSteps.join('<br>');
        }
    </script>
</body>
</html>
