            text-align: right;
        }
        label {
            display: block;
            margin-bottom: 5px;
        }
        input[type="range"] {
            width: 100%;
        }
        select {
            width: 100%;
            padding: 5px;
        }
        #plot-top, #plot-bottom {
            height: 400px;
            margin-bottom: 20px;
        }
    </style>
</head>
<body>
    <div class="container">
        <div id="controls">
            <details>
                <summary>initial conditions</summary>
                <div class="control-group">
                    <p>X(0): 0.1 mol/m³</p>
                    <p>Glu(0): 60 mol/m³</p>
                    <p>Eta(0): 0 mol/m³</p>
                    <p>E0(0): 1.0 mol/m³</p>
                    <p>E1(0): 0.0 mol/m³</p>
                    <p>E2(0): 0.0 mol/m³</p>
                    <p>V(0): 1.0 m³</p>
                </div>
            </details>

            <div class="control-group">
                <label for="slider-alpha-max">maximum fraction of respiring biomass</label>
                <input type="range" id="slider-alpha-max" min="0.1" max="1.0" step="0.01" value="0.3">
                <span class="slider-value" id="value-alpha-max">0.3</span>
            </div>

            <div class="control-group">
                <label for="slider-t-min">run time minimum (h)</label>
                <input type="range" id="slider-t-min" min="0" max="0" step="1" value="0" disabled>
                <span class="slider-value" id="value-t-min">0</span>
            </div>

            <div class="control-group">
                <label for="slider-t-max">run time maximum (h)</label>
                <input type="range" id="slider-t-max" min="10" max="100" step="1" value="60">
                <span class="slider-value" id="value-t-max">60</span>
            </div>

            <div class="control-group">
                <label for="slider-enzyme-control">intermediate enzyme control [mol/m³]</label>
                <input type="range" id="slider-enzyme-control" min="0.1" max="5.0" step="0.01" value="1">
                <span class="slider-value" id="value-enzyme-control">1</span>
            </div>

            <div class="control-group">
                <label for="slider-feed-rate">feed flow rate (m³/h)</label>
                <input type="range" id="slider-feed-rate" min="0" max="2.0" step="0.01" value="0.2">
                <span class="slider-value" id="value-feed-rate">0.2</span>
            </div>

            <div class="control-group">
                <label for="slider-substrate-feed-conc">substrate feed concentration [mol/m³]</label>
                <input type="range" id="slider-substrate-feed-conc" min="10" max="200" step="1" value="100">
                <span class="slider-value" id="value-substrate-feed-conc">100</span>
            </div>

            <div class="control-group">
                <label for="slider-feed-time">feed time (h)</label>
                <input type="range" id="slider-feed-time" min="0" max="60" step="1" value="35">
                <span class="slider-value" id="value-feed-time">35</span>
            </div>

            <div class="control-group">
                <label for="select-plot">plot</label>
                <select id="select-plot">
                    <option value="fed batch">fed batch</option>
                    <option value="α">α</option>
                </select>
            </div>
        </div>

        <div id="visualization">
            <div id="plot-top"></div>
            <div id="plot-bottom"></div>
        </div>
    </div>

    <script>
        // RK4 ODE Solver
        function rk4(y, t, dt, params, dydt) {
            const k1 = dydt(t, y, params);
            const k2 = dydt(t + dt/2, y.map((yi, i) => yi + k1[i]*dt/2), params);
            const k3 = dydt(t + dt/2, y.map((yi, i) => yi + k2[i]*dt/2), params);
            const k4 = dydt(t + dt, y.map((yi, i) => yi + k3[i]*dt), params);

            return y.map((yi, i) => yi + (dt/6)*(k1[i] + 2*k2[i] + 2*k3[i] + k4[i]));
        }

        // System of ODEs
        function dydt(t, y, p) {
            const [X, Glu, Eta, E0, E1, E2, V] = y;
            const alpha = p.alpha_max * Math.pow(p.KI, 4) / (Math.pow(p.KI, 4) + Math.pow(Glu, 4));

            // Feed is active only during feed time
            const F = t <= p.t_feed ? p.feed_rate : 0;

            // Growth rates and yields (simplified example)
            const mu_glu = 0.5 * Glu/(Glu + 1);
            const mu_eta = 0.3 * Eta/(Eta + 1);

            return [
                X * ((1-alpha)*mu_glu + alpha*mu_eta) - (F/V)*X,
                -X*mu_glu/0.5 + F*(p.S_in - Glu)/V,
                X*mu_glu/2 - X*alpha*mu_eta/0.3 - (F/V)*Eta,
                0.1*(1 - E0) - 0.1*E0*Glu/(Glu + 1),
                0.1*Glu/(Glu + 1) - 0.1*E1,
                0.1/(Glu + 1) - 0.1*E2,
                F
            ];
        }

        // Simulation function
        function runSimulation() {
            const params = {
                alpha_max: parseFloat(document.getElementById('slider-alpha-max').value),
                KI: parseFloat(document.getElementById('slider-enzyme-control').value),
                feed_rate: parseFloat(document.getElementById('slider-feed-rate').value),
                S_in: parseFloat(document.getElementById('slider-substrate-feed-conc').value),
                t_feed: parseFloat(document.getElementById('slider-feed-time').value)
            };

            const t_max = parseFloat(document.getElementById('slider-t-max').value);
            const dt = 0.1;
            const t = Array.from({length: Math.ceil(t_max/dt)+1}, (_, i) => i*dt);

            let y = [0.1, 60, 0, 1.0, 0, 0, 1.0]; // Initial conditions
            const results = {t: [], X: [], Glu: [], Eta: [], E0: [], E1: [], E2: [], V: [], alpha: []};

            for(let i = 0; i < t.length; i++) {
                results.t.push(t[i]);
                results.X.push(y[0]);
                results.Glu.push(y[1]);
                results.Eta.push(y[2]);
                results.E0.push(y[3]);
                results.E1.push(y[4]);
                results.E2.push(y[5]);
                results.V.push(y[6]);
                results.alpha.push(params.alpha_max * Math.pow(params.KI, 4) / 
                                (Math.pow(params.KI, 4) + Math.pow(y[1], 4)));

                y = rk4(y, t[i], dt, params, dydt);
            }

            return results;
        }

        // Plot drawing function
        function drawPlots(data) {
            const plotType = document.getElementById('select-plot').value;

            if(plotType === "fed batch") {
                Plotly.react('plot-top', [
                    {x: data.t, y: data.X, name: 'X(t)', line: {color: 'blue'}},
                    {x: data.t, y: data.Glu, name: 'Glu(t)', line: {color: 'red'}},
                    {x: data.t, y: data.Eta, name: 'Eta(t)', line: {color: 'green'}}
                ], {
                    title: 'diauxic behavior',
                    xaxis: {title: 'time (h)'},
                    yaxis: {title: 'concentration [mol/m³]'}
                });

                Plotly.react('plot-bottom', [
                    {x: data.t, y: data.E2, name: 'E₂(t)', line: {color: 'black'}},
                    {x: data.t, y: data.E1, name: 'E₁(t)', line: {color: 'magenta'}},
                    {x: data.t, y: data.E0, name: 'E₀(t)', line: {color: 'orange'}}
                ], {
                    title: 'enzyme concentration',
                    xaxis: {title: 'time (h)'},
                    yaxis: {title: 'enzyme concentration [mol/m³]'}
                });
            } else {
                Plotly.react('plot-top', [
                    {x: data.t, y: data.Glu, name: 'Glu(t)', line: {color: 'red'}}
                ], {
                    title: 'glucose concentration',
                    xaxis: {title: 'time (h)'},
                    yaxis: {title: 'glucose concentration [mol/m³]'}
                });

                Plotly.react('plot-bottom', [
                    {x: data.t, y: data.alpha, name: 'α', line: {color: 'blue'}}
                ], {
                    title: 'fraction of respiring biomass',
                    xaxis: {title: 'time (h)'},
                    yaxis: {title: 'α'}
                });
            }
        }

        // Event handlers
        function updateSimulation() {
            const data = runSimulation();
            drawPlots(data);
        }

        // Initialize all slider value displays and attach event listeners
        document.querySelectorAll('input[type="range"]').forEach(slider => {
            const valueDisplay = document.getElementById('value-' + slider.id.split('-').slice(1).join('-'));
            valueDisplay.textContent = slider.value;

            slider.addEventListener('input', (e) => {
                valueDisplay.textContent = e.target.value;

                // Update feed time max value when t_max changes
                if(e.target.id === 'slider-t-max') {
                    const feedTimeSlider = document.getElementById('slider-feed-time');
                    feedTimeSlider.max = e.target.value;
                    if(parseFloat(feedTimeSlider.value) > parseFloat(e.target.value)) {
                        feedTimeSlider.value = e.target.value;
                        document.getElementById('value-feed-time').textContent = e.target.value;
                    }
                }

                updateSimulation();
            });
        });

        document.getElementById('select-plot').addEventListener('change', () => {
            drawPlots(currentData);
        });

        // Initial simulation
        let currentData = runSimulation();
        drawPlots(currentData);
    </script>
</body>
</html>
