    <script>
        let scene, camera, renderer, controls;
        let isDirty = true;

        // Initialize Three.js scene
        function init() {
            scene = new THREE.Scene();
            camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);

            renderer = new THREE.WebGLRenderer();
            renderer.setSize(window.innerWidth - 300, window.innerHeight);
            document.getElementById('visualization-container').appendChild(renderer.domElement);

            // Set initial camera position for ground view
            camera.position.set(0, 1, 5);

            // Add OrbitControls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.addEventListener('change', () => isDirty = true);

            // Basic scene setup
            setupScene();

            // Add event listeners
            setupEventListeners();

            // Start animation loop
            animate();
        }

        function setupScene() {
            // Add ambient light
            const ambientLight = new THREE.AmbientLight(0x404040);
            scene.add(ambientLight);

            // Add ground plane (for ground view)
            const groundGeometry = new THREE.CylinderGeometry(10, 10, 0.1, 32);
            const groundMaterial = new THREE.MeshPhongMaterial({ color: 0x228B22 });
            const ground = new THREE.Mesh(groundGeometry, groundMaterial);
            scene.add(ground);

            // Add Sun
            const sunGeometry = new THREE.SphereGeometry(1, 32, 32);
            const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00 });
            const sun = new THREE.Mesh(sunGeometry, sunMaterial);
            scene.add(sun);

            // Initial position calculation and update
            updateScene();
        }

        function setupEventListeners() {
            document.getElementById('slider-date').addEventListener('input', () => {
                updateDateLabel();
                isDirty = true;
                updateScene();
            });

            document.getElementById('slider-latitude').addEventListener('input', () => {
                updateLatitudeLabel();
                isDirty = true;
                updateScene();
            });

            document.getElementById('slider-time').addEventListener('input', () => {
                updateTimeLabel();
                isDirty = true;
                updateScene();
            });

            document.getElementById('select-view').addEventListener('change', (e) => {
                const isSpace = e.target.value === 'space';
                document.getElementById('options-ground').style.display = isSpace ? 'none' : 'block';
                document.getElementById('options-space').style.display = isSpace ? 'block' : 'none';
                switchView(isSpace);
            });
        }

        function updateScene() {
            // Calculate sun position and update scene elements
            // This is where the astronomical calculations would go
            updateReadouts();
        }

        function updateDateLabel() {
            const days = ['Jan 1', 'Feb 1', 'Mar 1', 'Mar 20', 'Apr 1', 'May 1', 'Jun 1', 'Jul 1', 'Aug 1', 'Sep 1', 'Oct 1', 'Nov 1', 'Dec 1'];
            const value = document.getElementById('slider-date').value;
            document.getElementById('label-date').textContent = days[Math.floor(value/30)];
        }

        function updateLatitudeLabel() {
            const value = document.getElementById('slider-latitude').value;
            document.getElementById('label-latitude').textContent = value + '°';
        }

        function updateTimeLabel() {
            const value = document.getElementById('slider-time').value;
            const hours = Math.floor(value);
            const minutes = Math.floor((value - hours) * 60);
            document.getElementById('label-time').textContent = 
                `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}`;
        }

        function updateReadouts() {
            // Update all readout spans with calculated values
            // This would use the results from astronomical calculations
        }

        function switchView(isSpace) {
            if (isSpace) {
                camera.position.set(0, 0, 30);
            } else {
                camera.position.set(0, 1, 5);
            }
            controls.target.set(0, 0, 0);
            isDirty = true;
        }

        function animate() {
            requestAnimationFrame(animate);
            if (isDirty) {
                renderer.render(scene, camera);
                isDirty = false;
            }
        }

        // Handle window resize
        window.addEventListener('resize', () => {
            camera.aspect = (window.innerWidth - 300) / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth - 300, window.innerHeight);
            isDirty = true;
        });

        // Initialize everything when the page loads
        init();
    </script>
</body>
</html>
