<!DOCTYPE html>
<html>
<head>
    <title>Unsteady-State Diffusion in a Slab</title>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <style>
        #main-container {
            max-width: 800px;
            margin: 0 auto;
            padding: 20px;
            font-family: Arial, sans-serif;
        }

        #control-panel {
            background: #f0f0f0;
            border: 1px solid #ccc;
            padding: 20px;
            margin: 20px 0;
            border-radius: 4px;
        }

        .control-row {
            display: flex;
            align-items: center;
            margin: 10px 0;
        }

        .control-row label {
            width: 200px;
        }

        .control-row input {
            flex: 1;
            margin: 0 20px;
        }

        #plot-div {
            width: 100%;
            height: 500px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <h1>Unsteady-State Diffusion in a Slab</h1>

        <div id="control-panel">
            <div class="control-row">
                <label for="time-slider">time</label>
                <input type="range" id="time-slider" min="0" max="2500" step="1" value="100">
                <span id="time-value">100.0</span>
            </div>
            <div class="control-row">
                <label for="diffusivity-slider">diffusivity in m<sup>2</sup>/s</label>
                <input type="range" id="diffusivity-slider" min="1e-10" max="1e-8" step="1e-10" value="4e-9">
                <span id="diffusivity-value">4.0 × 10⁻⁹</span>
            </div>
        </div>

        <div id="plot-div"></div>
    </div>

    <script>
        let dataRevision = 0;

        function formatScientific(num) {
            let exp = Math.floor(Math.log10(Math.abs(num)));
            let base = num / Math.pow(10, exp);
            return `${base.toFixed(1)} × 10${exp.toString().replace(/(-?)(\d+)/, 
                (_, neg, digits) => (neg || '') + '⁻'.repeat(digits.length))}`;
        }

        function calculateProfile(t, D) {
            const L = 0.004;
            const cs = 4.0e-3;
            const N = 100; // Number of terms in series
            const nx = 200; // Number of x points

            let x = Array.from({length: nx}, (_, i) => (i * L) / (nx - 1));
            let c = new Array(nx).fill(0);

            if (t === 0) {
                c = x.map(xi => (1.0 + 250 * xi) * 1e-3);
            } else {
                for (let i = 0; i < nx; i++) {
                    let sum = 0;
                    for (let n = 0; n < N; n++) {
                        const kn = ((n + 0.5) * Math.PI) / L;
                        const Bn = (2/L) * 1e-3 * ((250 * Math.pow(-1, n)) / (kn*kn) - 3 / kn);
                        sum += Bn * Math.sin(kn * x[i]) * Math.exp(-kn * kn * D * t);
                    }
                    c[i] = sum + cs;
                }
            }

            return {x, c};
        }

        function updatePlot() {
            const t = parseFloat(document.getElementById('time-slider').value);
            const D = parseFloat(document.getElementById('diffusivity-slider').value);

            const {x, c} = calculateProfile(t, D);

            const trace = {
                x: x,
                y: c,
                mode: 'lines',
                line: {color: '#1f77b4', width: 2}
            };

            const layout = {
                xaxis: {
                    title: 'position',
                    range: [0, 0.004],
                    tickformat: '.3f',
                    dtick: 0.001,
                    showgrid: true,
                    minor: {showgrid: true}
                },
                yaxis: {
                    title: 'concentration',
                    range: [0.001, 0.0042],
                    tickformat: '.4f',
                    dtick: 0.0005,
                    showgrid: true,
                    minor: {showgrid: true}
                },
                margin: {l: 80, r: 20, t: 20, b: 50},
                datarevision: dataRevision++
            };

            Plotly.react('plot-div', [trace], layout);
        }

        // Event listeners
        document.getElementById('time-slider').addEventListener('input', function() {
            document.getElementById('time-value').textContent = this.value + '.0';
            updatePlot();
        });

        document.getElementById('diffusivity-slider').addEventListener('input', function() {
            document.getElementById('diffusivity-value').textContent = formatScientific(this.value);
            updatePlot();
        });

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
