                <span id="value-a">5</span>
            </div>
            <div id="slider-n-controls" class="slider-container">
                <label for="slider-n">number of quadrilaterals:</label>
                <input type="range" id="slider-n" min="1" max="50" step="1" value="3">
                <span id="value-n">3</span>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let state = {
            funcKey: 'x',
            type: 'right',
            a: 5,
            n: 3
        };

        // Function definitions
        const functions = {
            x: x => x,
            x2: x => Math.pow(x-3, 2),
            x3: x => Math.pow(x-3, 3) + 27,
            sqrt: x => Math.sqrt(x),
            sin: x => Math.sin(x) + 1,
            exp: x => Math.exp(x)/3
        };

        // Antiderivatives for exact integral calculation
        const antiderivatives = {
            x: x => x * x / 2,
            x2: x => Math.pow(x-3, 3) / 3,
            x3: x => (Math.pow(x-3, 4) / 4) + 27 * x,
            sqrt: x => (2/3) * Math.pow(x, 3/2),
            sin: x => -Math.cos(x) + x,
            exp: x => Math.exp(x)/3
        };

        function setup() {
            const canvas = createCanvas(800, 400);
            canvas.parent('canvas-container');

            // Event listeners for controls
            document.querySelectorAll('.function-btn').forEach(btn => {
                btn.addEventListener('click', e => {
                    document.querySelectorAll('.function-btn').forEach(b => b.classList.remove('active'));
                    e.target.classList.add('active');
                    state.funcKey = e.target.dataset.function;
                });
            });

            document.querySelectorAll('.type-btn').forEach(btn => {
                btn.addEventListener('click', e => {
                    document.querySelectorAll('.type-btn').forEach(b => b.classList.remove('active'));
                    e.target.classList.add('active');
                    state.type = e.target.dataset.type;
                });
            });

            document.getElementById('slider-a').addEventListener('input', e => {
                state.a = parseFloat(e.target.value);
                document.getElementById('value-a').textContent = state.a.toFixed(2);
            });

            document.getElementById('slider-n').addEventListener('input', e => {
                state.n = parseInt(e.target.value);
                document.getElementById('value-n').textContent = state.n;
            });
        }

        function draw() {
            background(248, 248, 248);

            // Set up coordinate system
            const margin = 40;
            const xScale = (width - 2 * margin) / 10;
            const yScale = (height - 2 * margin) / 10;

            translate(margin, height - margin);
            scale(xScale, -yScale);

            // Draw axes
            stroke(200);
            line(0, 0, 9, 0); // x-axis
            line(0, -5, 0, 5); // y-axis

            // Draw function
            const f = functions[state.funcKey];
            noFill();
            stroke(0);
            beginShape();
            for(let x = 0; x <= state.a; x += 0.1) {
                vertex(x, f(x));
            }
            endShape();

            // Draw estimation rectangles
            const dx = state.a / state.n;
            let estimatedArea = 0;

            for(let i = 0; i < state.n; i++) {
                const x1 = i * dx;
                const x2 = (i + 1) * dx;
                let h;

                switch(state.type) {
                    case 'left':
                        h = f(x1);
                        break;
                    case 'right':
                        h = f(x2);
                        break;
                    case 'midpoint':
                        h = f((x1 + x2)/2);
                        break;
                    case 'overestimate':
                        h = Math.max(f(x1), f(x2));
                        break;
                    case 'underestimate':
                        h = Math.min(f(x1), f(x2));
                        break;
                    case 'trapezoidal':
                        h = (f(x1) + f(x2))/2;
                        break;
                }

                fill(173, 216, 230, 180);
                stroke(100);
                rect(x1, 0, dx, h);
                estimatedArea += dx * h;
            }

            // Draw upper limit marker
            stroke(255, 0, 0);
            line(state.a, 0, state.a, f(state.a));

            // Calculate true integral
            const trueIntegral = antiderivatives[state.funcKey](state.a) - antiderivatives[state.funcKey](0);

            // Display results
            resetMatrix();
            fill(0);
            noStroke();
            textSize(14);
            text(`Estimated Area: ${estimatedArea.toFixed(5)}`, 10, 20);
            text(`True Integral: ${trueIntegral.toFixed(5)}`, 10, 40);
        }
    </script>
</body>
</html>
