            padding: 20px;
            display: flex;
            justify-content: center;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="slider-group">
                <label>current (A)</label>
                <input type="range" id="slider-current" min="0" max="1" step="0.001" value="0.431">
                <span id="value-current" class="value-display">0.431</span>
            </div>
            <div class="slider-group">
                <label>horizontal Earth field (T)</label>
                <input type="range" id="slider-earth-field" min="0" max="0.0001" step="0.0000001" value="0.0000194">
                <span id="value-earth-field" class="value-display">1.94e-5</span>
            </div>
            <div class="slider-group">
                <label>radius (m)</label>
                <input type="range" id="slider-radius" min="0.1" max="1.0" step="0.01" value="0.5">
                <span id="value-radius" class="value-display">0.50</span>
            </div>
            <div class="slider-group">
                <label>number of turns</label>
                <input type="range" id="slider-turns" min="1" max="100" step="1" value="25">
                <span id="value-turns" class="value-display">25</span>
            </div>
            <button id="btn-reset">↻</button>
        </div>
        <div id="viz-container">
            <div id="p5-canvas"></div>
        </div>
    </div>

    <script>
        let current, earthField, radius, turns;
        let p5Canvas;

        function setup() {
            p5Canvas = createCanvas(600, 600);
            p5Canvas.parent('p5-canvas');
            colorMode(HSB);
            updateParameters();
            noLoop();
        }

        function ellipticK(m) {
            // Simple AGM implementation of complete elliptic integral of first kind
            let a = 1.0;
            let b = Math.sqrt(1.0 - m);
            let c = Math.sqrt(m);
            while (Math.abs(c) > 1e-15) {
                let an = (a + b) / 2;
                let bn = Math.sqrt(a * b);
                c = (a - b) / 2;
                a = an;
                b = bn;
            }
            return Math.PI / (2 * a);
        }

        function ellipticE(m) {
            // Simple implementation of complete elliptic integral of second kind
            let sum = 1.0;
            let term = 1.0;
            for (let i = 1; i <= 10; i++) {
                term *= m * (2*i-1)*(2*i-1) / (4*i*i);
                sum -= term / (2*i-1);
            }
            return sum * Math.PI/2;
        }

        function calculateField(y, z) {
            const mu0 = 4e-7 * Math.PI;
            let Btotal = {y: 0, z: earthField}; // Earth field along z-axis

            // Calculate field from both coils
            for (let zOffset of [-radius/2, radius/2]) {
                let zp = z - zOffset;
                if (y === 0) {
                    // Special case for y=0 (on axis)
                    let common = mu0 * current * turns * radius * radius / 
                               (2 * Math.pow(radius*radius + zp*zp, 1.5));
                    Btotal.z += common;
                } else {
                    let m = (4 * radius * y) / ((radius + y)*(radius + y) + zp*zp);
                    let commonFactor1 = (mu0 * current * turns) / (2 * Math.PI);
                    let commonFactor2 = 1 / Math.sqrt((radius + y)*(radius + y) + zp*zp);

                    let K = ellipticK(m);
                    let E = ellipticE(m);

                    Btotal.y += commonFactor1 * (zp/y) * commonFactor2 * 
                               (((radius*radius + y*y + zp*zp)/((radius-y)*(radius-y) + zp*zp)) * E - K);
                    Btotal.z += commonFactor1 * commonFactor2 * 
                               (K - ((radius*radius - y*y - zp*zp)/((radius-y)*(radius-y) + zp*zp)) * E);
                }
            }

            return Btotal;
        }

        function draw() {
            background(255);

            // Draw axes
            stroke(0, 0, 100); // Red in HSB
            strokeWeight(1);

            // Y-axis
            line(width/2, 0, width/2, height);
            // Z-axis
            line(0, height/2, width, height/2);

            // Draw streamlines
            const gridSize = 20;
            const stepSize = 0.01;
            const maxSteps = 100;

            // Find field magnitude range
            let minMag = Infinity, maxMag = 0;
            for(let i = 0; i < gridSize; i++) {
                for(let j = 0; j < gridSize; j++) {
                    let y = map(i, 0, gridSize-1, -0.5, 0.5);
                    let z = map(j, 0, gridSize-1, -0.5, 0.5);
                    let field = calculateField(y, z);
                    let mag = Math.sqrt(field.y*field.y + field.z*field.z);
                    minMag = Math.min(minMag, mag);
                    maxMag = Math.max(maxMag, mag);
                }
            }

            // Draw streamlines from grid points
            for(let i = 0; i < gridSize; i++) {
                for(let j = 0; j < gridSize; j++) {
                    let y = map(i, 0, gridSize-1, -0.5, 0.5);
                    let z = map(j, 0, gridSize-1, -0.5, 0.5);

                    let px = map(y, -0.5, 0.5, 0, width);
                    let py = map(z, -0.5, 0.5, height, 0);

                    for(let dir of [1, -1]) {
                        let currY = y;
                        let currZ = z;

                        for(let step = 0; step < maxSteps; step++) {
                            let field = calculateField(currY, currZ);
                            let mag = Math.sqrt(field.y*field.y + field.z*field.z);

                            // Map magnitude to color
                            let hue = map(mag, minMag, maxMag, 270, 60);
                            stroke(hue, 100, 100);

                            // Normalize direction
                            let dx = field.y / mag;
                            let dy = field.z / mag;

                            // Calculate next point
                            let nextY = currY + dir * dx * stepSize;
                            let nextZ = currZ + dir * dy * stepSize;

                            // Map to screen coordinates
                            let x1 = map(currY, -0.5, 0.5, 0, width);
                            let y1 = map(currZ, -0.5, 0.5, height, 0);
                            let x2 = map(nextY, -0.5, 0.5, 0, width);
                            let y2 = map(nextZ, -0.5, 0.5, height, 0);

                            line(x1, y1, x2, y2);

                            if(step % 10 === 0) {
                                // Draw direction arrow
                                push();
                                translate(x2, y2);
                                rotate(atan2(y2-y1, x2-x1));
                                line(0, 0, -10, -5);
                                line(0, 0, -10, 5);
                                pop();
                            }

                            currY = nextY;
                            currZ = nextZ;

                            // Stop if out of bounds
                            if(Math.abs(currY) > 0.5 || Math.abs(currZ) > 0.5) break;
                        }
                    }
                }
            }
        }

        function updateParameters() {
            current = parseFloat(document.getElementById('slider-current').value);
            earthField = parseFloat(document.getElementById('slider-earth-field').value);
            radius = parseFloat(document.getElementById('slider-radius').value);
            turns = parseInt(document.getElementById('slider-turns').value);
            redraw();
        }

        // Event listeners
        document.querySelectorAll('input[type="range"]').forEach(slider => {
            slider.addEventListener('input', function() {
                let valueSpan = document.getElementById('value-' + this.id.split('-')[1]);
                if(this.id === 'slider-earth-field') {
                    valueSpan.textContent = parseFloat(this.value).toExponential(2);
                } else {
                    valueSpan.textContent = parseFloat(this.value).toFixed(2);
                }
                updateParameters();
            });
        });

        document.getElementById('btn-reset').addEventListener('click', function() {
            document.getElementById('slider-current').value = 0.431;
            document.getElementById('slider-earth-field').value = 0.0000194;
            document.getElementById('slider-radius').value = 0.5;
            document.getElementById('slider-turns').value = 25;

            document.getElementById('value-current').textContent = '0.431';
            document.getElementById('value-earth-field').textContent = '1.94e-5';
            document.getElementById('value-radius').textContent = '0.50';
            document.getElementById('value-turns').textContent = '25';

            updateParameters();
        });
    </script>
</body>
</html>
