<!DOCTYPE html>
<html>
<head>
    <title>Caesar Cipher Demo</title>
    <style>
        body {
            font-family: monospace;
            margin: 20px;
        }
        #app-container {
            max-width: 800px;
            margin: 0 auto;
        }
        .control-row {
            display: flex;
            gap: 10px;
            margin: 10px 0;
        }
        .slider-row {
            display: flex;
            align-items: center;
            gap: 10px;
            margin: 10px 0;
        }
        .keyboard {
            display: flex;
            flex-wrap: wrap;
            gap: 5px;
            margin: 10px 0;
        }
        .keyboard button {
            padding: 10px;
            min-width: 30px;
            cursor: pointer;
        }
        .alphabet-map {
            background: #f0f0f0;
            padding: 10px;
            margin: 10px 0;
        }
        button {
            padding: 5px 10px;
            border-radius: 4px;
            border: 1px solid #ccc;
            background: #f0f0f0;
            cursor: pointer;
        }
        button:hover {
            background: #e0e0e0;
        }
        button.active {
            background: #007bff;
            color: white;
        }
        table {
            width: 100%;
            border-collapse: collapse;
        }
        th, td {
            border: 1px solid #ccc;
            padding: 5px;
            text-align: left;
        }
        tr:nth-child(even) {
            background: #f5f5f5;
        }
        .highlighted {
            color: red;
            font-weight: bold;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="tab-controls">
            <button id="btn-toggle-encoding" class="active">encoding</button>
            <button id="btn-toggle-decoding">decoding</button>
        </div>

        <div id="panel-encoding">
            <div class="control-row">
                <select id="select-message">
                    <option value="">Choose a message</option>
                    <option value="THE IDES OF MARCH ARE COME">THE IDES OF MARCH ARE COME</option>
                    <option value="EVEN YOU BRUTUS">EVEN YOU BRUTUS</option>
                </select>
                <button id="btn-clear-encoding">clear encoding area</button>
            </div>

            <div class="slider-row">
                <label for="slider-encoding-offset">encoding offset</label>
                <input type="range" id="slider-encoding-offset" min="0" max="25" value="0">
                <span id="span-encoding-offset-value">0</span>
            </div>

            <div id="input-area-encoding">
                <p>or enter a message directly (max 32 characters)</p>
                <div id="keyboard-encoding" class="keyboard"></div>
            </div>

            <div id="output-area-encoding">
                <p>message = <span id="display-message"></span></p>
                <p>plaintext = <span id="display-plaintext"></span></p>
                <div class="alphabet-map">
                    <pre id="display-encoding-alphabet-from"></pre>
                    <pre>↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓</pre>
                    <pre id="display-encoding-alphabet-to"></pre>
                </div>
                <p>ciphertext = <span id="display-ciphertext"></span></p>
            </div>
        </div>

        <div id="panel-decoding" style="display: none;">
            <div class="control-row">
                <button id="btn-intercept">intercept an encrypted message</button>
                <button id="btn-clear-decoding">clear decoding area</button>
            </div>

            <div class="slider-row">
                <label for="slider-decoding-offset">decoding offset</label>
                <input type="range" id="slider-decoding-offset" min="-25" max="25" value="0">
                <span id="span-decoding-offset-value">0</span>
            </div>

            <div id="input-area-decoding">
                <p>or enter a ciphertext string directly (max 32 characters)</p>
                <div id="keyboard-decoding" class="keyboard"></div>
            </div>

            <div id="output-area-decoding">
                <p>intercept = <span id="display-intercept"></span></p>
                <div class="alphabet-map">
                    <pre id="display-decoding-alphabet-from"></pre>
                    <pre>↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓ ↓</pre>
                    <pre id="display-decoding-alphabet-to"></pre>
                </div>
                <table id="table-decoding-output">
                    <thead><tr><th>offset</th><th>decoding</th></tr></thead>
                    <tbody></tbody>
                </table>
            </div>
        </div>
    </div>

    <script>
        const WORD_LIST = ['THE', 'IDES', 'OF', 'MARCH', 'ARE', 'COME', 'EVEN', 'YOU', 'BRUTUS', 
                          'CAESAR', 'CIPHER', 'HELLO', 'WORLD', 'SECRET', 'MESSAGE', 'ATTACK', 
                          'DAWN', 'I', 'A'].sort((a, b) => b.length - a.length);

        let currentMessage = "";
        let currentCiphertext = "";

        // Initialize keyboard buttons
        function initKeyboards() {
            const encKeyboard = document.getElementById('keyboard-encoding');
            const decKeyboard = document.getElementById('keyboard-decoding');

            // Encoding keyboard (A-Z, SPACE, BACKSPACE)
            for (let i = 65; i <= 90; i++) {
                const btn = document.createElement('button');
                btn.textContent = String.fromCharCode(i);
                encKeyboard.appendChild(btn);
            }
            encKeyboard.appendChild(createButton('SPACE'));
            encKeyboard.appendChild(createButton('BACKSPACE'));

            // Decoding keyboard (A-Z, BACKSPACE)
            for (let i = 65; i <= 90; i++) {
                const btn = document.createElement('button');
                btn.textContent = String.fromCharCode(i);
                decKeyboard.appendChild(btn);
            }
            decKeyboard.appendChild(createButton('BACKSPACE'));
        }

        function createButton(text) {
            const btn = document.createElement('button');
            btn.textContent = text;
            return btn;
        }

        function updateEncoding() {
            const offset = parseInt(document.getElementById('slider-encoding-offset').value);
            const plaintext = currentMessage.toUpperCase().replace(/ /g, 'X');

            document.getElementById('display-plaintext').textContent = plaintext;

            // Update alphabet maps
            const alphabet = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'.split('');
            document.getElementById('display-encoding-alphabet-from').textContent = alphabet.join(' ');

            const shiftedAlphabet = alphabet.map(char => 
                String.fromCharCode((char.charCodeAt(0) - 65 + offset) % 26 + 65)
            );
            document.getElementById('display-encoding-alphabet-to').textContent = shiftedAlphabet.join(' ');

            // Generate ciphertext
            currentCiphertext = plaintext.split('').map(char => {
                if (char === ' ') return char;
                return String.fromCharCode((char.charCodeAt(0) - 65 + offset) % 26 + 65);
            }).join('');

            document.getElementById('display-ciphertext').textContent = currentCiphertext;
        }

        function updateDecoding() {
            const offset = parseInt(document.getElementById('slider-decoding-offset').value);
            const intercept = document.getElementById('display-intercept').textContent;

            // Update alphabet maps
            const alphabet = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'.split('');
            document.getElementById('display-decoding-alphabet-to').textContent = alphabet.join(' ');

            const shiftedAlphabet = alphabet.map(char => 
                String.fromCharCode((char.charCodeAt(0) - 65 - offset + 26) % 26 + 65)
            );
            document.getElementById('display-decoding-alphabet-from').textContent = shiftedAlphabet.join(' ');

            // Update decoding table
            const tbody = document.querySelector('#table-decoding-output tbody');
            tbody.innerHTML = '';

            for (let d = offset - 2; d <= offset + 2; d++) {
                const row = document.createElement('tr');
                row.innerHTML = `<td>${d}</td>`;

                const decrypted = intercept.split('').map(char => {
                    if (char === ' ') return char;
                    return String.fromCharCode((char.charCodeAt(0) - 65 - d + 26) % 26 + 65);
                }).join('');

                // Highlight known words
                let highlighted = decrypted;
                for (const word of WORD_LIST) {
                    const regex = new RegExp(word, 'g');
                    highlighted = highlighted.replace(regex, `<span class="highlighted">${word}</span>`);
                }

                row.innerHTML += `<td>${highlighted}</td>`;
                tbody.appendChild(row);
            }
        }

        // Event Listeners
        document.getElementById('btn-toggle-encoding').onclick = () => {
            document.getElementById('panel-encoding').style.display = 'block';
            document.getElementById('panel-decoding').style.display = 'none';
            document.getElementById('btn-toggle-encoding').classList.add('active');
            document.getElementById('btn-toggle-decoding').classList.remove('active');
        };

        document.getElementById('btn-toggle-decoding').onclick = () => {
            document.getElementById('panel-encoding').style.display = 'none';
            document.getElementById('panel-decoding').style.display = 'block';
            document.getElementById('btn-toggle-encoding').classList.remove('active');
            document.getElementById('btn-toggle-decoding').classList.add('active');
        };

        document.getElementById('select-message').onchange = (e) => {
            currentMessage = e.target.value;
            document.getElementById('display-message').textContent = currentMessage;
            updateEncoding();
        };

        document.getElementById('slider-encoding-offset').oninput = (e) => {
            document.getElementById('span-encoding-offset-value').textContent = e.target.value;
            updateEncoding();
        };

        document.getElementById('slider-decoding-offset').oninput = (e) => {
            document.getElementById('span-decoding-offset-value').textContent = e.target.value;
            updateDecoding();
        };

        document.getElementById('btn-clear-encoding').onclick = () => {
            currentMessage = "";
            currentCiphertext = "";
            document.getElementById('select-message').value = "";
            document.getElementById('slider-encoding-offset').value = 0;
            document.getElementById('span-encoding-offset-value').textContent = "0";
            document.getElementById('display-message').textContent = "";
            document.getElementById('display-plaintext').textContent = "";
            document.getElementById('display-ciphertext').textContent = "";
            updateEncoding();
        };

        document.getElementById('btn-clear-decoding').onclick = () => {
            document.getElementById('display-intercept').textContent = "";
            document.getElementById('slider-decoding-offset').value = 0;
            document.getElementById('span-decoding-offset-value').textContent = "0";
            document.querySelector('#table-decoding-output tbody').innerHTML = "";
            updateDecoding();
        };

        document.getElementById('btn-intercept').onclick = () => {
            document.getElementById('display-intercept').textContent = currentCiphertext;
            updateDecoding();
        };

        // Keyboard event handlers
        document.getElementById('keyboard-encoding').onclick = (e) => {
            if (e.target.tagName !== 'BUTTON') return;

            if (e.target.textContent === 'BACKSPACE') {
                currentMessage = currentMessage.slice(0, -1);
            } else if (currentMessage.length < 32) {
                currentMessage += e.target.textContent === 'SPACE' ? ' ' : e.target.textContent;
            }

            document.getElementById('display-message').textContent = currentMessage;
            updateEncoding();
        };

        document.getElementById('keyboard-decoding').onclick = (e) => {
            if (e.target.tagName !== 'BUTTON') return;
            const intercept = document.getElementById('display-intercept');

            if (e.target.textContent === 'BACKSPACE') {
                intercept.textContent = intercept.textContent.slice(0, -1);
            } else if (intercept.textContent.length < 32) {
                intercept.textContent += e.target.textContent;
            }

            updateDecoding();
        };

        // Initialize
        initKeyboards();
        updateEncoding();
        updateDecoding();
    </script>
</body>
</html>
