    </style>
</head>
<body>
    <h1>Applying Graphics Filters Using Convolution</h1>
    <p>This demo shows how convolution filters can be applied to create visual effects like shadows and blur.</p>

    <div id="control-panel">
        <div class="control-row">
            <label>convolver:</label>
            <button onclick="decrementSlider('slider-convolver')">-</button>
            <input type="range" id="slider-convolver" min="1" max="5" step="1" value="2">
            <button id="convolver-plus-btn" onclick="incrementSlider('slider-convolver')">+</button>
        </div>
        <div class="control-row">
            <label>image size:</label>
            <button onclick="decrementSlider('slider-image-size')">-</button>
            <input type="range" id="slider-image-size" min="10" max="40" step="1" value="24">
            <button id="image-size-plus-btn" onclick="incrementSlider('slider-image-size')">+</button>
        </div>
        <div class="control-row">
            <label>pick a letter:</label>
            <input type="text" id="input-letter" maxlength="1" value="a">
        </div>
        <div class="control-row">
            <label>filter:</label>
            <div class="radio-group">
                <input type="radio" name="filter" id="radio-shadow" value="shadow" checked>
                <label for="radio-shadow">shadow</label>
                <input type="radio" name="filter" id="radio-blur" value="blur">
                <label for="radio-blur">blur</label>
            </div>
        </div>
    </div>

    <div id="visualization-area">
        <div class="canvas-container">
            <div>
                <p>before</p>
                <div id="canvas-before-container"></div>
            </div>
            <div>
                <p>after</p>
                <div id="canvas-after-container"></div>
            </div>
        </div>
    </div>

    <script>
        let sketch = function(p) {
            const CANVAS_SIZE = 200;
            let beforeGrid, afterGrid;

            p.setup = function() {
                p.createCanvas(CANVAS_SIZE * 2, CANVAS_SIZE);
                p.noLoop();
                updateVisualization();
            };

            function createConvolutionKernel(size, type) {
                const k = size;
                const D = 2 * k + 1;
                let kernel = Array(D).fill().map(() => Array(D).fill(0));

                if (type === 'blur') {
                    const val = 1.0 / (D * D);
                    kernel = kernel.map(row => row.map(() => val));
                } else { // shadow
                    for(let i = k; i < D; i++) {
                        for(let j = k; j < D; j++) {
                            kernel[i][j] = 1.0 / (k * k);
                        }
                    }
                }
                return kernel;
            }

            function applyConvolution(grid, kernel) {
                const N = grid.length;
                const k = Math.floor(kernel.length / 2);
                let result = Array(N).fill().map(() => Array(N).fill(0));

                for(let i = 0; i < N; i++) {
                    for(let j = 0; j < N; j++) {
                        let sum = 0;
                        for(let ki = -k; ki <= k; ki++) {
                            for(let kj = -k; kj <= k; kj++) {
                                const ni = i + ki;
                                const nj = j + kj;
                                const val = (ni >= 0 && ni < N && nj >= 0 && nj < N) ? 
                                    grid[ni][nj] : 0;
                                sum += val * kernel[ki + k][kj + k];
                            }
                        }
                        result[i][j] = Math.min(1, Math.max(0, sum));
                    }
                }
                return result;
            }

            function updateVisualization() {
                const N = parseInt(document.getElementById('slider-image-size').value);
                const letter = document.getElementById('input-letter').value.charAt(0);
                const convolverSize = parseInt(document.getElementById('slider-convolver').value);
                const filterType = document.querySelector('input[name="filter"]:checked').value;

                // Create before grid
                beforeGrid = Array(N).fill().map(() => Array(N).fill(0));
                p.background(255);

                // Draw letter to graphics buffer
                let pg = p.createGraphics(N, N);
                pg.background(255);
                pg.fill(0);
                pg.textAlign(p.CENTER, p.CENTER);
                pg.textSize(N * 0.8);
                pg.text(letter, N/2, N/2);
                pg.loadPixels();

                // Convert to binary grid
                for(let i = 0; i < N; i++) {
                    for(let j = 0; j < N; j++) {
                        const idx = (i * N + j) * 4;
                        beforeGrid[i][j] = pg.pixels[idx] < 128 ? 1 : 0;
                    }
                }

                // Apply convolution
                const kernel = createConvolutionKernel(convolverSize, filterType);
                afterGrid = applyConvolution(beforeGrid, kernel);

                // Draw grids
                const cellSize = CANVAS_SIZE / N;
                p.background(255);

                // Draw before grid
                for(let i = 0; i < N; i++) {
                    for(let j = 0; j < N; j++) {
                        p.noStroke();
                        p.fill(beforeGrid[i][j] * 255);
                        p.rect(j * cellSize, i * cellSize, cellSize, cellSize);
                    }
                }

                // Draw after grid
                for(let i = 0; i < N; i++) {
                    for(let j = 0; j < N; j++) {
                        p.noStroke();
                        p.fill(255 * (1 - afterGrid[i][j]));
                        p.rect(CANVAS_SIZE + j * cellSize, i * cellSize, cellSize, cellSize);
                    }
                }
            }

            window.updateVisualization = updateVisualization;
        };

        new p5(sketch);

        // Event listeners
        document.querySelectorAll('input').forEach(input => {
            input.addEventListener('input', updateVisualization);
        });

        document.getElementById('input-letter').addEventListener('keydown', (e) => {
            if(e.key === 'ArrowUp' || e.key === 'ArrowDown') {
                e.preventDefault();
                let curr = e.target.value.charCodeAt(0);
                if(e.key === 'ArrowUp') {
                    curr = curr === 122 ? 97 : curr + 1; // z -> a or increment
                } else {
                    curr = curr === 97 ? 122 : curr - 1; // a -> z or decrement
                }
                e.target.value = String.fromCharCode(curr);
                updateVisualization();
            }
        });

        function incrementSlider(id) {
            const slider = document.getElementById(id);
            slider.value = Math.min(parseInt(slider.value) + parseInt(slider.step), slider.max);
            updateVisualization();
        }

        function decrementSlider(id) {
            const slider = document.getElementById(id);
            slider.value = Math.max(parseInt(slider.value) - parseInt(slider.step), slider.min);
            updateVisualization();
        }
    </script>
</body>
</html>
