        // Protein data generation
        const AA_CODES = ['A','C','D','E','F','G','H','I','K','L','M','N','P','Q','R','S','T','V','W','Y'];

        function generateProteinData(numResidues) {
            const protein = [];
            let pos = new THREE.Vector3(0,0,0);

            for(let i = 0; i < numResidues; i++) {
                const residue = {
                    resName: AA_CODES[Math.floor(Math.random() * AA_CODES.length)],
                    resSeq: i + 1,
                    atoms: []
                };

                // Random walk for CA position
                pos.add(new THREE.Vector3(
                    (Math.random() - 0.5) * 3,
                    (Math.random() - 0.5) * 3,
                    (Math.random() - 0.5) * 3
                ));

                // Generate atoms with offsets from CA
                residue.atoms.push(
                    {atomName: 'CA', x: pos.x, y: pos.y, z: pos.z},
                    {atomName: 'N', x: pos.x-1, y: pos.y, z: pos.z},
                    {atomName: 'C', x: pos.x+1, y: pos.y, z: pos.z},
                    {atomName: 'O', x: pos.x+1, y: pos.y-1, z: pos.z},
                    {atomName: 'CB', x: pos.x, y: pos.y+1, z: pos.z}
                );

                protein.push(residue);
            }
            return protein;
        }

        // Initialize protein data
        const proteinData = generateProteinData(400);

        // Populate residue selector
        const selectResidue = document.getElementById('select-residue');
        AA_CODES.forEach(code => {
