        // Initialize Three.js
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xffffff);

        const camera = new THREE.PerspectiveCamera(75, window.innerWidth/window.innerHeight, 0.1, 1000);
        camera.position.z = 100;

        const renderer = new THREE.WebGLRenderer();
        renderer.setSize(800, 600);
        document.getElementById('canvas-container').appendChild(renderer.domElement);

        // Initialize p5.js
        let p5Instance = null;

        // Event listeners for controls
        document.getElementById('btn-crystallography').addEventListener('click', () => {
            document.getElementById('btn-crystallography').classList.add('active');
            document.getElementById('btn-contact-map').classList.remove('active');
            // Switch to 3D view
            if(p5Instance) p5Instance.remove();
            renderer.domElement.style.display = 'block';
        });

        document.getElementById('btn-contact-map').addEventListener('click', () => {
            document.getElementById('btn-contact-map').classList.add('active');
            document.getElementById('btn-crystallography').classList.remove('active');
            // Switch to 2D view
            renderer.domElement.style.display = 'none';
            initP5();
        });

        // Initialize p5.js sketch
        function initP5() {
            p5Instance = new p5((p) => {
                p.setup = () => {
                    p.createCanvas(800, 600);
                };

                p.draw = () => {
                    p.background(255);
                    // Draw contact map here
                };
            }, document.getElementById('canvas-container'));
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            renderer.render(scene, camera);
        }
        animate();

        // Update info display
        function updateInfoDisplay() {
            const residueA = proteinData[document.getElementById('slider-browse-sequence').value - 1];
            const residueX = proteinData[document.getElementById('slider-residue-x').value - 1];
            const residueY = proteinData[document.getElementById('slider-residue-y').value - 1];

            document.getElementById('info-display').textContent = 
                `Res A: ${residueA.resName}${residueA.resSeq} | ` +
                `Res X: ${residueX.resName}${residueX.resSeq} | ` +
                `Res Y: ${residueY.resName}${residueY.resSeq}`;
        }

        // Initialize
        updateInfoDisplay();
    </script>
