            </div>
            <div>
                <input type="checkbox" id="check-show-locations" checked>
                <label for="check-show-locations">show locations</label>
            </div>
            <div>
                <input type="checkbox" id="check-show-some">
                <label for="check-show-some">show some rivers and lakes</label>
            </div>
        </div>
        <p id="p-position">Current position {-172, -19}</p>
        <div id="table-container">
            <table id="table-quiz"></table>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        const riverData = [
            {name: 'Nile', lon: 31.2, lat: 30.2, found: false},
            {name: 'Saint Lawrence', lon: -64.5, lat: 49.5, found: false},
            {name: 'Nelson', lon: -92.8, lat: 57.1, found: false},
            {name: 'Mekong', lon: 106.6, lat: 9.5, found: false},
            {name: 'Araguaia', lon: -51.1, lat: -9.3, found: false},
            {name: 'Yangtze', lon: 121.5, lat: 31.8, found: false},
            {name: 'Brahmaputra', lon: 90.6, lat: 23.6, found: false},
            {name: 'Yukon', lon: -164.5, lat: 62.6, found: false},
            {name: 'Mackenzie', lon: -134.1, lat: 68.4, found: false}
            // ... additional rivers would be listed here
        ];

        const lakeData = [
            {name: 'Onega', lon: 35.5, lat: 61.7, found: false},
            {name: 'Erie', lon: -81.2, lat: 42.2, found: false},
            {name: 'Huron', lon: -82.4, lat: 44.8, found: false},
            {name: 'Issyk-Kul', lon: 77.3, lat: 42.4, found: false},
            {name: 'Michigan', lon: -87.1, lat: 43.7, found: false},
            {name: 'Ladoga', lon: 31.5, lat: 60.8, found: false},
            {name: 'Winnipeg', lon: -97.2, lat: 52.1, found: false},
            {name: 'Baikal', lon: 108.2, lat: 53.5, found: false},
            {name: 'Tanganyika', lon: 29.9, lat: -6.5, found: false}
            // ... additional lakes would be listed here
        ];

        let currentData = riverData;
        let worldMap;

        const sketch = new p5((p) => {
            p.preload = () => {
                worldMap = p.loadImage('https://upload.wikimedia.org/wikipedia/commons/c/c5/Blue_Marble_Next_Generation_%2B_topography_%2B_bathymetry.jpg');
            };

            p.setup = () => {
                p.createCanvas(1200, 600).parent('canvas-container');
                updateTable();
            };

            p.draw = () => {
                p.image(worldMap, 0, 0, p.width, p.height);

                // Draw location markers if enabled
                if (document.getElementById('check-show-locations').checked) {
                    currentData.forEach(item => {
                        if (!item.found) {
                            const [x, y] = mapToPixels(item.lon, item.lat);
                            p.fill('red');
                            p.noStroke();
                            p.circle(x, y, 10);
                        }
                    });
                }

                // Draw hint labels if enabled
                if (document.getElementById('check-show-some').checked) {
                    const unfoundItems = currentData.filter(item => !item.found);
                    const hintItems = unfoundItems.slice(0, 3);
                    hintItems.forEach(item => {
                        const [x, y] = mapToPixels(item.lon, item.lat);
                        p.fill('white');
                        p.textSize(12);
                        p.text(item.name, x + 15, y);
                    });
                }

                // Draw cursor marker
                p.fill(173, 216, 230, 150);
                p.noStroke();
                p.circle(p.mouseX, p.mouseY, 30);

                // Update position display
                const [lon, lat] = mapToGeo(p.mouseX, p.mouseY);
                document.getElementById('p-position').textContent = 
                    `Current position {${Math.round(lon)}, ${Math.round(lat)}}`;
            };

            p.mouseClicked = () => {
                if (p.mouseX < 0 || p.mouseX > p.width || p.mouseY < 0 || p.mouseY > p.height) return;

                const clickPos = [p.mouseX, p.mouseY];
                for (let item of currentData) {
                    if (item.found) continue;

                    const [x, y] = mapToPixels(item.lon, item.lat);
                    const d = p.dist(clickPos[0], clickPos[1], x, y);

                    if (d < 15) {
                        item.found = true;
                        updateTable();
                        break;
                    }
                }
            };

            function mapToPixels(lon, lat) {
                const x = p.map(lon, -180, 180, 0, p.width);
                const y = p.map(lat, 90, -90, 0, p.height);
                return [x, y];
            }

            function mapToGeo(x, y) {
                const lon = p.map(x, 0, p.width, -180, 180);
                const lat = p.map(y, 0, p.height, 90, -90);
                return [lon, lat];
            }
        });

        function updateTable() {
            const table = document.getElementById('table-quiz');
            table.innerHTML = '';

            // Create header row
            const headerRow = document.createElement('tr');
            currentData.forEach(item => {
                const th = document.createElement('th');
                th.textContent = item.name;
                headerRow.appendChild(th);
            });
            table.appendChild(headerRow);

            // Create status row
            const statusRow = document.createElement('tr');
            currentData.forEach(item => {
                const td = document.createElement('td');
                td.textContent = item.found ? 'True' : 'False';
                if (item.found) td.classList.add('found');
                statusRow.appendChild(td);
            });
            table.appendChild(statusRow);
        }

        // Event Listeners
        document.getElementById('radio-rivers').addEventListener('change', () => {
            currentData = riverData;
            updateTable();
        });

        document.getElementById('radio-lakes').addEventListener('change', () => {
            currentData = lakeData;
            updateTable();
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            currentData.forEach(item => item.found = false);
            updateTable();
        });
    </script>
</body>
</html>
