<!DOCTYPE html>
<html>
<head>
    <title>Function Series Visualization</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <style>
        body {
            margin: 20px;
            font-family: Arial, sans-serif;
        }
        #control-panel {
            display: flex;
            align-items: center;
            gap: 20px;
            margin-bottom: 20px;
        }
        #visualization-area {
            display: flex;
            gap: 20px;
        }
        #function-plot-container {
            flex: 2;
            position: relative;
        }
        #sum-plot-container {
            flex: 1;
        }
        #formula-label {
            position: absolute;
            top: 10px;
            right: 10px;
        }
        canvas {
            border: 1px solid #ccc;
        }
    </style>
</head>
<body>
    <div id="control-panel">
        <label for="slider-n">n:</label>
        <input type="range" id="slider-n" min="1" max="20" step="1" value="1">
        <input type="number" id="input-n" min="1" max="20" step="1" value="1">
    </div>
    <div id="visualization-area">
        <div id="function-plot-container">
            <div id="canvas-function"></div>
            <div id="formula-label">$$y = \frac{2}{x}$$</div>
        </div>
        <div id="sum-plot-container">
            <div id="canvas-sum"></div>
        </div>
    </div>

    <script>
        const colors = ['#ffff80', '#98fb98', '#7fffd4', '#00ffff', '#87cefa', 
                       '#6495ed', '#9370db', '#8a2be2', '#ff69b4', '#ff6347',
                       '#ffa500', '#9acd32', '#32cd32', '#00fa9a', '#48d1cc',
                       '#4169e1', '#8a2be2', '#ba55d3', '#ff1493', '#ff4500'];

        let n = 1;
        let functionSketch = null;
        let sumSketch = null;

        // Sync controls
        document.getElementById('slider-n').addEventListener('input', function(e) {
            n = parseInt(e.target.value);
            document.getElementById('input-n').value = n;
            updatePlots();
        });

        document.getElementById('input-n').addEventListener('input', function(e) {
            n = Math.min(Math.max(parseInt(e.target.value) || 1, 1), 20);
            document.getElementById('slider-n').value = n;
            updatePlots();
        });

        const functionPlot = (p) => {
            p.setup = function() {
                p.createCanvas(600, 400);
            };

            p.draw = function() {
                p.background(255);
                p.translate(50, p.height - 50);

                // Draw axes
                p.stroke(0);
                p.line(0, 0, p.width-70, 0);  // x-axis
                p.line(0, 0, 0, -p.height+70); // y-axis

                // Draw ticks and labels
                for(let i = 0; i <= 10; i++) {
                    let x = i * (p.width-100)/10;
                    p.line(x, 0, x, 5);
                    p.text(i, x-5, 20);
                }
                for(let i = 0; i <= 2; i+=0.5) {
                    let y = -i * (p.height-100)/2.2;
                    p.line(-5, y, 0, y);
                    p.text(i, -25, y+5);
                }

                // Draw function curve
                p.stroke(0, 0, 255);
                p.noFill();
                p.beginShape();
                for(let x = 0.5; x <= 10; x += 0.1) {
                    let px = x * (p.width-100)/10;
                    let py = -(2/x) * (p.height-100)/2.2;
                    p.vertex(px, py);
                }
                p.endShape();

                // Draw rectangles
                for(let k = 1; k <= n; k++) {
                    p.fill(colors[k-1] + '80');
                    p.noStroke();
                    let x1 = k * (p.width-100)/10;
                    let x2 = (k+1) * (p.width-100)/10;
                    let y1 = -(2/k) * (p.height-100)/2.2;
                    let y2 = -(2/(k+1)) * (p.height-100)/2.2;
                    p.quad(x1, y1, x2, y2, x2, 0, x1, 0);

                    if(k <= 3) {
                        p.stroke(128, 128, 128);
                        p.setLineDash([5, 5]);
                        p.line(x1, 0, x1, y1);
                        p.setLineDash([]);
                    }
                }
            };
        };

        const sumPlot = (p) => {
            p.setup = function() {
                p.createCanvas(300, 400);
            };

            p.draw = function() {
                p.background(255);
                p.translate(50, p.height - 50);

                // Draw axes
                p.stroke(0);
                p.line(0, 0, p.width-70, 0);  // x-axis
                p.line(0, 0, 0, -p.height+70); // y-axis

                // Draw ticks and labels
                for(let i = 0; i <= 1; i++) {
                    let x = i * (p.width-100)/1.5;
                    p.line(x, 0, x, 5);
                    p.text(i, x-5, 20);
                }
                for(let i = 0; i <= 2; i+=0.5) {
                    let y = -i * (p.height-100)/2.2;
                    p.line(-5, y, 0, y);
                    p.text(i, -25, y+5);
                }

                // Draw stacked rectangles
                let yStack = 0;
                for(let k = n; k >= 1; k--) {
                    let h = 2/k - 2/(k+1);
                    p.fill(colors[k-1] + '80');
                    p.noStroke();
                    let y = -yStack * (p.height-100)/2.2;
                    let height = -h * (p.height-100)/2.2;
                    p.rect(0, y, (p.width-100)/1.5, height);
                    yStack += h;
                }
            };
        };

        function updatePlots() {
            if(functionSketch) functionSketch.draw();
            if(sumSketch) sumSketch.draw();
        }

        // Initialize sketches
        new p5(functionPlot, 'canvas-function');
        new p5(sumPlot, 'canvas-sum');
    </script>
</body>
</html>
