    <title>Biochemical Reactor Visualization</title>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <style>
        #main-container {
            display: flex;
            width: 100%;
            height: 100vh;
            font-family: Arial, sans-serif;
        }

        #control-panel {
            width: 300px;
            padding: 20px;
            background: #f5f5f5;
        }

        #visualization-area {
            flex: 1;
            padding: 20px;
        }

        .button-group {
            margin-bottom: 20px;
        }

        .button-group button {
            padding: 8px 15px;
            margin-right: 5px;
            cursor: pointer;
        }

        .button-group button.active {
            background: #007bff;
            color: white;
            border: none;
        }

        .slider-container {
            margin-bottom: 15px;
        }

        .slider-container label {
            display: block;
            margin-bottom: 5px;
        }

        .slider-container input[type="range"] {
            width: 100%;
        }

        #plot-container {
            width: 100%;
            height: 600px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="button-group">
                <button id="btn-monod" class="active">Monod</button>
                <button id="btn-si">Substrate Inhibition</button>
            </div>

            <div class="button-group">
                <button id="btn-bifurcation" class="active">Bifurcation Diagram</button>
                <button id="btn-nontrivial">Nontrivial Steady State</button>
            </div>

            <div class="slider-container">
                <label for="slider-x2f">x<sub>2f</sub></label>
                <input type="range" id="slider-x2f" min="1" max="10" step="0.1" value="4.0">
                <span id="value-x2f">4.0</span>
            </div>

            <div class="slider-container">
                <label for="slider-umax">μ<sub>max</sub></label>
                <input type="range" id="slider-umax" min="0.1" max="2" step="0.01" value="0.6">
                <span id="value-umax">0.60</span>
            </div>

            <div class="slider-container">
                <label for="slider-km">k<sub>m</sub></label>
                <input type="range" id="slider-km" min="0.01" max="1" step="0.01" value="0.12">
                <span id="value-km">0.12</span>
            </div>

            <div class="slider-container" id="k1-container" style="display:none">
                <label for="slider-k1">k<sub>1</sub></label>
                <input type="range" id="slider-k1" min="0.1" max="2" step="0.0001" value="0.4545">
                <span id="value-k1">0.4545</span>
            </div>

            <div class="slider-container">
                <label for="slider-ds">D<sub>s</sub></label>
                <input type="range" id="slider-ds" min="0.01" max="1.0" step="0.01" value="0.6">
                <span id="value-ds">0.60</span>
            </div>
        </div>

        <div id="visualization-area">
            <div id="plot-container"></div>
        </div>
    </div>

    <script>
        let currentModel = 'monod';
        let currentView = 'bifurcation';

        const params = {
            x2f: 4.0,
            umax: 0.6,
            km: 0.12,
            k1: 0.4545,
            ds: 0.6
        };

        function updateSliderValue(sliderId, value) {
            document.getElementById('value-' + sliderId.split('-')[1]).textContent = 
                Number(value).toFixed(sliderId.includes('k1') ? 4 : 2);
        }

        function calculateD(x2) {
            if (currentModel === 'monod') {
                return params.umax * x2 / (params.km + x2);
            } else {
                return params.umax * x2 / (params.km + x2 + params.k1 * x2 * x2);
            }
        }

        function drawBifurcationDiagram() {
            const x2Values = Array.from({length: 1000}, (_, i) => i * params.x2f / 999);
            const dValues = x2Values.map(x2 => calculateD(x2));

            let traces = [];

            if (currentModel === 'monod') {
                traces.push({
                    x: dValues,
                    y: x2Values,
                    mode: 'lines',
                    line: {color: 'blue'},
                    name: 'Stable'
                });
            } else {
                const x2_crit = Math.sqrt(params.km / params.k1);
                const stableX2 = x2Values.filter(x2 => x2 <= x2_crit);
                const unstableX2 = x2Values.filter(x2 => x2 > x2_crit);

                traces.push(
                    {
                        x: stableX2.map(x2 => calculateD(x2)),
                        y: stableX2,
                        mode: 'lines',
                        line: {color: 'blue'},
                        name: 'Stable'
                    },
                    {
                        x: unstableX2.map(x2 => calculateD(x2)),
                        y: unstableX2,
                        mode: 'lines',
                        line: {color: 'red', dash: 'dash'},
                        name: 'Unstable'
                    }
                );
            }

            const layout = {
                xaxis: {title: 'Dilution Rate', range: [0, 1]},
                yaxis: {title: 'X₂', range: [0, 6]},
                shapes: [{
                    type: 'line',
                    x0: params.ds,
                    x1: params.ds,
                    y0: 0,
                    y1: 6,
                    line: {color: 'gray', width: 1}
                }]
            };

            Plotly.newPlot('plot-container', traces, layout);
        }

        function drawNontrivialStatePlot() {
            const x2Values = Array.from({length: 1000}, (_, i) => i * 7 / 999);
            const muValues = x2Values.map(x2 => calculateD(x2));

            const traces = [{
                x: x2Values,
                y: muValues,
                mode: 'lines',
                line: {color: 'blue'},
                name: 'μ(x₂)'
            }];

            const layout = {
                xaxis: {title: 'Substrate Concentration', range: [0, 7]},
                yaxis: {title: 'Specific Growth Rate Coefficient', range: [0, 1]},
                shapes: [{
                    type: 'line',
                    x0: 0,
                    x1: 7,
                    y0: params.ds,
                    y1: params.ds,
                    line: {color: 'gray', width: 1}
                }]
            };

            Plotly.newPlot('plot-container', traces, layout);
        }

        function updatePlot() {
            if (currentView === 'bifurcation') {
                drawBifurcationDiagram();
            } else {
                drawNontrivialStatePlot();
            }
        }

        // Event Listeners
        document.querySelectorAll('.button-group button').forEach(button => {
            button.addEventListener('click', function() {
                const group = this.parentElement;
                group.querySelector('.active').classList.remove('active');
                this.classList.add('active');

                if (this.id === 'btn-monod' || this.id === 'btn-si') {
                    currentModel = this.id === 'btn-monod' ? 'monod' : 'si';
                    document.getElementById('k1-container').style.display = 
                        currentModel === 'si' ? 'block' : 'none';
                } else {
                    currentView = this.id === 'btn-bifurcation' ? 'bifurcation' : 'nontrivial';
                }

                updatePlot();
            });
        });

        document.querySelectorAll('input[type="range"]').forEach(slider => {
            slider.addEventListener('input', function() {
                const param = this.id.split('-')[1];
                params[param] = parseFloat(this.value);
                updateSliderValue(this.id, this.value);
                updatePlot();
            });
        });

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
