            document.getElementById('value-latitude').textContent = parseFloat(latitude).toFixed(4);
            document.getElementById('value-time').textContent = parseFloat(time).toFixed(2);

            // Update 3D scene
            earth.rotation.y = (time / 24) * 2 * Math.PI;

            // Calculate solar declination
            const declination = 23.5 * Math.cos(2 * Math.PI * season);
            const decRad = declination * Math.PI / 180;

            // Update sun position
            sunLight.position.set(0, Math.sin(decRad), -Math.cos(decRad));

            // Render scene
            renderer.render(scene, camera);
        }

        // Add event listeners
        document.getElementById('slider-season').addEventListener('input', update);
        document.getElementById('slider-latitude').addEventListener('input', update);
        document.getElementById('slider-time').addEventListener('input', update);

        // Initial update
        update();

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }
        animate();

        // Handle window resize
        window.addEventListener('resize', () => {
            const width = globeContainer.clientWidth;
            const height = globeContainer.clientHeight;
            renderer.setSize(width, height);
            camera.aspect = width / height;
            camera.updateProjectionMatrix();
        });
    </script>
