    </style>
</head>
<body>
    <div id="demo-container">
        <div id="control-panel">
            <span>area:</span>
            <input type="range" id="slider-area" min="1" max="101" step="1" value="13">
            <input type="number" id="input-area" min="1" max="101" step="1" value="13">
        </div>
        <div id="canvas-container"></div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        const congruentData = {
            5: { a: {n: 3, d: 2}, b: {n: 20, d: 3}, c: {n: 41, d: 6} },
            6: { a: {n: 3, d: 1}, b: {n: 4, d: 1}, c: {n: 5, d: 1} },
            7: { a: {n: 35, d: 12}, b: {n: 24, d: 5}, c: {n: 337, d: 60} },
            13: { a: {n: 780, d: 323}, b: {n: 323, d: 30}, c: {n: 106921, d: 9690} },
            79: { a: {n: 233126551, d: 167973000}, b: {n: 335946000, d: 2950969}, c: {n: 56434050774922081, d: 495683115837000} }
        };

        let canvas;
        const canvasWidth = 600;
        const canvasHeight = 400;

        function setup() {
            canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('canvas-container');

            const slider = document.getElementById('slider-area');
            const input = document.getElementById('input-area');

            slider.addEventListener('input', function() {
                input.value = this.value;
                drawTriangle(parseInt(this.value));
            });

            input.addEventListener('input', function() {
                slider.value = this.value;
                drawTriangle(parseInt(this.value));
            });

            drawTriangle(13);
        }

        function formatNumber(num) {
            return num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ");
        }

        function drawFraction(fractionObj, x, y, rotation, fontSize) {
            push();
            translate(x, y);
            rotate(rotation);

            textSize(fontSize);
            textAlign(CENTER, CENTER);
            fill('#8B5A2B');

            const num = formatNumber(fractionObj.n);
            const den = formatNumber(fractionObj.d);

            const numWidth = textWidth(num);
            const denWidth = textWidth(den);
            const lineWidth = max(numWidth, denWidth);

            text(num, 0, -fontSize/2);
            text(den, 0, fontSize/2);

            stroke('#8B5A2B');
            line(-lineWidth/2, 0, lineWidth/2, 0);

            pop();
        }

        function drawTriangle(n) {
            background(255);

            if (!congruentData[n]) {
                textAlign(CENTER, CENTER);
                textSize(20);
                fill('#FF0000');
                noStroke();
                text(`${n} is not a congruent number`, width/2, height/2);
                return;
            }

            const data = congruentData[n];
            const a = data.a.n / data.a.d;
            const b = data.b.n / data.b.d;

            const padding = 80;
            const scale = min((width-padding)/a, (height-padding)/b) * 0.8;

            const startX = width/2 - (a*scale)/2;
            const startY = height/2 + (b*scale)/2;

            push();
            fill('#FFF2E5');
            stroke(0);
            strokeWeight(2);

            beginShape();
            vertex(startX, startY);
            vertex(startX + a*scale, startY);
            vertex(startX, startY - b*scale);
            endShape(CLOSE);
            pop();

            // Draw fractions
            drawFraction(data.a, startX + a*scale/2, startY + 30, 0, 16);
            drawFraction(data.b, startX - 30, startY - b*scale/2, 0, 16);

            const angle = atan2(-b*scale, a*scale);
            const hypX = startX + a*scale/2;
            const hypY = startY - b*scale/2;
            drawFraction(data.c, hypX, hypY, angle, 16);
        }
    </script>
</body>
</html>
