        // Initialize Three.js scenes
        function initScenes() {
            // Primal plot setup
            const primalContainer = document.getElementById('plot-primal');
            const primalScene = new THREE.Scene();
            const primalCamera = new THREE.PerspectiveCamera(75, primalContainer.clientWidth / primalContainer.clientHeight, 0.1, 1000);
            const primalRenderer = new THREE.WebGLRenderer();
            primalRenderer.setSize(primalContainer.clientWidth, primalContainer.clientHeight);
            primalContainer.appendChild(primalRenderer.domElement);

            // Dual plot setup
            const dualContainer = document.getElementById('plot-dual');
            const dualScene = new THREE.Scene();
            const dualCamera = new THREE.PerspectiveCamera(75, dualContainer.clientWidth / dualContainer.clientHeight, 0.1, 1000);
            const dualRenderer = new THREE.WebGLRenderer();
            dualRenderer.setSize(dualContainer.clientWidth, dualContainer.clientHeight);
            dualContainer.appendChild(dualRenderer.domElement);

            // Initial camera positions
            primalCamera.position.set(5, 5, 5);
            dualCamera.position.set(5, 5, 5);

            return {
                primal: { scene: primalScene, camera: primalCamera, renderer: primalRenderer },
                dual: { scene: dualScene, camera: dualCamera, renderer: dualRenderer }
            };
        }

        // Update function
        function update() {
            const alpha = parseFloat(document.getElementById('slider-alpha').value);
            const beta = parseFloat(document.getElementById('slider-beta').value);
            const w = parseFloat(document.getElementById('slider-w').value);
            const r = parseFloat(document.getElementById('slider-r').value);
            const Q = parseFloat(document.getElementById('slider-Q').value);
            const C = parseFloat(document.getElementById('slider-C').value);

            // Update labels
            document.getElementById('label-alpha').textContent = alpha.toFixed(2);
            document.getElementById('label-beta').textContent = beta.toFixed(2);
            document.getElementById('label-w').textContent = w.toFixed(2);
            document.getElementById('label-r').textContent = r.toFixed(2);
            document.getElementById('label-Q').textContent = Q.toFixed(1);
            document.getElementById('label-C').textContent = C.toFixed(1);

            // Solve primal problem
            const k = (w * beta) / (r * alpha);
            const x_p = Math.pow(Q / Math.pow(k, beta), 1 / (alpha + beta));
            const y_p = x_p * k;
            const C_out = w * x_p + r * y_p;

            // Solve dual problem
            const x_d = (C * alpha) / (w * (alpha + beta));
            const y_d = (C * beta) / (r * (alpha + beta));
            const Q_out = Math.pow(x_d, alpha) * Math.pow(y_d, beta);

            // Update output displays
            document.getElementById('output-primal').textContent = 
                `x = ${x_p.toFixed(2)}, y = ${y_p.toFixed(2)}, C = ${C_out.toFixed(2)}`;
            document.getElementById('output-dual').textContent = 
                `x = ${x_d.toFixed(2)}, y = ${y_d.toFixed(2)}, Q = ${Q_out.toFixed(2)}`;

            // Update visualizations would go here
            // This would involve updating the Three.js geometries and materials
        }

        // Initialize everything
        const scenes = initScenes();

        // Add event listeners
        document.querySelectorAll('input[type="range"]').forEach(slider => {
            slider.addEventListener('input', update);
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            document.getElementById('slider-alpha').value = 0.5;
