        <div id="visualization-area"></div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script>
        let flashResults = null;
        const components = ['n-butane', 'n-pentane', 'n-heptane', 'n-octane'];
        const antoineConstants = [
            {A: 6.80165, B: 1005.157, C: -27.159},
            {A: 6.86177, B: 1184.249, C: -35.223},
            {A: 6.90237, B: 1358.59, C: -51.303},
            {A: 6.92383, B: 1431.149, C: -59.501}
        ];
        const Cp_L = [132, 161, 225, 255];
        const dH_vap = [22400, 25800, 31800, 34400];
        const z_i = [0.25, 0.25, 0.25, 0.25];
        const F = 10; // kmol/hr
        const T_F = 323.2; // K
        const P_F = 1000; // kPa

        function calculateK(T, P_flash) {
            return antoineConstants.map(({A, B, C}) => 
                Math.pow(10, A - B/(T + C)) / P_flash);
        }

        function solveRR(K, z_i, V) {
            return z_i.reduce((sum, z, i) => 
                sum + z * (K[i] - 1) / (1 + V * (K[i] - 1)), 0);
        }

        function calculateFlash(P_flash, Q_hr) {
            let T_min = 273, T_max = 473;
            let T = (T_min + T_max) / 2;

            for(let iter = 0; iter < 50; iter++) {
                const K = calculateK(T, P_flash);
                let V = 0.5;

                // Solve RR equation for V
                for(let i = 0; i < 20; i++) {
                    const f = solveRR(K, z_i, V);
                    if(Math.abs(f) < 1e-6) break;
                    V = Math.max(0.001, Math.min(0.999, V - f * 0.1));
                }

                // Calculate x_i and y_i
                const x = z_i.map((z, i) => z / (1 + V * (K[i] - 1)));
                const y = x.map((x, i) => K[i] * x);

                // Energy balance
                const Q_sensible = z_i.reduce((sum, z, i) => 
                    sum + z * Cp_L[i] * (T_F - T), 0);
                const Q_latent = V * y.reduce((sum, y, i) => 
                    sum + y * dH_vap[i], 0);
                const Q_balance = Q_hr/F + Q_sensible - Q_latent;

                if(Math.abs(Q_balance) < 1) {
                    return {T_flash: T, V_frac: V, x, y};
                }

                if(Q_balance > 0) {
                    T_min = T;
                } else {
                    T_max = T;
                }
                T = (T_min + T_max) / 2;
            }
            return null;
        }

        function setup() {
            const canvas = createCanvas(800, 500);
            canvas.parent('visualization-area');
            textAlign(LEFT, CENTER);
            strokeWeight(2);

            // Initial calculation
            const pressure = document.getElementById('slider-pressure').value;
            const heat = document.getElementById('slider-heat').value;
            flashResults = calculateFlash(pressure, heat);
        }

        function draw() {
            background('#F0F0F0');

            // Draw vessel
            fill('#E6E6FA');
            rect(350, 200, 100, 100);
            fill(0);
            textAlign(CENTER, CENTER);
            text('Flash', 400, 250);

            // Draw streams
            stroke('#9ACD32'); // Feed
            line(100, 250, 350, 250);
            drawArrow(350, 250);

            stroke('#9370DB'); // Vapor
            line(400, 200, 400, 100);
            line(400, 100, 550, 100);
            drawArrow(550, 100);

            stroke('#FFA500'); // Liquid
            line(400, 300, 400, 400);
            line(400, 400, 550, 400);
            drawArrow(550, 400);

            // Draw heat input
            stroke('#FF0000');
            drawDashedLine(250, 270, 350, 270);
            drawArrow(350, 270);

            // Draw labels
            noStroke();
            fill(0);
            textAlign(LEFT, CENTER);

            if(flashResults) {
                text(`Feed: T = ${T_F.toFixed(1)} K, P = ${P_F} kPa`, 50, 200);
                text(`Flash T = ${flashResults.T_flash.toFixed(1)} K`, 570, 250);
                text(`Vapor fraction = ${flashResults.V_frac.toFixed(3)}`, 570, 270);

                text('Vapor mole fractions:', 570, 80);
                flashResults.y.forEach((y, i) => {
                    text(`${components[i]}: ${y.toFixed(3)}`, 590, 100 + i * 20);
                });

                text('Liquid mole fractions:', 570, 380);
                flashResults.x.forEach((x, i) => {
                    text(`${components[i]}: ${x.toFixed(3)}`, 590, 400 + i * 20);
                });
            }

            const heat = document.getElementById('slider-heat').value;
            text(`Q = ${heat} kJ/hr`, 250, 290);
        }

        function drawArrow(x, y) {
            push();
            translate(x, y);
            triangle(-10, -5, 0, 0, -10, 5);
            pop();
        }

        function drawDashedLine(x1, y1, x2, y2) {
            const segments = 10;
            for(let i = 0; i < segments; i++) {
                if(i % 2 === 0) {
                    const x = lerp(x1, x2, i/segments);
                    const y = lerp(y1, y2, i/segments);
                    const nextX = lerp(x1, x2, (i+1)/segments);
                    const nextY = lerp(y1, y2, (i+1)/segments);
                    line(x, y, nextX, nextY);
                }
            }
        }

        // Event listeners
        document.getElementById('slider-pressure').addEventListener('input', function(e) {
            document.getElementById('pressure-value').textContent = `${e.target.value} kPa`;
            const heat = document.getElementById('slider-heat').value;
            flashResults = calculateFlash(parseFloat(e.target.value), parseFloat(heat));
        });

        document.getElementById('slider-heat').addEventListener('input', function(e) {
            document.getElementById('heat-value').textContent = `${e.target.value} kJ/hr`;
            const pressure = document.getElementById('slider-pressure').value;
            flashResults = calculateFlash(parseFloat(pressure), parseFloat(e.target.value));
        });
    </script>
</body>
</html>
