<!DOCTYPE html>
<html>
<head>
    <title>Pendulum Simulation</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 20px;
        }
        .main-container {
            display: flex;
            flex-direction: column;
            align-items: center;
            gap: 20px;
        }
        .control-panel {
            display: flex;
            flex-direction: column;
            gap: 10px;
            padding: 15px;
            background: #f8f8f8;
            border-radius: 8px;
            width: 500px;
        }
        .slider-container {
            display: flex;
            align-items: center;
            gap: 10px;
        }
        .slider-container label {
            width: 120px;
        }
        .slider-container input {
            flex: 1;
        }
        .slider-container span {
            width: 60px;
            text-align: right;
        }
        .button-group {
            display: flex;
            justify-content: center;
            gap: 10px;
        }
        button {
            padding: 5px 15px;
            font-size: 16px;
        }
    </style>
</head>
<body>
    <div class="main-container">
        <div class="control-panel">
            <div class="slider-container">
                <label>θ<sub>0</sub></label>
                <input type="range" id="slider-theta0" min="-3.14159" max="3.14159" step="0.01" value="0.5236">
                <span id="value-theta0">π/6</span>
            </div>
            <div class="slider-container">
                <label>v<sub>0</sub></label>
                <input type="range" id="slider-v0" min="-5" max="5" step="0.1" value="0">
                <span id="value-v0">0</span>
            </div>
            <div class="slider-container">
                <label>connector length</label>
                <input type="range" id="slider-length" min="0.5" max="4" step="0.1" value="2">
                <span id="value-length">2</span>
            </div>
            <div class="button-group">
                <button id="btn-play-pause">▶</button>
                <button id="btn-reset">|<</button>
            </div>
        </div>
        <div id="visualization"></div>
    </div>

    <script>
        let isPlaying = false;
        let theta, theta_dot, x, x_dot, t;
        let l = 2; // connector length
        const m = 1, M = 1, g = 9.8;
        const scale = 50; // pixels per meter
        const dt = 0.016; // time step (roughly 60 FPS)

        function formatAngle(rad) {
            const pi = Math.PI;
            const fractions = [
                {val: pi/2, str: "π/2"}, {val: pi/3, str: "π/3"}, 
                {val: pi/4, str: "π/4"}, {val: pi/6, str: "π/6"},
                {val: -pi/2, str: "-π/2"}, {val: -pi/3, str: "-π/3"},
                {val: -pi/4, str: "-π/4"}, {val: -pi/6, str: "-π/6"}
            ];

            for(let frac of fractions) {
                if(Math.abs(rad - frac.val) < 0.01) return frac.str;
            }
            return rad.toFixed(2);
        }

        function resetSystem() {
            t = 0;
            theta = parseFloat(document.getElementById('slider-theta0').value);
            theta_dot = 0;
            x = 0;
            x_dot = parseFloat(document.getElementById('slider-v0').value);
            l = parseFloat(document.getElementById('slider-length').value);
        }

        function setup() {
            const canvas = createCanvas(500, 500);
            canvas.parent('visualization');
            resetSystem();

            // Setup event listeners
            document.getElementById('slider-theta0').addEventListener('input', function(e) {
                document.getElementById('value-theta0').textContent = formatAngle(parseFloat(e.target.value));
                isPlaying = false;
                document.getElementById('btn-play-pause').textContent = '▶';
                resetSystem();
            });

            document.getElementById('slider-v0').addEventListener('input', function(e) {
                document.getElementById('value-v0').textContent = e.target.value;
                isPlaying = false;
                document.getElementById('btn-play-pause').textContent = '▶';
                resetSystem();
            });

            document.getElementById('slider-length').addEventListener('input', function(e) {
                document.getElementById('value-length').textContent = e.target.value;
                isPlaying = false;
                document.getElementById('btn-play-pause').textContent = '▶';
                resetSystem();
            });

            document.getElementById('btn-play-pause').addEventListener('click', function() {
                isPlaying = !isPlaying;
                this.textContent = isPlaying ? '||' : '▶';
            });

            document.getElementById('btn-reset').addEventListener('click', function() {
                isPlaying = false;
                document.getElementById('btn-play-pause').textContent = '▶';
                resetSystem();
            });
        }

        function draw() {
            if(isPlaying) {
                // Physics update
                const theta_ddot = (-m*l*sin(theta)*cos(theta)*theta_dot*theta_dot - g*(M+m)*sin(theta)) / 
                                 (l*(M + m*sin(theta)*sin(theta)));
                theta_dot += theta_ddot * dt;
                theta += theta_dot * dt;
                x_dot = parseFloat(document.getElementById('slider-v0').value) - 
                        (m*l*cos(theta)*theta_dot) / (M+m);
                x += x_dot * dt;
                t += dt;
            }

            // Render
            background(240);

            // Calculate screen positions
            const px = width/2 + x * scale;
            const py = height/2;
            const bx = px + l * scale * sin(theta);
            const by = py + l * scale * cos(theta);

            // Draw connector
            stroke(0);
            strokeWeight(2);
            line(px, py, bx, by);

            // Draw pivot
            fill(255, 0, 0);
