<!DOCTYPE html>
<html>
<head>
    <title>Grover's Algorithm Visualization</title>
    <style>
        body {
            display: flex;
            justify-content: center;
            align-items: center;
            min-height: 100vh;
            margin: 0;
            font-family: Arial, sans-serif;
        }
        #app-container {
            border: 1px solid #ccc;
            box-shadow: 0 0 10px rgba(0,0,0,0.1);
            padding: 20px;
            width: 800px;
        }
        #control-panel {
            display: flex;
            justify-content: space-between;
            margin-bottom: 20px;
        }
        .control-group {
            display: flex;
            flex-direction: column;
            width: 45%;
        }
        .value-display {
            text-align: center;
            margin-top: 5px;
        }
        #probability-display {
            text-align: center;
            margin-top: 20px;
            font-weight: bold;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-db-size">database size</label>
                <input type="range" id="slider-db-size" min="8" max="100" value="10" step="1">
                <div class="value-display"><span id="display-db-size">10</span></div>
            </div>
            <div class="control-group">
                <label for="slider-iterations">number of iterations</label>
                <input type="range" id="slider-iterations" min="0" max="20" value="2" step="1">
                <div class="value-display"><span id="display-iterations">2</span></div>
            </div>
        </div>
        <div id="canvas-container"></div>
        <div id="probability-display">99.9% success probability</div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let canvas;
        const TARGET_INDEX = 6;

        function setup() {
            canvas = createCanvas(760, 600);
            canvas.parent('canvas-container');
            noLoop();
        }

        function draw() {
            background(255);
            const N = parseInt(document.getElementById('slider-db-size').value);
            const k = parseInt(document.getElementById('slider-iterations').value);

            // Initialize states
            let states = new Array(N).fill(1/Math.sqrt(N));

            // Draw four stages
            drawStage(states, 0, "Original Amplitudes", true);

            // After first inversion
            let afterInversion = [...states];
            afterInversion[TARGET_INDEX] *= -1;
            drawStage(afterInversion, 1, "After First Inversion");

            // After first diffusion
            let afterDiffusion = diffuse(afterInversion);
            drawStage(afterDiffusion, 2, "After First Diffusion");

            // After k iterations
            let finalState = [...states];
            for(let i = 0; i < k; i++) {
                finalState[TARGET_INDEX] *= -1;
                finalState = diffuse(finalState);
            }
            drawStage(finalState, 3, "After " + k + " Iterations");

            // Update probability
            const probability = Math.pow(Math.sin((2 * k + 1) * Math.asin(1 / Math.sqrt(N))), 2);
            document.getElementById('probability-display').textContent = 
                (probability * 100).toFixed(1) + "% success probability";
        }

        function drawStage(states, stageIndex, title, drawParens = false) {
            const sectionHeight = height/4;
            const yBase = stageIndex * sectionHeight + sectionHeight/2;
            const xMargin = 40;
            const barWidth = Math.min(20, (width-2*xMargin)/states.length);

            // Draw baseline
            stroke(0);
            line(xMargin, yBase, width-xMargin, yBase);

            // Draw title
            noStroke();
            fill(0);
            textAlign(CENTER);
            text(title, width/2, yBase - sectionHeight/2 + 20);

            // Draw amplitudes
            noStroke();
            const scale = sectionHeight/3;
            for(let i = 0; i < states.length; i++) {
                const x = map(i, 0, states.length-1, xMargin, width-xMargin);
                const h = states[i] * scale;
                fill(i === TARGET_INDEX ? '#FFA500' : '#808080');
                rect(x - barWidth/2, yBase - Math.max(0,h), barWidth, Math.abs(h));
            }

            // Draw parentheses if needed
            if(drawParens) {
                noFill();
                stroke(0);
                arc(xMargin, yBase, 40, sectionHeight/2, -HALF_PI, HALF_PI);
                arc(width-xMargin, yBase, 40, sectionHeight/2, HALF_PI, -HALF_PI);
            }
        }

        function diffuse(states) {
            const mean = states.reduce((a,b) => a + b, 0) / states.length;
            return states.map(a => 2 * mean - a);
        }

        // Event listeners
        document.getElementById('slider-db-size').addEventListener('input', function(e) {
            document.getElementById('display-db-size').textContent = e.target.value;
            redraw();
        });

        document.getElementById('slider-iterations').addEventListener('input', function(e) {
            document.getElementById('display-iterations').textContent = e.target.value;
            redraw();
        });
    </script>
</body>
</html>
