    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <style>
        .container {
            max-width: 900px;
            margin: 0 auto;
            padding: 20px;
            display: flex;
            flex-direction: column;
            gap: 20px;
        }
        .control-panel {
            display: flex;
            gap: 40px;
        }
        .parameter-container {
            flex: 1;
        }
        .slider-container {
            display: flex;
            align-items: center;
            gap: 10px;
        }
        input[type="range"] {
            flex: 1;
        }
        .plot-title {
            text-align: center;
            font-size: 1.2em;
            margin: 10px 0;
        }
        .density { color: blue; }
        .distribution { color: #FF4500; }
        .statistics {
            margin-top: 20px;
        }
    </style>
</head>
<body>
    <div class="container">
        <div class="control-panel">
            <div class="parameter-container">
                <div class="slider-container">
                    <label>parameter α:</label>
                    <input type="range" id="slider-alpha" min="0.1" max="5.0" step="0.005" value="1.825">
                    <span id="display-alpha">1.825</span>
                </div>
            </div>
            <div class="parameter-container">
                <div class="slider-container">
                    <label>parameter β:</label>
                    <input type="range" id="slider-beta" min="0.1" max="5.0" step="0.005" value="1.945">
                    <span id="display-beta">1.945</span>
                </div>
            </div>
        </div>

        <div>
            <div class="plot-title">
                <span class="density">density function</span>
                <span>and</span>
                <span class="distribution">distribution function</span>
            </div>
            <div id="plot-container"></div>
        </div>

        <div class="statistics">
            <p>Mean: <span id="mean-value">3.550</span></p>
            <p>Variance: <span id="variance-value">6.904</span></p>
            <p>Moment generating function: <span id="mgf-formula"></span></p>
        </div>
    </div>

    <script>
        // Lanczos approximation for Gamma function
        function gamma(z) {
            const p = [676.5203681218851, -1259.1392167224028, 771.32342877765313,
                      -176.61502916214059, 12.507343278686905, -0.13857109526572012,
                      9.9843695780195716e-6, 1.5056327351493116e-7];
            if(z < 0.5) {
                return Math.PI / (Math.sin(Math.PI * z) * gamma(1 - z));
            }
            z -= 1;
            let x = 0.99999999999980993;
            for(let i = 0; i < p.length; i++) {
                x += p[i] / (z + i + 1);
            }
            const t = z + p.length - 0.5;
            return Math.sqrt(2 * Math.PI) * Math.pow(t, z + 0.5) * Math.exp(-t) * x;
        }

        // Gamma PDF
        function gammaPDF(x, alpha, beta) {
            if(x <= 0) return 0;
            return (Math.pow(x, alpha-1) * Math.exp(-x/beta)) / (Math.pow(beta, alpha) * gamma(alpha));
        }

        // Generate x values for plotting
        function generateXValues(start, end, step) {
            const values = [];
            for(let x = start; x <= end; x += step) {
                values.push(x);
            }
            return values;
        }

        // Update plot and calculations
        function updateVisualization() {
            const alpha = parseFloat(document.getElementById('slider-alpha').value);
            const beta = parseFloat(document.getElementById('slider-beta').value);

            // Update displays
            document.getElementById('display-alpha').textContent = alpha.toFixed(3);
            document.getElementById('display-beta').textContent = beta.toFixed(3);

            // Calculate statistics
            const mean = alpha * beta;
            const variance = alpha * beta * beta;
            document.getElementById('mean-value').textContent = mean.toFixed(3);
            document.getElementById('variance-value').textContent = variance.toFixed(3);

            // Update MGF formula
            const mgfFormula = `$$ (1 - \\beta t)^{-\\alpha} = \\frac{1}{(1 - ${beta.toFixed(3)} t)^{${alpha.toFixed(3)}}} $$`;
            document.getElementById('mgf-formula').innerHTML = mgfFormula;
            MathJax.typeset();

            // Generate plot data
            const xValues = generateXValues(0, 25, 0.1);
            const yPDF = xValues.map(x => gammaPDF(x, alpha, beta));

            // Calculate CDF through numerical integration
            const yCDF = yPDF.map((_, i) => {
                return yPDF.slice(0, i+1).reduce((sum, y, j, arr) => {
                    if(j === 0 || j === arr.length-1) return sum + y * 0.1 / 2;
                    return sum + y * 0.1;
                }, 0);
            });

            // Update plot
            const data = [
                {
                    x: xValues,
                    y: yPDF,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'blue', width: 2.5 }
                },
                {
                    x: xValues,
                    y: yCDF,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'orange', width: 2.5 }
                }
            ];

            const layout = {
                xaxis: { range: [0, 25], title: '' },
                yaxis: { range: [0, 1.1], title: '' },
                showlegend: false,
                margin: { l: 40, r: 40, t: 40, b: 40 },
                shapes: [{
                    type: 'line',
                    x0: mean,
                    x1: mean,
                    y0: 0,
                    y1: gammaPDF(mean, alpha, beta),
                    line: { color: 'red', width: 3 }
                }]
            };

            Plotly.react('plot-container', data, layout);
        }

        // Set up event listeners
        document.getElementById('slider-alpha').addEventListener('input', updateVisualization);
        document.getElementById('slider-beta').addEventListener('input', updateVisualization);

        // Initial plot
        document.addEventListener('DOMContentLoaded', updateVisualization);
    </script>
</body>
</html>
