<!DOCTYPE html>
<html>
<head>
    <title>Ternary Plot Visualization</title>
    <style>
        #canvas-container {
            width: 650px;
            margin: 20px auto;
        }
        canvas {
            border: 1px solid #ccc;
        }
    </style>
</head>
<body>
    <div id="canvas-container">
        <canvas id="visualization-canvas"></canvas>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js"></script>
    <script>
        let canvas;
        let locator = { x: 0.2, y: 0.22 };
        let isDragging = false;
        const margin = 60;
        const plotSize = 530;

        // Azeotrope points
        const azeotropes = {
            A1: {x: 0.655, y: 0},
            A2: {x: 0, y: 0.795},
            A3: {x: 0.605, y: 0.345},
            A4: {x: 0.26, y: 0.335}
        };

        function setup() {
            canvas = createCanvas(650, 650);
            canvas.parent('canvas-container');
        }

        function dataToPixel(x, y) {
            return {
                x: margin + x * plotSize,
                y: height - (margin + y * plotSize)
            };
        }

        function pixelToData(px, py) {
            return {
                x: (px - margin) / plotSize,
                y: (height - margin - py) / plotSize
            };
        }

        function getVector(x, y) {
            const A3 = {x: 0.605, y: 0.345};
            const M = {x: 0, y: 0};

            let v_repel_x = x - A3.x;
            let v_repel_y = y - A3.y;
            let v_attract_x = M.x - x;
            let v_attract_y = M.y - y;

            let dist_repel_sq = v_repel_x * v_repel_x + v_repel_y * v_repel_y + 0.01;
            let dist_attract_sq = v_attract_x * v_attract_x + v_attract_y * v_attract_y + 0.01;

            let dx = (v_repel_x / dist_repel_sq) * 0.05 + (v_attract_x / dist_attract_sq) * 0.03;
            let dy = (v_repel_y / dist_repel_sq) * 0.05 + (v_attract_y / dist_attract_sq) * 0.03;

            dx += -0.1 * y;
            dy += 0.1 * x;

            return { dx, dy };
        }

        function calculateDistillationLine(startPoint) {
            let points = [];
            let current = {...startPoint};

            // Forward integration
            for(let i = 0; i < 250; i++) {
                if(current.x < 0 || current.y < 0 || current.x + current.y > 1) break;
                points.push({...current});
                let v = getVector(current.x, current.y);
                current.x += v.dx * 0.005;
                current.y += v.dy * 0.005;
            }

            // Reset to start point for backward integration
            current = {...startPoint};
            let backwardPoints = [];

            // Backward integration
            for(let i = 0; i < 250; i++) {
                if(current.x < 0 || current.y < 0 || current.x + current.y > 1) break;
                backwardPoints.unshift({...current});
                let v = getVector(current.x, current.y);
                current.x -= v.dx * 0.005;
                current.y -= v.dy * 0.005;
            }

            return [...backwardPoints, ...points];
        }

        function draw() {
            background(255);

            // Draw axes
            stroke(0);
            strokeWeight(1);

            // X axis
            line(margin, height-margin, width-margin, height-margin);
            // Y axis
            line(margin, margin, margin, height-margin);

            // Draw ticks and labels
            textAlign(RIGHT, CENTER);
            for(let i = 0; i <= 1; i += 0.2) {
                let px = dataToPixel(i, 0);
                line(px.x, px.y, px.x, px.y+5);
                text(i.toFixed(1), px.x, px.y+15);

                let py = dataToPixel(0, i);
                line(py.x-5, py.y, py.x, py.y);
                text(i.toFixed(1), py.x-10, py.y);
            }

            // Draw hypotenuse
            let p1 = dataToPixel(1, 0);
            let p2 = dataToPixel(0, 1);
            line(p1.x, p1.y, p2.x, p2.y);

            // Draw component labels
            fill(255, 0, 0);
            noStroke();
            textAlign(LEFT);
            text("methanol", margin+5, height-margin-15);
            text("chloroform", width-margin-70, height-margin-15);
            text("acetone", margin+5, margin+15);

            // Draw azeotrope points
            fill(153, 0, 0);
            stroke(0);
            Object.entries(azeotropes).forEach(([key, point]) => {
                let p = dataToPixel(point.x, point.y);
                circle(p.x, p.y, 12);
                fill(0);
                noStroke();
                text(key, p.x+10, p.y);
                fill(153, 0, 0);
                stroke(0);
            });

            // Draw distillation line
            let distLine = calculateDistillationLine(locator);
            stroke(0, 0, 255);
            strokeWeight(2);
            noFill();
            beginShape();
            distLine.forEach(p => {
                let pixel = dataToPixel(p.x, p.y);
                curveVertex(pixel.x, pixel.y);
            });
            endShape();

            // Draw locator
            let loc = dataToPixel(locator.x, locator.y);
            stroke(0);
            strokeWeight(2);
            fill(255);
            circle(loc.x, loc.y, 16);
        }

        function mousePressed() {
            let loc = dataToPixel(locator.x, locator.y);
            let d = dist(mouseX, mouseY, loc.x, loc.y);
            if(d < 15) {
                isDragging = true;
            }
        }

        function mouseDragged() {
            if(isDragging) {
                let data = pixelToData(mouseX, mouseY);
                // Constrain to valid region
                data.x = constrain(data.x, 0, 1);
                data.y = constrain(data.y, 0, 1);
                if(data.x + data.y <= 1) {
                    locator.x = data.x;
                    locator.y = data.y;
                }
            }
        }

        function mouseReleased() {
            isDragging = false;
        }
    </script>
</body>
</html>
