    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <h3>lengths of semi-axes</h3>
            <div id="slider-group-a" class="slider-group">
                <label for="slider-a">a</label>
                <input type="range" id="slider-a" min="0.1" max="5" step="0.1" value="1.5">
                <input type="number" id="input-a" min="0.1" max="5" step="0.1" value="1.5">
            </div>
            <div id="slider-group-b" class="slider-group">
                <label for="slider-b">b</label>
                <input type="range" id="slider-b" min="0.1" max="5" step="0.1" value="1.5">
                <input type="number" id="input-b" min="0.1" max="5" step="0.1" value="1.5">
            </div>
            <div id="slider-group-c" class="slider-group">
                <label for="slider-c">c</label>
                <input type="range" id="slider-c" min="0.1" max="5" step="0.1" value="1.0">
                <input type="number" id="input-c" min="0.1" max="5" step="0.1" value="1.0">
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>

    <script>
        let scene, camera, renderer, controls;
        let paraboloidMesh, wireframeMesh;

        function init() {
            // Scene setup
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0x000000);

            // Camera setup
            camera = new THREE.PerspectiveCamera(45, window.innerWidth / window.innerHeight, 0.1, 1000);
            camera.position.set(5, 5, 5);
            camera.lookAt(0, 0, 0);

            // Renderer setup
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(window.innerWidth, window.innerHeight);
            document.getElementById('canvas-container').appendChild(renderer.domElement);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);

            // Lighting
            const ambientLight = new THREE.AmbientLight(0x404040);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
            directionalLight.position.set(5, 5, 5);
            scene.add(directionalLight);

            // Add coordinate axes
            addAxes();

            // Create initial paraboloid
            updateParaboloid();

            // Event listeners
            window.addEventListener('resize', onWindowResize, false);
            setupSliderListeners();

            animate();
        }

        function addAxes() {
            const axisLength = 5;
            const axisColor = 0x888888;

            ['x', 'y', 'z'].forEach(axis => {
                const points = [];
                points.push(new THREE.Vector3(
                    axis === 'x' ? -axisLength : 0,
                    axis === 'y' ? -axisLength : 0,
                    axis === 'z' ? -axisLength : 0
                ));
                points.push(new THREE.Vector3(
                    axis === 'x' ? axisLength : 0,
                    axis === 'y' ? axisLength : 0,
                    axis === 'z' ? axisLength : 0
                ));

                const geometry = new THREE.BufferGeometry().setFromPoints(points);
                const material = new THREE.LineBasicMaterial({ color: axisColor });
                const line = new THREE.Line(geometry, material);
                scene.add(line);
            });
        }

        function createParaboloidGeometry(a, b, c) {
            const range = 2.0;
            const segments = 50;

            return new THREE.ParametricBufferGeometry((u, v, target) => {
                const x = a * v * range * Math.cos(u * 2 * Math.PI);
                const y = b * v * range * Math.sin(u * 2 * Math.PI);
                const z = c * (v * range) * (v * range);
                target.set(x, y, z);
            }, segments, segments);
        }

        function updateParaboloid() {
            const a = parseFloat(document.getElementById('slider-a').value);
            const b = parseFloat(document.getElementById('slider-b').value);
            const c = parseFloat(document.getElementById('slider-c').value);

            if (paraboloidMesh) scene.remove(paraboloidMesh);
            if (wireframeMesh) scene.remove(wireframeMesh);

            const geometry = createParaboloidGeometry(a, b, c);

            // Add vertex colors
            const colors = [];
            const positions = geometry.attributes.position.array;
            for (let i = 0; i < positions.length; i += 3) {
                const z = positions[i + 2];
                const normalizedZ = z / (c * 4);
                const color = new THREE.Color().lerpColors(
                    new THREE.Color(0xFFD700),
                    new THREE.Color(0xFF4500),
                    normalizedZ
                );
                colors.push(color.r, color.g, color.b);
            }
            geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));

            // Create solid mesh
            const material = new THREE.MeshPhongMaterial({
                side: THREE.DoubleSide,
                vertexColors: true
            });
            paraboloidMesh = new THREE.Mesh(geometry, material);
            scene.add(paraboloidMesh);

            // Create wireframe mesh
            const wireframeMaterial = new THREE.MeshBasicMaterial({
                color: 0xcccccc,
                wireframe: true
            });
            wireframeMesh = new THREE.Mesh(geometry, wireframeMaterial);
            scene.add(wireframeMesh);
        }

        function setupSliderListeners() {
            ['a', 'b', 'c'].forEach(param => {
                const slider = document.getElementById(`slider-${param}`);
                const input = document.getElementById(`input-${param}`);

                slider.addEventListener('input', () => {
                    input.value = slider.value;
                    updateParaboloid();
                });

                input.addEventListener('input', () => {
                    slider.value = input.value;
                    updateParaboloid();
                });
            });
        }

        function onWindowResize() {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        init();
    </script>
</body>
</html>
