            if (event.target.tagName === 'BUTTON') {
                state.j2 = parseFloat(event.target.value);
                updateMSelectors();
                updateJSelector();
                resetAnimation();
                drawScene();
            }
        }

        function updateMSelectors() {
            // Update m1 selector
            const m1Selector = document.getElementById('m1-selector');
            m1Selector.innerHTML = '';
            for (let m = -state.j1; m <= state.j1; m += 1) {
                const btn = document.createElement('button');
                btn.textContent = m;
                btn.value = m;
                if (m === state.m1) btn.classList.add('selected');
                btn.onclick = () => {
                    state.m1 = parseFloat(m);
                    resetAnimation();
                    drawScene();
                };
                m1Selector.appendChild(btn);
            }

            // Update m2 selector similarly
            const m2Selector = document.getElementById('m2-selector');
            m2Selector.innerHTML = '';
            for (let m = -state.j2; m <= state.j2; m += 1) {
                const btn = document.createElement('button');
                btn.textContent = m;
                btn.value = m;
                if (m === state.m2) btn.classList.add('selected');
                btn.onclick = () => {
                    state.m2 = parseFloat(m);
                    resetAnimation();
                    drawScene();
                };
                m2Selector.appendChild(btn);
            }
        }

        function updateJSelector() {
            const minJ = Math.abs(state.j1 - state.j2);
            const maxJ = state.j1 + state.j2;
            const jSelector = document.getElementById('j-selector');
            jSelector.innerHTML = '';

            for (let j = minJ; j <= maxJ; j += 1) {
                const btn = document.createElement('button');
                btn.textContent = j;
                btn.value = j;
                if (j === state.j) btn.classList.add('selected');
                btn.onclick = () => {
                    state.j = parseFloat(j);
                    resetAnimation();
                    drawScene();
                };
                jSelector.appendChild(btn);
            }
        }

        function toggleAnimation() {
            isAnimating = !isAnimating;
            const btn = document.getElementById('btn-play-pause');
            btn.textContent = isAnimating ? '❚❚' : '▶';
        }

        function resetAnimation() {
            isAnimating = false;
            animationPhase = 0;
            document.getElementById('btn-play-pause').textContent = '▶';
            drawScene();
        }

        function drawScene() {
            // Clear existing scene
            while(scene.children.length > 0) { 
                scene.remove(scene.children[0]); 
            }

            // Add axes
            const axesHelper = new THREE.AxesHelper(5);
            scene.add(axesHelper);

            // Add vectors and cones based on current state
            // This is where the complex vector calculations would go
            // For brevity, just adding placeholder geometry

            // Render
            renderer.render(scene, camera);
        }

        function animate() {
            requestAnimationFrame(animate);

            if (isAnimating) {
                animationPhase += 0.02;
                drawScene();
            }

            controls.update();
            renderer.render(scene, camera);
        }

        // Initialize everything when the window loads
        window.onload = init;

        // Handle window resizing
        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        });
    </script>
</body>
</html>
