            width: 200px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="steps-slider">steps</label>
            <input type="range" id="steps-slider" min="0" max="100" value="61" step="1">
            <span id="steps-value">+ 61</span>
            <label>box</label>
            <button id="btn-none">None</button>
            <button id="btn-box">Box</button>
            <button id="btn-scattered" class="active">Scattered</button>
        </div>
        <div id="viz-container">
            <div id="recursive-container" class="grid-container">
                <h3>Recursive</h3>
                <div id="canvas-recursive"></div>
            </div>
            <div id="iterative-container" class="grid-container">
                <h3>Iterative</h3>
                <div id="canvas-iterative"></div>
            </div>
        </div>
    </div>

    <script>
        const EMPTY = 0;
        const OBSTACLE = 1;
        const FILLED = 2;

        let recursiveHistory = [];
        let iterativeHistory = [];
        let grid = Array(10).fill().map(() => Array(10).fill(EMPTY));
        let recursiveSketch, iterativeSketch;

        function createScatteredPattern() {
            for(let i = 0; i < 10; i++) {
                for(let j = 0; j < 10; j++) {
                    if(Math.random() < 0.25 && !(i === 4 && j === 4)) {
                        grid[i][j] = OBSTACLE;
                    } else {
                        grid[i][j] = EMPTY;
                    }
                }
            }
        }

        function createBoxPattern() {
            grid = grid.map((row, i) => row.map((cell, j) => 
                (i === 0 || i === 9 || j === 0 || j === 9) ? OBSTACLE : EMPTY
            ));
        }

        function floodFillRecursive(x, y, visitedGrid) {
            if(x < 0 || x >= 10 || y < 0 || y >= 10) return;
            if(visitedGrid[y][x] || grid[y][x] === OBSTACLE) return;

            visitedGrid[y][x] = true;
            recursiveHistory.push([x, y]);

            floodFillRecursive(x, y-1, visitedGrid);
            floodFillRecursive(x-1, y, visitedGrid);
            floodFillRecursive(x, y+1, visitedGrid);
            floodFillRecursive(x+1, y, visitedGrid);
        }

        function floodFillIterative() {
            let visitedGrid = Array(10).fill().map(() => Array(10).fill(false));
            let queue = [[4, 4]];

            while(queue.length > 0) {
                let [x, y] = queue.shift();
                if(x < 0 || x >= 10 || y < 0 || y >= 10) continue;
                if(visitedGrid[y][x] || grid[y][x] === OBSTACLE) continue;

                visitedGrid[y][x] = true;
                iterativeHistory.push([x, y]);

                queue.push([x, y-1], [x-1, y], [x, y+1], [x+1, y]);
            }
        }

        const p5Instance = (container, isRecursive) => (p) => {
            p.setup = () => {
                p.createCanvas(201, 201);
            };

            p.draw = () => {
                p.background(255);
                p.stroke(0);
                p.strokeWeight(1);

                const steps = parseInt(document.getElementById('steps-slider').value);
                const history = isRecursive ? recursiveHistory : iterativeHistory;

                // Draw grid and obstacles
                for(let i = 0; i < 10; i++) {
                    for(let j = 0; j < 10; j++) {
                        if(grid[j][i] === OBSTACLE) {
                            p.fill(0);
                            p.rect(i*20, j*20, 20, 20);
                        }
                    }
                }

                // Draw filled cells
                p.fill(0, 0, 255);
                for(let i = 0; i < steps && i < history.length; i++) {
                    const [x, y] = history[i];
                    p.rect(x*20, y*20, 20, 20);
                }

                // Draw grid lines
                for(let i = 0; i <= 10; i++) {
                    p.line(i*20, 0, i*20, 200);
                    p.line(0, i*20, 200, i*20);
                }
            };
        };

        function resetSimulation(pattern) {
            grid = Array(10).fill().map(() => Array(10).fill(EMPTY));
            recursiveHistory = [];
            iterativeHistory = [];

            if(pattern === 'scattered') createScatteredPattern();
            else if(pattern === 'box') createBoxPattern();

            let visitedGrid = Array(10).fill().map(() => Array(10).fill(false));
            floodFillRecursive(4, 4, visitedGrid);
            floodFillIterative();

            const slider = document.getElementById('steps-slider');
            slider.max = Math.max(recursiveHistory.length, iterativeHistory.length);
            slider.value = 0;
            document.getElementById('steps-value').textContent = '+ 0';
        }

        window.onload = () => {
            recursiveSketch = new p5(p5Instance('canvas-recursive', true), 'canvas-recursive');
            iterativeSketch = new p5(p5Instance('canvas-iterative', false), 'canvas-iterative');

            resetSimulation('scattered');

            document.getElementById('steps-slider').addEventListener('input', (e) => {
                document.getElementById('steps-value').textContent = `+ ${e.target.value}`;
            });

            ['none', 'box', 'scattered'].forEach(pattern => {
                document.getElementById(`btn-${pattern}`).addEventListener('click', (e) => {
                    document.querySelectorAll('button').forEach(btn => btn.classList.remove('active'));
                    e.target.classList.add('active');
                    resetSimulation(pattern);
                });
            });

            document.getElementById('steps-slider').value = 61;
            document.getElementById('steps-value').textContent = '+ 61';
        };
    </script>
</body>
</html>
