<!DOCTYPE html>
<html>
<head>
    <title>Bouncing Ball Simulation</title>
    <style>
        body {
            margin: 0;
            padding: 20px;
            background: #333;
            color: white;
            font-family: Arial, sans-serif;
        }
        #control-panel {
            margin-bottom: 20px;
        }
        #control-panel div {
            margin: 10px 0;
            display: flex;
            align-items: center;
        }
        label {
            width: 150px;
            margin-right: 10px;
        }
        input[type="range"] {
            width: 300px;
        }
    </style>
</head>
<body>
    <div id="control-panel">
        <div>
            <label for="slider-k">k</label>
            <input type="range" id="slider-k" min="0.6" max="0.95" step="0.01" value="0.9">
        </div>
        <div>
            <label for="slider-time">time</label>
            <input type="range" id="slider-time" min="5" max="25" step="0.5" value="15">
        </div>
        <div>
            <label for="slider-vx">initial velocity in x</label>
            <input type="range" id="slider-vx" min="1" max="8" step="0.1" value="2.5">
        </div>
        <div>
            <label for="slider-dt">Δt</label>
            <input type="range" id="slider-dt" min="0.05" max="0.4" step="0.01" value="0.15">
        </div>
    </div>
    <div id="canvas-container"></div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let sketch = function(p) {
            const g = 9.8;
            const y0 = 10;
            const vy0 = 0;
            const x0 = 0;
            const padding = 40;
            let scaleX, scaleY;

            p.setup = function() {
                p.createCanvas(700, 400);
                p.noLoop();
                attachSliderListeners();
            };

            p.draw = function() {
                const k = parseFloat(document.getElementById('slider-k').value);
                const totalTime = parseFloat(document.getElementById('slider-time').value);
                const vx = parseFloat(document.getElementById('slider-vx').value);
                const dt = parseFloat(document.getElementById('slider-dt').value);

                let {pathPoints, strobePoints} = simulateBounces(k, totalTime, vx, dt);

                // Calculate scaling factors
                const maxX = Math.max(...pathPoints.map(p => p.x));
                const maxY = y0;
                scaleX = (p.width - 2*padding) / maxX;
                scaleY = (p.height - 2*padding) / maxY;

                // Draw
                p.background(0);
                p.push();
                p.translate(padding, p.height - padding);
                p.scale(1, -1);

                // Draw axes
                p.stroke(100, 100, 150);
                p.strokeWeight(1);
                p.line(0, 0, p.width-padding, 0);  // x-axis
                p.line(0, 0, 0, p.height-2*padding);  // y-axis

                // Draw tick marks
                for(let i = 0; i <= maxX; i++) {
                    p.line(i*scaleX, -5, i*scaleX, 5);
                }
                for(let i = 0; i <= maxY; i++) {
                    p.line(-5, i*scaleY, 5, i*scaleY);
                }

                // Draw trajectory
                p.stroke(255, 0, 0);
                p.strokeWeight(2);
                p.noFill();
                p.beginShape();
                pathPoints.forEach(point => {
                    p.vertex(point.x * scaleX, point.y * scaleY);
                });
                p.endShape();

                // Draw strobe points
                p.fill(255, 255, 0);
                p.noStroke();
                strobePoints.forEach(point => {
                    p.ellipse(point.x * scaleX, point.y * scaleY, 8, 8);
                });

                p.pop();
            };

            function simulateBounces(k, totalTime, vx, dt) {
                let pathPoints = [];
                let strobePoints = [];
                let currentTime = 0;
                let currentX = x0;
                let currentY = y0;
                let currentVy = vy0;
                let lastStrobeTime = 0;

                while(currentTime < totalTime) {
                    // Calculate time of flight and impact velocity
                    let vy_impact = currentVy - g * Math.sqrt(2*currentY/g);
                    let t_flight = Math.abs((-currentVy - Math.sqrt(currentVy*currentVy + 2*g*currentY))/(-g));

                    // Generate trajectory points
                    for(let t = 0; t <= t_flight; t += 0.01) {
                        let x = currentX + vx * t;
                        let y = currentY + currentVy * t - 0.5 * g * t * t;
                        pathPoints.push({x, y});
                    }

                    // Generate strobe points
                    for(let t = lastStrobeTime; t <= currentTime + t_flight; t += dt) {
                        if(t >= currentTime) {
                            let deltaT = t - currentTime;
                            let x = currentX + vx * deltaT;
                            let y = currentY + currentVy * deltaT - 0.5 * g * deltaT * deltaT;
                            if(y >= 0) {
                                strobePoints.push({x, y});
                            }
                        }
                    }

                    // Update state for next bounce
                    currentTime += t_flight;
                    currentX += vx * t_flight;
                    currentVy = Math.sqrt(k) * Math.abs(vy_impact);
                    currentY = 0;
                    lastStrobeTime = currentTime;
                }

                return {pathPoints, strobePoints};
            }

            function attachSliderListeners() {
                ['slider-k', 'slider-time', 'slider-vx', 'slider-dt'].forEach(id => {
                    document.getElementById(id).addEventListener('input', () => p.redraw());
                });
            }
        };

        new p5(sketch, 'canvas-container');
    </script>
</body>
</html>
