<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Angle Bisectors on the Circumcircle</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <style>
        body {
            display: flex;
            flex-direction: column;
            align-items: center;
            background-color: #ffffff;
            font-family: Arial, sans-serif;
        }
        #canvas-container {
            margin: 20px;
        }
    </style>
</head>
<body>
    <h1>Angle Bisectors on the Circumcircle</h1>
    <p>Extend the angle bisectors of the triangle ABC to meet the circumcircle at A', B' and C'. Then AA' ⊥ B'C', BB' ⊥ A'C', and CC' ⊥ A'B'.</p>
    <div id="canvas-container"></div>

    <script>
        let points = {
            A: { angle: 7*Math.PI/6 },
            B: { angle: 11*Math.PI/6 },
            C: { angle: Math.PI/2 }
        };
        let derived = { A: {}, B: {}, C: {} };
        let radius = 200;
        let activePoint = null;
        let center;

        function setup() {
            const canvas = createCanvas(500, 500);
            canvas.parent('canvas-container');
            center = createVector(width/2, height/2);
            updatePoints();
        }

        function updatePoints() {
            // Update main points
            for (let p of ['A', 'B', 'C']) {
                points[p].x = center.x + radius * cos(points[p].angle);
                points[p].y = center.y + radius * sin(points[p].angle);
            }

            // Calculate derived points
            derived.A.angle = atan2(
                sin(points.B.angle) + sin(points.C.angle),
                cos(points.B.angle) + cos(points.C.angle)
            );
            derived.B.angle = atan2(
                sin(points.A.angle) + sin(points.C.angle),
                cos(points.A.angle) + cos(points.C.angle)
            );
            derived.C.angle = atan2(
                sin(points.A.angle) + sin(points.B.angle),
                cos(points.A.angle) + cos(points.B.angle)
            );

            for (let p of ['A', 'B', 'C']) {
                derived[p].x = center.x + radius * cos(derived[p].angle);
                derived[p].y = center.y + radius * sin(derived[p].angle);
            }
        }

        function draw() {
            background('#F0F0F0');

            // Draw circumcircle
            stroke('#6A5ACD');
            strokeWeight(4);
            noFill();
            circle(center.x, center.y, radius * 2);

            // Draw triangle ABC
            stroke('#6A5ACD');
            strokeWeight(4);
            line(points.A.x, points.A.y, points.B.x, points.B.y);
            line(points.B.x, points.B.y, points.C.x, points.C.y);
            line(points.C.x, points.C.y, points.A.x, points.A.y);

            // Draw extended bisectors and derived triangle
            stroke('#FFA500');
            strokeWeight(4);
            line(points.A.x, points.A.y, derived.A.x, derived.A.y);
            line(points.B.x, points.B.y, derived.B.x, derived.B.y);
            line(points.C.x, points.C.y, derived.C.x, derived.C.y);
            line(derived.A.x, derived.A.y, derived.B.x, derived.B.y);
            line(derived.B.x, derived.B.y, derived.C.x, derived.C.y);
            line(derived.C.x, derived.C.y, derived.A.x, derived.A.y);

            // Draw points and labels
            for (let p of ['A', 'B', 'C']) {
                // Main points
                fill('#FFA500');
                stroke(0);
                strokeWeight(1);
                circle(points[p].x, points[p].y, 12);

                // Crosshairs
                line(points[p].x-6, points[p].y, points[p].x+6, points[p].y);
                line(points[p].x, points[p].y-6, points[p].x, points[p].y+6);

                // Derived points
                circle(derived[p].x, derived[p].y, 12);

                // Labels
                noStroke();
                fill(0);
                textSize(16);
                textAlign(CENTER, CENTER);
                text(p, points[p].x + 15*cos(points[p].angle), 
                         points[p].y + 15*sin(points[p].angle));
                text(p + "'", derived[p].x + 15*cos(derived[p].angle), 
                             derived[p].y + 15*sin(derived[p].angle));
            }
        }

        function mousePressed() {
            for (let p of ['A', 'B', 'C']) {
                if (dist(mouseX, mouseY, points[p].x, points[p].y) < 15) {
                    activePoint = p;
                    break;
                }
            }
        }

        function mouseDragged() {
            if (activePoint) {
                let angle = atan2(mouseY - center.y, mouseX - center.x);
                points[activePoint].angle = angle;
                updatePoints();
            }
        }

        function mouseReleased() {
            activePoint = null;
        }
    </script>
</body>
</html>
