    <script>
        let slider;
        let tooltipText = '';
        let descriptions = {
            'Photosynthesis': 'Plants use sunlight, water, and carbon dioxide to create food, releasing oxygen.',
            'Cellular Respiration': 'Animals and plants break down sugars for energy, releasing carbon dioxide.',
            'Organisms\' Waste and Decay': 'Decomposers break down dead organic matter, returning carbon to the soil and atmosphere.',
            'Fossils and Fossil Fuels': 'Over millions of years, buried organic matter can turn into fossil fuels like coal and oil.',
            'Fossil Fuel Extraction': 'Humans extract fossil fuels from the ground for energy.',
            'Fossil Fuel Emissions': 'Burning fossil fuels releases large amounts of carbon dioxide into the atmosphere.',
            'CO2 Diffusion': 'Carbon dioxide dissolves into and is released from bodies of water like oceans.'
        };

        function setup() {
            let canvas = createCanvas(600, 500);
            canvas.parent('canvas-container');
            slider = document.getElementById('slider-steps');
        }

        function drawArrow(x1, y1, x2, y2) {
            line(x1, y1, x2, y2);
            push();
            translate(x2, y2);
            let angle = atan2(y2 - y1, x2 - x1);
            rotate(angle);
            triangle(-15, -5, -15, 5, 0, 0);
            pop();
        }

        function checkHover(x, y, w, h, label) {
            if (mouseX > x && mouseX < x + w && mouseY > y && mouseY < y + h) {
                tooltipText = descriptions[label];
            }
        }

        function drawStaticScene() {
            // Sky
            background('#4169E1');

            // Sun
            fill('#FFD700');
            noStroke();
            circle(500, 80, 60);

            // Ground
            fill('#228B22');
            rect(0, 250, width, 150);

            // Soil
            fill('#8B4513');
            rect(0, 400, width, 100);

            // Water
            fill('#0000CD');
            beginShape();
            vertex(500, 300);
            vertex(600, 300);
            vertex(600, 500);
            vertex(500, 500);
            endShape(CLOSE);

            // Trees
            fill('#8B4513');
            rect(280, 200, 20, 100);
            fill('#006400');
            ellipse(290, 180, 80, 80);

            // Sheep
            fill(255);
            ellipse(200, 280, 40, 30);
            ellipse(180, 280, 20, 20);
            fill(0);
            circle(175, 278, 4);

            // Factory
            fill('#696969');
            rect(450, 200, 60, 100);
            rect(470, 150, 20, 50);

            // Fossil deposits
            fill(0);
            circle(400, 450, 10);
            circle(450, 460, 10);
            circle(500, 455, 10);
        }

        function draw() {
            drawStaticScene();
            tooltipText = '';
            stroke(0);
            strokeWeight(2);

            let steps = parseInt(slider.value);

            if (steps >= 1) {
                // Cellular Respiration
                drawArrow(200, 240, 250, 180);
                fill(0);
                noStroke();
                text('Cellular\nRespiration', 150, 180);
                checkHover(150, 170, 100, 30, 'Cellular Respiration');
