            if (steps >= 3) {
                // Fossils
                drawArrow(220, 370, 220, 440);
                fill(0);
                noStroke();
                text('Fossils and\nFossil Fuels', 240, 440);
                checkHover(240, 430, 100, 30, 'Fossils and Fossil Fuels');
            }

            if (steps >= 4) {
                // Photosynthesis
                drawArrow(350, 80, 300, 180);
                fill(0);
                noStroke();
                text('Photosynthesis', 360, 120);
                checkHover(360, 110, 100, 20, 'Photosynthesis');
            }

            if (steps >= 5) {
                // Emissions
                drawArrow(480, 200, 430, 120);
                fill(0);
                noStroke();
                text('Fossil Fuel\nEmissions', 440, 150);
                checkHover(440, 140, 100, 30, 'Fossil Fuel Emissions');
            }

            if (steps >= 6) {
                // Extraction
                drawArrow(450, 420, 480, 280);
                fill(0);
                noStroke();
                text('Fossil Fuel\nExtraction', 400, 350);
                checkHover(400, 340, 100, 30, 'Fossil Fuel Extraction');
            }

            if (steps >= 7) {
                // CO2 Diffusion
                drawArrow(520, 250, 550, 400);
                drawArrow(570, 400, 540, 250);
                fill(0);
                noStroke();
                text('CO2 Diffusion', 480, 420);
                checkHover(480, 410, 100, 20, 'CO2 Diffusion');
            }

            // Draw tooltip if needed
            if (tooltipText) {
                fill(0, 0, 0, 150);
                noStroke();
                rect(mouseX + 10, mouseY + 10, 200, 60);
                fill(255);
                text(tooltipText, mouseX + 20, mouseY + 30, 180, 50);
            }
        }
    </script>
</body>
</html>
