            position: false,
            velocity: false,
            acceleration: true,
            coords: 'rectangular',
            circle: false
        };

        function setup() {
            const canvas = createCanvas(
                document.getElementById('canvas-container').offsetWidth,
                document.getElementById('canvas-container').offsetHeight
            );
            canvas.parent('canvas-container');

            // Setup event listeners
            document.getElementById('slider-time').addEventListener('input', (e) => {
                state.time = parseFloat(e.target.value);
                redraw();
            });

            document.getElementById('btn-reset-time').addEventListener('click', () => {
                state.time = 0.4;
                document.getElementById('slider-time').value = state.time;
                redraw();
            });

            document.querySelectorAll('input[type="radio"]').forEach(radio => {
                radio.addEventListener('change', (e) => {
                    const prop = e.target.name;
                    state[prop] = e.target.value === 'on';
                    redraw();
