            endShape();

            // Current point
            let x = cos(state.time);
            let y = sin(2*state.time);

            // Calculate vectors
            let vx = -sin(state.time);
            let vy = 2*cos(2*state.time);
            let ax = -cos(state.time);
            let ay = -4*sin(2*state.time);

            // Draw vectors if enabled
