                arrow(x, y, x + vx*0.2, y + vy*0.2);
            }

            if(state.acceleration) {
                stroke(200, 0, 200);
                arrow(x, y, x + ax*0.1, y + ay*0.1);
            }

            // Draw basis vectors based on coordinate system
            if(state.coords === 'rectangular') {
                drawRectangularBasis(x, y);
            } else if(state.coords === 'normal-tangential') {
                drawNormalTangentialBasis(x, y, vx, vy, ax, ay);
            } else if(state.coords === 'polar') {
                drawPolarBasis(x, y);
            }

            // Draw current point
            stroke(0);
            fill(0);
            circle(x, y, 0.05);

            // Draw instantaneous circle if enabled
            if(state.circle) {
                drawInstantaneousCircle(x, y, vx, vy, ax, ay);
            }
        }

