            position: absolute;
            top: 10px;
            right: 10px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="control-row">
                <label for="slider-amplitude">Amplitude:</label>
                <input type="range" id="slider-amplitude" min="0.1" max="1.0" step="0.001" value="0.3">
                <span id="amplitude-value">0.3</span>
            </div>
            <div class="control-row">
                <label for="slider-stiffness">Stiffness:</label>
                <input type="range" id="slider-stiffness" min="0.1" max="10.0" step="0.01" value="1.0">
                <span id="stiffness-value">1.0</span>
            </div>
            <div class="control-row">
                <label for="slider-mass">Mass:</label>
                <input type="range" id="slider-mass" min="0.1" max="10.0" step="0.01" value="1.0">
                <span id="mass-value">1.0</span>
            </div>
            <div class="control-row">
                <label>Time Controls:</label>
                <button id="btn-step-back">◀</button>
                <button id="btn-play-pause">▶</button>
                <button id="btn-step-forward">▶|</button>
            </div>
            <button id="btn-reset">×</button>
        </div>
        <div id="visualization-container">
            <div id="plot-canvas-container"></div>
            <div id="animation-canvas-container"></div>
        </div>
    </div>

    <script>
        let amplitude = 0.3;
        let stiffness = 1.0;
        let mass = 1.0;
        let time = 0;
        let isPlaying = false;
        let angularFrequency;
        let positionHistory = [];
        let plotSketch, animationSketch;

        // Update angular frequency
        function updateAngularFrequency() {
            angularFrequency = Math.sqrt(stiffness / mass);
        }

        // Initialize p5.js sketches
        new p5(function(p) {
            p.setup = function() {
                let canvas = p.createCanvas(800, 400);
                canvas.parent('plot-canvas-container');
                updateAngularFrequency();
            };

            p.draw = function() {
                p.background(255);

                // Draw axes and grid
                p.stroke(200);
                p.line(0, p.height/2, p.width, p.height/2);
                p.line(50, 0, 50, p.height);

                // Draw position history
                p.stroke('#6363B2');
                p.noFill();
                p.beginShape();
                for(let point of positionHistory) {
                    let x = p.map(point.t, 0, 25, 50, p.width-50);
                    let y = p.map(point.y, -1, 1, p.height-50, 50);
                    p.vertex(x, y);
                }
                p.endShape();
            };
        }, 'plot-canvas-container');

        new p5(function(p) {
            p.setup = function() {
                let canvas = p.createCanvas(200, 400);
                canvas.parent('animation-canvas-container');
            };

            p.draw = function() {
                p.background(255);

                // Calculate current position
                let y = amplitude * Math.sin(angularFrequency * time);

                // Draw spring and mass
                p.stroke(0);
                p.line(p.width/2-20, 50, p.width/2+20, 50);

                let massY = p.map(y, -1, 1, p.height-100, 100);

                // Draw spring
                p.beginShape();
                for(let i = 0; i < 10; i++) {
                    let py = p.map(i, 0, 10, 50, massY);
                    p.vertex(p.width/2 + (i%2)*20 - 10, py);
                }
                p.endShape();

                // Draw mass
                p.fill('#6363B2');
                p.rect(p.width/2-20, massY, 40, 40);
            };
        }, 'animation-canvas-container');

        // Event listeners
        document.getElementById('slider-amplitude').addEventListener('input', function(e) {
            amplitude = parseFloat(e.target.value);
            document.getElementById('amplitude-value').textContent = amplitude.toFixed(3);
        });

        document.getElementById('slider-stiffness').addEventListener('input', function(e) {
            stiffness = parseFloat(e.target.value);
            document.getElementById('stiffness-value').textContent = stiffness.toFixed(2);
            updateAngularFrequency();
        });

        document.getElementById('slider-mass').addEventListener('input', function(e) {
            mass = parseFloat(e.target.value);
            document.getElementById('mass-value').textContent = mass.toFixed(2);
            updateAngularFrequency();
        });

        document.getElementById('btn-play-pause').addEventListener('click', function() {
            isPlaying = !isPlaying;
            this.textContent = isPlaying ? '||' : '▶';
            if(isPlaying) {
                animate();
            }
        });

        function animate() {
            if(!isPlaying) return;

            time += 0.05;
            let y = amplitude * Math.sin(angularFrequency * time);
            positionHistory.push({t: time, y: y});

            requestAnimationFrame(animate);
        }

        document.getElementById('btn-reset').addEventListener('click', function() {
            time = 0;
            isPlaying = false;
            positionHistory = [];
            document.getElementById('btn-play-pause').textContent = '▶';

            // Reset sliders
            document.getElementById('slider-amplitude').value = 0.3;
            document.getElementById('slider-stiffness').value = 1.0;
            document.getElementById('slider-mass').value = 1.0;

            amplitude = 0.3;
            stiffness = 1.0;
            mass = 1.0;

            updateAngularFrequency();
        });

        // Initialize
        updateAngularFrequency();
    </script>
</body>
</html>
