<head>
    <title>Distance Transform Demo</title>
    <style>
        body {
            display: flex;
            justify-content: center;
            align-items: center;
            flex-direction: column;
            font-family: sans-serif;
            margin: 20px;
        }

        #control-panel {
            margin-bottom: 20px;
        }

        button {
            padding: 5px 10px;
            margin: 0 5px;
            border: 1px solid #666;
            background: #eee;
            cursor: pointer;
        }

        button.active {
            background: #A9A9A9;
        }

        #grid-container {
            display: grid;
            grid-template-columns: repeat(7, 1fr);
            grid-template-rows: repeat(9, 1fr);
            gap: 2px;
        }

        .cell {
            width: 40px;
            height: 40px;
            border: 1px solid #666;
            display: flex;
            justify-content: center;
            align-items: center;
            cursor: pointer;
            font-size: 14px;
        }

        .cell-on {
            background-color: #FFD700;
        }

        .cell-off {
            background-color: #D3D3D3;
        }
    </style>
</head>
<body>
    <div id="control-panel">
        <span>distance function:</span>
        <button id="btn-manhattan" class="active">Manhattan</button>
        <button id="btn-euclidean">squared Euclidean</button>
        <button id="btn-chebyshev">Chebyshev</button>
    </div>
    <div id="grid-container"></div>

    <script>
        let currentDistanceFunction = 'manhattan';
        let gridState = Array(9).fill().map(() => Array(7).fill(false));

        // Set initial "on" cells
        gridState[2][1] = true;
        gridState[6][4] = true;

        function createGrid() {
            const container = document.getElementById('grid-container');
            container.innerHTML = '';

            for (let r = 0; r < 9; r++) {
                for (let c = 0; c < 7; c++) {
                    const cell = document.createElement('div');
                    cell.className = `cell ${gridState[r][c] ? 'cell-on' : 'cell-off'}`;
                    cell.dataset.row = r;
                    cell.dataset.col = c;

                    const span = document.createElement('span');
                    cell.appendChild(span);
                    container.appendChild(cell);

                    cell.addEventListener('click', () => handleCellClick(r, c));
                }
            }
            updateDistances();
        }

        function manhattan(r1, c1, r2, c2) {
            return Math.abs(r1 - r2) + Math.abs(c1 - c2);
        }

        function euclidean(r1, c1, r2, c2) {
            return Math.pow(r1 - r2, 2) + Math.pow(c1 - c2, 2);
        }

        function chebyshev(r1, c1, r2, c2) {
            return Math.max(Math.abs(r1 - r2), Math.abs(c1 - c2));
        }

        function calculateDistance(r1, c1, r2, c2) {
            switch (currentDistanceFunction) {
                case 'manhattan': return manhattan(r1, c1, r2, c2);
                case 'euclidean': return euclidean(r1, c1, r2, c2);
                case 'chebyshev': return chebyshev(r1, c1, r2, c2);
            }
        }

        function updateDistances() {
            const cells = document.querySelectorAll('.cell');
            cells.forEach(cell => {
                const r = parseInt(cell.dataset.row);
                const c = parseInt(cell.dataset.col);

                if (gridState[r][c]) {
                    cell.querySelector('span').textContent = '0';
                    return;
                }

                let minDist = Infinity;
                for (let r2 = 0; r2 < 9; r2++) {
                    for (let c2 = 0; c2 < 7; c2++) {
                        if (gridState[r2][c2]) {
                            const dist = calculateDistance(r, c, r2, c2);
                            minDist = Math.min(minDist, dist);
                        }
                    }
                }
                cell.querySelector('span').textContent = minDist;
            });
        }

        function handleCellClick(row, col) {
            gridState[row][col] = !gridState[row][col];
            const cell = document.querySelector(`.cell[data-row="${row}"][data-col="${col}"]`);
            cell.className = `cell ${gridState[row][col] ? 'cell-on' : 'cell-off'}`;
            updateDistances();
        }

        function initializeButtons() {
            const buttons = document.querySelectorAll('button');
            buttons.forEach(btn => {
                btn.addEventListener('click', () => {
                    buttons.forEach(b => b.classList.remove('active'));
                    btn.classList.add('active');
                    currentDistanceFunction = btn.id.replace('btn-', '');
                    updateDistances();
                });
            });
        }

        // Initialize the demo
        createGrid();
        initializeButtons();
    </script>
</body>
</html>
