        function kelvinToCelsius(k) {
            return k - 273.15;
        }

        function celsiusToKelvin(c) {
            return c + 273.15;
        }

        function eyringPolanyi(t, tRef, aEp) {
            const tK = celsiusToKelvin(t);
            const tRefK = celsiusToKelvin(tRef);
            return (tK / tRefK) * Math.exp(aEp * (1/tRefK - 1/tK));
        }

        function arrhenius(t, tRef, aAr) {
            const tK = celsiusToKelvin(t);
            const tRefK = celsiusToKelvin(tRef);
            return Math.exp(aAr * (1/tRefK - 1/tK));
