            const temps = Array.from({length: n}, (_, i) => tMin + (tMax - tMin) * i / (n-1));
            const rates = temps.map(t => isEP ? 
                eyringPolanyi(t, tRef, param) : 
                arrhenius(t, tRef, param)
            );

            return {temps, rates};
