        <span id="n-display">6</span>
        <button id="btn-n-3">3</button>
        <button id="btn-n-4">4</button>
        <button id="btn-n-5">5</button>
        <button id="btn-n-6" class="active">6</button>
        <button id="btn-n-7">7</button>
        <button id="btn-n-8">8</button>
        <button id="btn-n-9">9</button>
        <button id="btn-n-10">10</button>
    </div>
    <div id="canvas-container"></div>

    <script>
        let n = 6;
        const colors = ['#fefd00', '#ee1f24', '#0048a1'];
        let canvas;

        function setup() {
            canvas = createCanvas(800, 600);
            canvas.parent('canvas-container');
            drawScene();
        }

        function drawScene() {
            background(255);

            // Left side: 2n-gon
            const R = 150;
            const cx = width / 4;
            const cy = height / 2;
            const angleStep = PI / n;

            // Draw 2n triangles
            for (let j = 1; j <= 2*n; j++) {
                const x1 = cx + R * cos((j-1) * angleStep - PI/2);
                const y1 = cy + R * sin((j-1) * angleStep - PI/2);
                const x2 = cx + R * cos(j * angleStep - PI/2);
                const y2 = cy + R * sin(j * angleStep - PI/2);

                fill(colors[(j-1) % 3]);
                stroke(0);
                triangle(cx, cy, x1, y1, x2, y2);
            }

            // Right side: rectangles
            const startX = width / 2;
            const startY = height / 2;
            const s = 2 * R * sin(angleStep / 2);
            const a = R * cos(angleStep / 2);

            // Draw full rectangles
            for (let k = 0; k < floor(n/2); k++) {
                const rectX = startX + k * (s + 20);
                const rectY = startY - a;

                // Draw rectangle parts
                stroke(0);

                // Top triangle
                fill(colors[0]);
                triangle(rectX, rectY, rectX + s, rectY, rectX + s/2, rectY + a);

                // Bottom triangle
                fill(colors[1]);
                triangle(rectX, rectY + 2*a, rectX + s, rectY + 2*a, rectX + s/2, rectY + a);

                // Left triangle
                fill(colors[2]);
                triangle(rectX, rectY, rectX, rectY + 2*a, rectX + s/2, rectY + a);

                // Right triangle
                fill(colors[0]);
                triangle(rectX + s, rectY, rectX + s, rectY + 2*a, rectX + s/2, rectY + a);

                // Rectangle outline
                noFill();
                rect(rectX, rectY, s, 2*a);
            }

            // Draw half-rectangle if n is odd
            if (n % 2 === 1) {
                const lastX = startX + floor(n/2) * (s + 20);
                const lastY = startY - a;

                // Top triangle
                fill(colors[2]);
                triangle(lastX, lastY, lastX + s, lastY, lastX + s/2, lastY + a);

                // Bottom triangle
                fill(colors[1]);
                triangle(lastX, lastY + a, lastX + s, lastY + a, lastX + s/2, lastY + 2*a);

                // Outline
                noFill();
                rect(lastX, lastY, s, 2*a);
            }
        }

        // Button click handlers
        document.querySelectorAll('button').forEach(button => {
            button.addEventListener('click', function() {
                const newN = parseInt(this.id.split('-')[2]);
                n = newN;
                document.getElementById('n-display').textContent = n;

                // Update button styles
                document.querySelectorAll('button').forEach(b => b.classList.remove('active'));
                this.classList.add('active');

                // Redraw scene
                drawScene();
            });
        });
    </script>
</body>
</html>
