<body>
    <div id="controls-container">
        <div class="slider-group">
            <label for="slider-q">Q:</label>
            <input type="range" id="slider-q" min="1" max="7" value="2" step="1">
            <span id="value-q">2</span>
        </div>
        <div class="slider-group">
            <label for="slider-p">P:</label>
            <input type="range" id="slider-p" min="0" max="1" value="0" step="0.01">
            <span id="value-p">0.00</span>
        </div>
        <button id="btn-reset">x</button>
    </div>
    <div id="main-container">
        <div id="workspace-panel" class="panel">
            <h3>robot workspace</h3>
            <div id="canvas-workspace"></div>
        </div>
        <div id="phase-space-panel" class="panel">
            <h3 id="phase-space-title">robot phase space, path length = 0.00 rads</h3>
            <div id="canvas-phase-space"></div>
        </div>
    </div>

    <script>
        // State variables
        let state = {
            q: 2,
            p: 0,
            startConfig: { q1: -1.5, q2: 0.5 },
            goalConfig: { q1: 1.5, q2: 1.0 },
            obstacles: [
                { x: 200, y: 100, radius: 15 },
                { x: 200, y: 300, radius: 15 }
            ],
            isDraggingObstacle1: false,
            isDraggingObstacle2: false,
            isDraggingStart: false,
            isDraggingGoal: false,
            robotParams: {
                baseX: 200,
                baseY: 200,
                link1Length: 75,
                link2Length: 50,
                linkWidth: 10
            },
            path: [],
            distanceField: [],
            cSpace: []
        };

        // Workspace sketch
        let workspaceSketch = new p5((p) => {
            p.setup = () => {
                p.createCanvas(400, 400);
            };

            p.draw = () => {
                p.background(240);
                drawRobot(p);
                drawObstacles(p);
            };

            function drawRobot(p) {
                const { baseX, baseY, link1Length, link2Length } = state.robotParams;
                const theta1 = lerp(state.startConfig.q1, state.goalConfig.q1, state.p);
                const theta2 = lerp(state.startConfig.q2, state.goalConfig.q2, state.p);

                p.push();
                p.translate(baseX, baseY);

                // Draw link 1
                p.push();
                p.rotate(theta1);
                p.fill(150);
                p.rect(0, -5, link1Length, 10);
                p.pop();

                // Draw link 2
                const x2 = link1Length * Math.cos(theta1);
                const y2 = link1Length * Math.sin(theta1);
                p.push();
                p.translate(x2, y2);
                p.rotate(theta1 + theta2);
                p.fill(150);
                p.rect(0, -5, link2Length, 10);
                p.pop();

                p.pop();
            }

            function drawObstacles(p) {
                state.obstacles.forEach(obs => {
                    p.fill(200, 100, 0);
                    p.ellipse(obs.x, obs.y, obs.radius * 2);
                });
            }
        }, 'canvas-workspace');

        // Phase space sketch
        let phaseSpaceSketch = new p5((p) => {
            p.setup = () => {
                p.createCanvas(400, 400);
                calculateCSpace();
            };

            p.draw = () => {
                p.background(240);
                drawGrid(p);
                drawPath(p);
                drawMarkers(p);
            };

            function drawGrid(p) {
                const n = 10 * Math.pow(2, state.q);
                const cellSize = p.width / n;

                for(let i = 0; i < n; i++) {
                    for(let j = 0; j < n; j++) {
                        if(state.cSpace[i] && state.cSpace[i][j]) {
                            p.fill(204, 0, 0);
                            p.rect(i * cellSize, j * cellSize, cellSize, cellSize);
                        }
                    }
                }
            }

            function drawPath(p) {
                if(state.path.length > 0) {
                    p.stroke(0, 255, 0);
                    p.strokeWeight(2);
                    for(let i = 0; i < state.path.length - 1; i++) {
                        const pt1 = state.path[i];
                        const pt2 = state.path[i + 1];
                        p.line(pt1.x, pt1.y, pt2.x, pt2.y);
                    }
                    p.strokeWeight(1);
                }
            }

            function drawMarkers(p) {
                // Draw start and goal markers
                const n = 10 * Math.pow(2, state.q);
                const scale = p.width / (2 * Math.PI);

                p.fill(0, 255, 0);
                p.ellipse(
                    (state.startConfig.q1 + Math.PI) * scale,
                    (state.startConfig.q2 + Math.PI) * scale,
                    10
                );
                p.ellipse(
                    (state.goalConfig.q1 + Math.PI) * scale,
                    (state.goalConfig.q2 + Math.PI) * scale,
                    10
                );
            }
        }, 'canvas-phase-space');

        // Event listeners
        document.getElementById('slider-q').addEventListener('input', (e) => {
            state.q = parseInt(e.target.value);
            document.getElementById('value-q').textContent = state.q;
            calculateCSpace();
        });

        document.getElementById('slider-p').addEventListener('input', (e) => {
            state.p = parseFloat(e.target.value);
            document.getElementById('value-p').textContent = state.p.toFixed(2);
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            resetSimulation();
        });

        // Utility functions
        function calculateCSpace() {
            // Simplified C-space calculation
            const n = 10 * Math.pow(2, state.q);
            state.cSpace = Array(n).fill().map(() => Array(n).fill(false));
            findPath();
        }

        function findPath() {
            // Simplified path finding
            state.path = [];
            updatePathLength();
        }

        function updatePathLength() {
            const length = state.path.length > 0 ? 
                state.path.reduce((acc, pt, i) => {
                    if (i === 0) return 0;
                    return acc + Math.hypot(pt.x - state.path[i-1].x, pt.y - state.path[i-1].y);
                }, 0) : 0;

            document.getElementById('phase-space-title').textContent = 
                `robot phase space, path length = ${length.toFixed(2)} rads`;
        }

        function resetSimulation() {
            state = {
                ...state,
                q: 2,
                p: 0,
                startConfig: { q1: -1.5, q2: 0.5 },
                goalConfig: { q1: 1.5, q2: 1.0 },
                obstacles: [
                    { x: 200, y: 100, radius: 15 },
                    { x: 200, y: 300, radius: 15 }
                ]
            };

            document.getElementById('slider-q').value = state.q;
            document.getElementById('value-q').textContent = state.q;
            document.getElementById('slider-p').value = state.p;
            document.getElementById('value-p').textContent = state.p.toFixed(2);

            calculateCSpace();
        }

        function lerp(start, end, t) {
            return start + (end - start) * t;
        }
    </script>
</body>
