                    <input type="range" id="slider-number" min="5" max="150" value="40" step="1">
                    <span id="number-value">40</span>
                </div>
            </div>

            <div class="control-group">
                <h4>method</h4>
                <div>
                    <button id="btn-prim" class="active">Prim</button>
                    <button id="btn-kruskal">Kruskal</button>
                </div>
            </div>

            <div class="control-group">
                <h4>process</h4>
                <div>
                    <button id="btn-play">►</button>
                    <button id="btn-pause" disabled>||</button>
                    <button id="btn-reset">|◄</button>
                </div>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let dimension = '2D';
        let algorithm = 'prim';
        let animationState = 'stopped';
        let points = [];
        let mstEdges = [];
        let animationStep = 0;
        let p5Canvas;

        function setup() {
            p5Canvas = createCanvas(
                document.getElementById('canvas-container').offsetWidth,
                document.getElementById('canvas-container').offsetHeight,
                dimension === '3D' ? WEBGL : P2D
            );
            p5Canvas.parent('canvas-container');
            generatePoints();
        }

        function draw() {
            background('#E0E0E0');

            if (dimension === '2D') {
                draw2D();
            } else {
                draw3D();
            }

            if (animationState === 'playing') {
                if (frameCount % 10 === 0) {
                    updateAnimation();
                }
            }
        }

        function generatePoints() {
            randomSeed(parseInt(document.getElementById('slider-seed').value));
            points = [];
            const n = parseInt(document.getElementById('slider-number').value);

            for (let i = 0; i < n; i++) {
                if (dimension === '2D') {
                    points.push(createVector(
                        random(width * 0.1, width * 0.9),
                        random(height * 0.1, height * 0.9)
                    ));
                } else {
                    points.push(createVector(
                        random(-100, 100),
                        random(-100, 100),
                        random(-100, 100)
                    ));
                }
            }
        }

        function draw2D() {
            // Draw edges
            stroke('red');
            strokeWeight(2);
            for (let edge of mstEdges) {
                line(
                    points[edge[0]].x, points[edge[0]].y,
                    points[edge[1]].x, points[edge[1]].y
                );
            }

            // Draw vertices
            noStroke();
            fill('blue');
            for (let p of points) {
                circle(p.x, p.y, 8);
            }
        }

        function draw3D() {
            orbitControl();
            // Draw reference cube
            stroke(200);
            noFill();
            box(200);

            // Draw edges
            stroke('red');
            strokeWeight(2);
            for (let edge of mstEdges) {
                line(
                    points[edge[0]].x, points[edge[0]].y, points[edge[0]].z,
                    points[edge[1]].x, points[edge[1]].y, points[edge[1]].z
                );
            }

            // Draw vertices
            noStroke();
            fill('blue');
            for (let p of points) {
                push();
                translate(p.x, p.y, p.z);
                sphere(3);
                pop();
            }
        }

        // Event Listeners
        document.getElementById('btn-2d').addEventListener('click', () => {
            dimension = '2D';
            document.getElementById('btn-2d').classList.add('active');
            document.getElementById('btn-3d').classList.remove('active');
            resetVisualization();
        });

        document.getElementById('btn-3d').addEventListener('click', () => {
            dimension = '3D';
            document.getElementById('btn-3d').classList.add('active');
            document.getElementById('btn-2d').classList.remove('active');
            resetVisualization();
        });

        document.getElementById('btn-prim').addEventListener('click', () => {
            algorithm = 'prim';
            document.getElementById('btn-prim').classList.add('active');
            document.getElementById('btn-kruskal').classList.remove('active');
            resetAlgorithm();
        });

        document.getElementById('btn-kruskal').addEventListener('click', () => {
            algorithm = 'kruskal';
            document.getElementById('btn-prim').classList.remove('active');
            document.getElementById('btn-kruskal').classList.add('active');
            resetAlgorithm();
        });

        document.getElementById('slider-seed').addEventListener('input', (e) => {
            document.getElementById('seed-value').textContent = e.target.value;
            resetVisualization();
        });

        document.getElementById('slider-number').addEventListener('input', (e) => {
            document.getElementById('number-value').textContent = e.target.value;
            resetVisualization();
        });

        document.getElementById('btn-play').addEventListener('click', () => {
            animationState = 'playing';
            document.getElementById('btn-play').disabled = true;
            document.getElementById('btn-pause').disabled = false;
        });

        document.getElementById('btn-pause').addEventListener('click', () => {
            animationState = 'paused';
            document.getElementById('btn-pause').disabled = true;
            document.getElementById('btn-play').disabled = false;
        });

        document.getElementById('btn-reset').addEventListener('click', resetAlgorithm);

        function resetVisualization() {
            animationState = 'stopped';
            mstEdges = [];
            animationStep = 0;
            setup();
            document.getElementById('btn-play').disabled = false;
            document.getElementById('btn-pause').disabled = true;
        }

        function resetAlgorithm() {
            animationState = 'stopped';
            mstEdges = [];
            animationStep = 0;
            document.getElementById('btn-play').disabled = false;
            document.getElementById('btn-pause').disabled = true;
        }

        function updateAnimation() {
            // Simplified animation step - just adds a random edge
            if (points.length > 1 && mstEdges.length < points.length - 1) {
                let a = floor(random(points.length));
                let b;
                do {
                    b = floor(random(points.length));
                } while (a === b);
                mstEdges.push([a, b]);
            } else {
                animationState = 'stopped';
                document.getElementById('btn-play').disabled = true;
                document.getElementById('btn-pause').disabled = true;
            }
        }
    </script>
</body>
</html>
