    <script>
        const selectMethod = document.getElementById('select-method');
        const sliderNum1 = document.getElementById('slider-num1');
        const sliderNum2 = document.getElementById('slider-num2');
        const btnIntYes = document.getElementById('btn-int-yes');
        const btnIntNo = document.getElementById('btn-int-no');
        const outputDisplay = document.getElementById('output-display');

        function getNumberParts(num) {
            const str = num.toString();
            const parts = str.split('.');
            const intPart = parts[0];
            const factor = parts.length > 1 ? Math.pow(10, parts[1].length) : 1;
            const asInt = parseInt(parts.join(''));
            return { intPart, factor, asInt };
        }

        function updateDisplay() {
            const num1 = parseFloat(sliderNum1.value);
            const num2 = parseFloat(sliderNum2.value);
            const method = selectMethod.value;

            let output = [];

            if (method === 'estimation') {
                output = [
                    `start with: ${num1} × ${num2}`,
                    `convert to: ${getNumberParts(num1).asInt}. × ${getNumberParts(num2).asInt}. = ${getNumberParts(num1).asInt * getNumberParts(num2).asInt}.`,
                    `estimate as: ${Math.round(num1)} × ${Math.round(num2)} = ${Math.round(num1) * Math.round(num2)}`,
                    `result is: ${(num1 * num2).toFixed(2)}`
                ];
            } else {
                const n1 = getNumberParts(num1);
                const n2 = getNumberParts(num2);
                output = [
                    `start with: ${num1} × ${num2}`,
                    `rewrite as: (${n1.asInt}. ÷ ${n1.factor}) × (${n2.asInt}. ÷ ${n2.factor})`,
                    `= (${n1.asInt}. × ${n2.asInt}.) ÷ (${n1.factor} × ${n2.factor})`,
                    `= ${n1.asInt * n2.asInt}. ÷ ${n1.factor * n2.factor}`,
                    `= ${(num1 * num2).toFixed(2)}`
                ];
            }

            outputDisplay.innerHTML = output.map(line => `<p>${line}</p>`).join('');
        }

        btnIntYes.addEventListener('click', () => {
            sliderNum2.step = "1";
            sliderNum2.value = Math.round(sliderNum2.value);
            btnIntYes.classList.add('active');
            btnIntNo.classList.remove('active');
            updateDisplay();
        });

        btnIntNo.addEventListener('click', () => {
            sliderNum2.step = "0.1";
            btnIntNo.classList.add('active');
            btnIntYes.classList.remove('active');
            updateDisplay();
        });

        selectMethod.addEventListener('change', updateDisplay);
        sliderNum1.addEventListener('input', updateDisplay);
        sliderNum2.addEventListener('input', updateDisplay);

        // Initial display
        updateDisplay();
    </script>
</body>
</html>
