<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-time">time (seconds):</label>
            <input type="range" id="slider-time" min="0" max="100" step="0.1" value="9.8">
            <span id="span-time-value">9.8</span>
            <button id="btn-reset">+</button>
        </div>
        <canvas id="canvas-material-bar" width="600" height="80"></canvas>
        <div id="div-plot"></div>
    </div>

    <script>
        // Constants
        const CANVAS_WIDTH = 600;
        const SYSTEM_LENGTH = 16; // mm
        const PIXELS_PER_MM = CANVAS_WIDTH / SYSTEM_LENGTH;
        const GLASS_WIDTH = 6; // mm

        // Calculate Stefan distance
        function stefanDistance(t) {
            return 0.155 * Math.sqrt(t);
        }

        // Draw material visualization
        function drawMaterialBar(time) {
            const canvas = document.getElementById('canvas-material-bar');
            const ctx = canvas.getContext('2d');
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            const meltDistance = stefanDistance(time);

            // Draw glass block with gradient
            const glassGrad = ctx.createLinearGradient(0, 0, GLASS_WIDTH * PIXELS_PER_MM, 0);
            glassGrad.addColorStop(0, '#FF0000');
            glassGrad.addColorStop(1, '#FFFFFF');
            ctx.fillStyle = glassGrad;
            ctx.fillRect(0, 10, GLASS_WIDTH * PIXELS_PER_MM, 40);

            // Draw melted water
            ctx.fillStyle = '#00BFFF';
            ctx.fillRect(
                GLASS_WIDTH * PIXELS_PER_MM, 
                10, 
                meltDistance * PIXELS_PER_MM, 
                40
            );

            // Draw ice block
            ctx.fillStyle = '#003366';
            ctx.fillRect(
                (GLASS_WIDTH + meltDistance) * PIXELS_PER_MM,
                10,
                canvas.width - (GLASS_WIDTH + meltDistance) * PIXELS_PER_MM,
                40
            );

            // Draw axis
            ctx.beginPath();
            ctx.moveTo(0, 60);
            ctx.lineTo(canvas.width, 60);
            ctx.stroke();

            // Draw axis labels
            ctx.fillStyle = '#000000';
            ctx.textAlign = 'center';
            [0, 5, 10, 15].forEach(x => {
                ctx.fillText(
                    x.toString(),
                    x * PIXELS_PER_MM,
                    75
                );
            });
        }

        // Initialize plot
        function initPlot() {
            const times = Array.from({length: 1001}, (_, i) => i * 0.1);
            const distances = times.map(stefanDistance);

            const data = [{
                x: times,
                y: distances,
                type: 'scatter',
                mode: 'lines',
                line: {color: '#FFA500'}
            }];

            const layout = {
                title: 'Stefan distance versus time',
                xaxis: {
                    title: 'time (s)',
                    range: [0, 100]
                },
                yaxis: {
                    title: 'distance (mm)',
                    range: [0, 1.6]
                },
                shapes: [{
                    type: 'line',
                    x0: 9.8,
                    y0: 0,
                    x1: 9.8,
                    y1: stefanDistance(9.8),
                    line: {
                        color: 'black',
                        width: 2
                    }
                }]
            };

            Plotly.newPlot('div-plot', data, layout);
        }

        // Update visualization
        function updateViz(time) {
            document.getElementById('span-time-value').textContent = time.toFixed(1);
            drawMaterialBar(time);

            Plotly.relayout('div-plot', {
                'shapes[0].x0': time,
                'shapes[0].x1': time,
                'shapes[0].y1': stefanDistance(time)
            });
        }

        // Event listeners
        document.getElementById('slider-time').addEventListener('input', (e) => {
            updateViz(parseFloat(e.target.value));
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            const slider = document.getElementById('slider-time');
            slider.value = 0;
            updateViz(0);
        });

        // Initialize
        window.onload = function() {
            initPlot();
            updateViz(9.8);
        };
    </script>
</body>
</html>
