            border: 1px solid black;
            padding: 2px 5px;
            font-family: monospace;
        }
        #offset-display {
            text-align: center;
            margin-top: 20px;
            padding: 10px;
            font-weight: bold;
        }
        #date-slider {
            width: 300px;
            margin: 0 10px;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <label for="date-slider">date:</label>
            <input type="range" id="date-slider" min="0" max="364" value="340" step="1">
            <span id="date-display">Friday, 07 December</span>
            <button id="today-button">today</button>
        </div>

        <div id="visualization-panel">
            <div id="clocks-container">
                <div id="local-clock-group" class="clock-group">
                    <h2>Local Time</h2>
                    <div id="local-clock-wrapper" class="clock-wrapper">
                        <canvas id="local-clock-canvas"></canvas>
                        <div id="local-time-digital" class="digital-display">00:00:00</div>
                    </div>
                </div>

                <div id="solar-clock-group" class="clock-group">
                    <h2>Solar Time</h2>
                    <div id="solar-clock-wrapper" class="clock-wrapper">
                        <canvas id="solar-clock-canvas"></canvas>
                        <div id="solar-time-digital" class="digital-display">00:00:00</div>
                    </div>
                </div>
            </div>
            <div id="offset-display">offset of solar time from local time: -46 minutes</div>
        </div>
    </div>

    <script>
        let userLongitude = 0;
        let userLatitude = 51.5;

        function formatTimeDigital(date) {
            return date.toTimeString().split(' ')[0];
        }

        function getDayOfYear(date) {
            const start = new Date(date.getFullYear(), 0, 0);
            const diff = date - start;
            return Math.floor(diff / (1000 * 60 * 60 * 24));
        }

        function calculateEoT(dayOfYear) {
            const B = (360 / 365.24) * (dayOfYear - 81);
            const Brad = B * Math.PI / 180;
            return 9.87 * Math.sin(2 * Brad) - 7.53 * Math.cos(Brad) - 1.5 * Math.sin(Brad);
        }

        function calculateSolarTime(localTime, dayOfYear) {
            const EoT = calculateEoT(dayOfYear);
            const UTCTime = localTime.getTime() + localTime.getTimezoneOffset() * 60000;
            const TimeOffsetMinutes = EoT + 4 * userLongitude;
            const SolarTimeMilliseconds = UTCTime + TimeOffsetMinutes * 60000;
            return new Date(SolarTimeMilliseconds);
        }

        new p5((p) => {
            p.setup = function() {
                const localCanvas = p.createCanvas(300, 300);
                localCanvas.parent('local-clock-wrapper');
                const solarCanvas = p.createCanvas(300, 300);
                solarCanvas.parent('solar-clock-wrapper');
                p.angleMode(p.RADIANS);
            };

            p.drawClock = function(timeObj, x, y, faceColor) {
                p.push();
                p.translate(x, y);

                // Draw clock face
                p.fill(faceColor);
                p.stroke(0);
                p.strokeWeight(2);
                p.circle(0, 0, 280);

                // Draw hour markers
                for(let i = 0; i < 12; i++) {
                    const angle = i * Math.PI/6;
                    p.strokeWeight(2);
                    p.line(120 * Math.cos(angle), 120 * Math.sin(angle),
                          140 * Math.cos(angle), 140 * Math.sin(angle));
                }

                // Draw hands
                const hours = timeObj.getHours() % 12 + timeObj.getMinutes()/60;
                const minutes = timeObj.getMinutes() + timeObj.getSeconds()/60;
                const seconds = timeObj.getSeconds();

                // Hour hand
                p.push();
                p.rotate(hours * (Math.PI/6));
                p.strokeWeight(4);
                p.line(0, 0, 0, -80);
                p.pop();

                // Minute hand
                p.push();
                p.rotate(minutes * (Math.PI/30));
                p.strokeWeight(2);
                p.line(0, 0, 0, -120);
                p.pop();

                // Second hand
                p.push();
                p.stroke(255, 0, 0);
                p.rotate(seconds * (Math.PI/30));
                p.strokeWeight(1);
                p.line(0, 0, 0, -130);
                p.pop();

                p.pop();
            };

            p.draw = function() {
                const localTime = new Date();
                const dayOfYear = parseInt(document.getElementById('date-slider').value);
                const solarTime = calculateSolarTime(localTime, dayOfYear);

                // Draw local clock
                p.clear();
                p.drawClock(localTime, 150, 150, '#E6E6FA');

                // Draw solar clock
                p.drawClock(solarTime, 150, 150, '#FAFAD2');

                // Update digital displays
                document.getElementById('local-time-digital').textContent = formatTimeDigital(localTime);
                document.getElementById('solar-time-digital').textContent = formatTimeDigital(solarTime);

                // Update offset display
                const offsetMinutes = Math.round((solarTime.getTime() - localTime.getTime()) / 60000);
                document.getElementById('offset-display').textContent = 
                    `offset of solar time from local time: ${offsetMinutes} minutes`;
            };
        });

        // Initialize geolocation
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                position => {
                    userLatitude = position.coords.latitude;
                    userLongitude = position.coords.longitude;
                },
                error => console.log("Geolocation error:", error)
            );
        }

        // Date slider and today button handlers
        document.getElementById('date-slider').addEventListener('input', function(e) {
            const day = parseInt(e.target.value);
            const date = new Date(new Date().getFullYear(), 0, day);
            document.getElementById('date-display').textContent = 
                date.toLocaleDateString('en-US', {weekday: 'long', day: '2-digit', month: 'long'});
        });

        document.getElementById('today-button').addEventListener('click', function() {
            const today = new Date();
            const dayOfYear = getDayOfYear(today);
            document.getElementById('date-slider').value = dayOfYear;
            document.getElementById('date-display').textContent = 
                today.toLocaleDateString('en-US', {weekday: 'long', day: '2-digit', month: 'long'});
        });
    </script>
</body>
</html>
